// src/components/M3UUploader.jsx
import React, { useState } from 'react';
import './M3UUploader.css';

const M3UUploader = ({ onLoad, onClose, isLoading, theme = 'dark' }) => {
  const [activeTab, setActiveTab] = useState('file'); // FILE √´sht√´ default!
  const [url, setUrl] = useState('');
  const [error, setError] = useState('');

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (e) => {
      const content = e.target.result;
      const channels = parseM3U(content);
      onLoad({ type: 'file', content, channels });
    };
    reader.readAsText(file);
  };

  const handleUrlSubmit = async (e) => {
    e.preventDefault();
    if (!url) {
      setError('Shkruani URL-n√´');
      return;
    }
    
    // Hap URL n√´ shfletues q√´ p√´rdoruesi ta shkarkoj√´
    window.open(url, '_blank');
    setError('‚úÖ Playlist po shkarkohet! Tani ngarko file-in nga opsioni "Nga File".');
  };

  // Parse M3U function
  const parseM3U = (content) => {
    if (!content || !content.includes('#EXTM3U')) return [];
    
    const lines = content.split('\n');
    const channels = [];
    let currentChannel = null;
    
    lines.forEach(line => {
      line = line.trim();
      if (!line) return;
      
      if (line.startsWith('#EXTINF:')) {
        const match = line.match(/#EXTINF:(-?\d+)(?:\s+(.*?))?,(.*)/);
        if (match) {
          const attributes = match[2] || '';
          const name = match[3] || 'Pa em√´r';
          
          currentChannel = {
            id: `m3u_${Date.now()}_${Math.random()}`,
            name,
            tvgLogo: '',
            groupTitle: '',
            url: '',
            stream_url: ''
          };
          
          const tvgLogoMatch = attributes.match(/tvg-logo="([^"]*)"/);
          if (tvgLogoMatch) currentChannel.tvgLogo = tvgLogoMatch[1];
          
          const groupMatch = attributes.match(/group-title="([^"]*)"/);
          if (groupMatch) currentChannel.groupTitle = groupMatch[1];
        }
      } else if (!line.startsWith('#') && currentChannel) {
        currentChannel.url = line;
        currentChannel.stream_url = line;
        channels.push({ ...currentChannel });
        currentChannel = null;
      }
    });
    
    return channels;
  };

  return (
    <div className={`xtream-modal-overlay theme-${theme}`}>
      <div className="xtream-modal">
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üìã</span>
            <h2>M3U Playlist</h2>
          </div>
          <button className="xtream-close-btn" onClick={onClose}>√ó</button>
        </div>
        
        <div className="xtream-modal-body">
          {/* Tabs - FILE √´sht√´ aktiv! */}
          <div className="m3u-tabs">
            <button 
              className={`m3u-tab ${activeTab === 'file' ? 'active' : ''}`}
              onClick={() => setActiveTab('file')}
            >
              üìÅ Nga File
            </button>
            <button 
              className={`m3u-tab ${activeTab === 'url' ? 'active' : ''}`}
              onClick={() => setActiveTab('url')}
            >
              üîó Nga URL
            </button>
          </div>

          {/* Tab Content */}
          <div className="m3u-tab-content">
            {activeTab === 'file' && (
              <div className="m3u-file-upload">
                <div className="file-upload-area">
                  <input
                    type="file"
                    accept=".m3u,.m3u8,text/plain"
                    onChange={handleFileUpload}
                    id="m3u-file-input"
                    disabled={isLoading}
                  />
                  <label htmlFor="m3u-file-input" className="file-upload-label">
                    <span className="upload-icon">üìÅ</span>
                    <span className="upload-text">Zgjidh skedar M3U</span>
                    <span className="upload-hint">.m3u ose .m3u8</span>
                  </label>
                </div>
                
                <div className="steps-box">
                  <h4>üìã 3 HAPA T√ã THJESHT√ã:</h4>
                  <ol>
                    <li><strong>Shkarko playlist-in</strong> - Hap URL-n√´ n√´ shfletues</li>
                    <li><strong>Ruaj file-in</strong> - N√´ kompjuterin tuaj</li>
                    <li><strong>Ngarko k√´tu</strong> - Zgjidh file-in e shkarkuar</li>
                  </ol>
                </div>

                <div className="quick-download">
                  <p style={{ marginBottom: '10px', fontWeight: 'bold' }}>üéØ Shkarko tani:</p>
                  <button
                    type="button"
                    className="download-btn"
                    onClick={() => window.open('http://bmedia.vip/get.php?username=Hadimovic2428&password=TGjJm4rv7H&type=m3u_plus&output=m3u8', '_blank')}
                  >
                    ‚¨áÔ∏è Shkarko Playlist M3U
                  </button>
                </div>
              </div>
            )}

            {activeTab === 'url' && (
              <form onSubmit={handleUrlSubmit} className="m3u-url-form">
                <div className="xtream-form-group">
                  <label>M3U URL</label>
                  <input
                    type="url"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    placeholder="https://example.com/playlist.m3u"
                    disabled={isLoading}
                  />
                  
                  <div className="info-box warning">
                    <strong>‚ö†Ô∏è URL NUK MUND T√ã NGARKOHET DIREKT!</strong>
                    <p>Serveri bllokon k√´rkesat nga aplikacioni.</p>
                  </div>
                  
                  <button
                    type="button"
                    className="open-url-btn"
                    onClick={() => {
                      if (url) {
                        window.open(url, '_blank');
                        setError('‚úÖ Playlist po shkarkohet! Tani shko te "Nga File" dhe ngarko file-in.');
                      }
                    }}
                  >
                    üîó Hap URL n√´ shfletues
                  </button>
                </div>
                
                <div className="xtream-form-actions">
                  <button type="button" onClick={onClose} disabled={isLoading}>
                    Anulo
                  </button>
                  <button type="submit" disabled={!url || isLoading}>
                    {isLoading ? 'Duke ngarkuar...' : 'Ngarko URL'}
                  </button>
                </div>
              </form>
            )}
          </div>

          {error && (
            <div className="error-message success">
              <span className="error-icon">‚úÖ</span>
              {error}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default M3UUploader;
