import React from 'react';
import { getItemName, getItemLogo, getItemCategory, getStreamType } from '../utils/streamHelpers';
import './ChannelList.css';

const ChannelList = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites = [], 
  selectedItem, 
  theme = 'dark',
  activeTab = 'live'
}) => {
  
  const isFavorite = (item) => {
    const id = item.stream_id || item.series_id || item.id;
    return favorites.includes(id);
  };

  const getTypeClass = (item) => {
    const type = getStreamType(item, activeTab);
    if (type === 'live') return 'live';
    if (type === 'movie') return 'movie';
    if (type === 'series') return 'series';
    return 'live';
  };

  const getTypeText = (item) => {
    const type = getStreamType(item, activeTab);
    if (type === 'live') return 'LIVE';
    if (type === 'movie') return 'FILM';
    if (type === 'series') return 'SERIAL';
    return 'LIVE';
  };

  if (!items || items.length === 0) {
    return (
      <div className="smart-channel-empty">
        <span className="empty-icon">üì∫</span>
        <h3>Nuk ka kanale</h3>
        <p>Nuk u gjet asnj√´ kanal n√´ k√´t√´ kategori</p>
      </div>
    );
  }

  return (
    <div className="smart-channel-grid">
      {items.map((item) => {
        const id = item.stream_id || item.series_id || item.id;
        const name = getItemName(item);
        const logo = getItemLogo(item);
        const category = getItemCategory(item, activeTab);
        const typeClass = getTypeClass(item);
        const typeText = getTypeText(item);
        const isFav = isFavorite(item);
        const isSelected = selectedItem && (selectedItem.stream_id || selectedItem.series_id) === id;

        return (
          <div
            key={id}
            className={`smart-channel-card ${isSelected ? 'playing' : ''}`}
            onClick={() => onPlay(item)}
          >
            <img
              src={logo || `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&size=56&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`}
              alt={name}
              className="smart-channel-card-logo"
              loading="lazy"
              onError={(e) => {
                e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&size=56&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`;
              }}
            />
            
            <div className="smart-channel-card-info">
              <div className="smart-channel-card-name">{name}</div>
              <div className="smart-channel-card-meta">
                <span className={`smart-channel-card-badge ${typeClass}`}>
                  {typeText}
                </span>
                {category && (
                  <span className="smart-channel-card-category">
                    {category}
                  </span>
                )}
              </div>
            </div>

            <button
              className={`smart-channel-card-fav ${isFav ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                onToggleFavorite(id);
              }}
            >
              {isFav ? '‚≠ê' : '‚òÜ'}
            </button>
          </div>
        );
      })}
    </div>
  );
};

export default ChannelList;
