 */
const buildUrl = (server, user, pass, action = '') => {
  let baseUrl = server.trim();
  
  // Pastro URL-n√´
  if (baseUrl.endsWith('/')) {
    baseUrl = baseUrl.slice(0, -1);
  }
  
  // Shto protocol n√´se mungon
  if (!baseUrl.startsWith('http://') && !baseUrl.startsWith('https://')) {
    baseUrl = 'http://' + baseUrl; // P√´rdor http si default
  }
  
  // Player API URL
  if (action) {
    return `${baseUrl}/player_api.php?username=${encodeURIComponent(user)}&password=${encodeURIComponent(pass)}&action=${action}`;
  } else {
    return `${baseUrl}/player_api.php?username=${encodeURIComponent(user)}&password=${encodeURIComponent(pass)}`;
  }
};

/**
 * Fetch direkt pa proxy p√´r testim
 */
const fetchDirect = async (url) => {
  console.log('üåê Fetching:', url);
  
  try {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 15000); // 15 sekonda timeout

    const response = await fetch(url, {
      signal: controller.signal,
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      },
      mode: 'cors',
      credentials: 'omit'
    });

    clearTimeout(timeoutId);

    if (!response.ok) {
      throw new Error(`HTTP ${response.status}`);
    }

    const text = await response.text();
    console.log('üì¶ Response:', text.substring(0, 200) + '...');
    
    // P√´rpiqu t√´ parse-oj JSON
    try {
      return JSON.parse(text);
    } catch (e) {
      console.error('‚ùå JSON Parse error:', e);
      return { error: 'Invalid JSON response', raw: text.substring(0, 500) };
    }
  } catch (error) {
    console.error('‚ùå Fetch error:', error.message);
    throw error;
  }
};

