export const xtreamLogin = async (server, user, pass) => {
  try {
    const url = buildUrl(server, user, pass, '');
    console.log('üîë Login URL:', url);
    
    const data = await fetchDirect(url);
    console.log('üîë Login response:', data);
    
    if (data && data.user_info) {
      if (data.user_info.auth === 1 || data.user_info.status === 'Active') {
        return {
          success: true,
          data: data.user_info,
          server: server
