import React, { useState, useRef } from 'react';
import { fetchM3UFromUrl, parseM3U } from '../services/xtream';

const M3UUploader = ({ onLoad, onClose, isLoading, theme }) => {
  const [sourceType, setSourceType] = useState('url');
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [error, setError] = useState('');
  const [focusedField, setFocusedField] = useState(null);
  const [isLoadingLocal, setIsLoadingLocal] = useState(false);
  const fileInputRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoadingLocal(true);

    try {
      if (sourceType === 'url') {
        if (!url.trim()) {
          setError('Ju lutem vendosni URL-n√´ e M3U playlist');
          setIsLoadingLocal(false);
          return;
        }

        // Validimi i URL-s√´
        let urlToFetch = url.trim();
        if (!urlToFetch.startsWith('http://') && !urlToFetch.startsWith('https://')) {
          urlToFetch = 'https://' + urlToFetch;
        }

        console.log('Fetching M3U from:', urlToFetch);
        
        // Fetch M3U content
        const m3uText = await fetchM3UFromUrl(urlToFetch);
        
        // Parse M3U
        const channels = parseM3U(m3uText);
        
        if (channels.length === 0) {
          throw new Error('Nuk u gjet asnj√´ kanal n√´ playlist');
        }

        console.log(`‚úÖ Found ${channels.length} channels`);
        await onLoad({ type: 'url', content: m3uText, channels });
        
      } else {
        // File upload
        if (!file) {
          setError('Ju lutem zgjidhni nj√´ skedar M3U');
          setIsLoadingLocal(false);
          return;
        }

        const content = await file.text();
        const channels = parseM3U(content);
        
        if (channels.length === 0) {
          throw new Error('Nuk u gjet asnj√´ kanal n√´ skedar');
        }

        console.log(`‚úÖ Found ${channels.length} channels from file`);
        await onLoad({ type: 'file', content, channels });
      }
    } catch (err) {
      console.error('M3U Error:', err);
      setError(err.message || 'Gabim gjat√´ ngarkimit t√´ M3U');
    } finally {
      setIsLoadingLocal(false);
    }
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (!selectedFile.name.toLowerCase().endsWith('.m3u') && 
          !selectedFile.name.toLowerCase().endsWith('.m3u8')) {
        setError('Ju lutem zgjidhni nj√´ skedar M3U ose M3U8');
        setFile(null);
        fileInputRef.current.value = '';
        return;
      }
      setFile(selectedFile);
      setError('');
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      if (!droppedFile.name.toLowerCase().endsWith('.m3u') && 
          !droppedFile.name.toLowerCase().endsWith('.m3u8')) {
        setError('Ju lutem zgjidhni nj√´ skedar M3U ose M3U8');
        return;
      }
      setFile(droppedFile);
      setError('');
      setSourceType('file');
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleUrlExample = () => {
    setUrl('http://51.89.146.138:25461/get.php?username=demo&password=demo&type=m3u_plus');
  };

  const isLoadingActive = isLoading || isLoadingLocal;

  return (
    <div className="xtream-modal-overlay" onClick={onClose}>
      <div className="xtream-modal" onClick={(e) => e.stopPropagation()}>
        {/* Header */}
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üìã</span>
            <h2>M3U Playlist</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={onClose}
            disabled={isLoadingActive}
          >
            √ó
          </button>
        </div>

        {/* Body */}
        <div className="xtream-modal-body">
          {/* Info Box */}
          <div className="xtream-info-box">
            <span className="info-icon">üí°</span>
            <div className="info-text">
              <strong>Ngarko M3U Playlist</strong>
              <p>P√´rdor URL ose ngarko skedar lokal</p>
              <button 
                className="info-example" 
                onClick={handleUrlExample}
                type="button"
                disabled={isLoadingActive}
              >
                P√´rdor URL Demo
              </button>
            </div>
          </div>

          {/* Source Type Tabs */}
          <div className="m3u-source-tabs">
            <button
              className={`m3u-source-tab ${sourceType === 'url' ? 'active' : ''}`}
              onClick={() => setSourceType('url')}
              type="button"
              disabled={isLoadingActive}
            >
              <span className="tab-icon">üîó</span>
              URL
            </button>
            <button
              className={`m3u-source-tab ${sourceType === 'file' ? 'active' : ''}`}
              onClick={() => setSourceType('file')}
              type="button"
              disabled={isLoadingActive}
            >
              <span className="tab-icon">üìÅ</span>
              Skedar Lokal
            </button>
          </div>

          {/* Form */}
          <form onSubmit={handleSubmit} className="xtream-form">
            {sourceType === 'url' ? (
              // URL Input
              <div className={`xtream-form-group ${focusedField === 'url' ? 'focused' : ''} ${error && !url ? 'error' : ''}`}>
                <label>
                  <span className="field-icon">üîó</span>
                  M3U URL
                </label>
                <div className="input-wrapper">
                  <input
                    type="url"
                    placeholder="https://example.com/playlist.m3u"
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    onFocus={() => setFocusedField('url')}
                    onBlur={() => setFocusedField(null)}
                    disabled={isLoadingActive}
                    autoComplete="off"
                  />
                  {url && !error && !isLoadingActive && <span className="input-valid">‚úì</span>}
                </div>
              </div>
            ) : (
              // File Upload
              <div 
                className={`m3u-dropzone ${file ? 'has-file' : ''} ${error ? 'error' : ''}`}
                onDrop={handleDrop}
                onDragOver={handleDragOver}
                onClick={() => !isLoadingActive && fileInputRef.current?.click()}
              >
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".m3u,.m3u8"
                  onChange={handleFileChange}
                  style={{ display: 'none' }}
                  disabled={isLoadingActive}
                />
                
                {file ? (
                  <>
                    <span className="dropzone-icon">‚úÖ</span>
                    <div className="dropzone-text">
                      <strong>{file.name}</strong>
                      <span>{(file.size / 1024).toFixed(1)} KB</span>
                    </div>
                    <button
                      type="button"
                      className="dropzone-remove"
                      onClick={(e) => {
                        e.stopPropagation();
                        setFile(null);
                        fileInputRef.current.value = '';
                      }}
                      disabled={isLoadingActive}
                    >
                      √ó
                    </button>
                  </>
                ) : (
                  <>
                    <span className="dropzone-icon">üìÇ</span>
                    <div className="dropzone-text">
                      <strong>Kliko ose zvarrit skedar</strong>
                      <span>M3U ose M3U8</span>
                    </div>
                  </>
                )}
              </div>
            )}

            {/* Error Message */}
            {error && (
              <div className="error-message">
                <span className="error-icon">‚ö†Ô∏è</span>
                {error}
              </div>
            )}

            {/* Actions */}
            <div className="xtream-form-actions">
              <button
                type="submit"
                className="xtream-btn xtream-btn-primary"
                disabled={isLoadingActive || (sourceType === 'file' && !file)}
              >
                {isLoadingActive ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke ngarkuar...
                  </>
                ) : (
                  <>
                    <span className="btn-icon">üì•</span>
                    Ngarko
                  </>
                )}
              </button>
              
              <button
                type="button"
                className="xtream-btn xtream-btn-secondary"
                onClick={onClose}
                disabled={isLoadingActive}
              >
                <span className="btn-icon">‚úï</span>
                Anulo
              </button>
            </div>
          </form>

          {/* Help Text */}
          <div className="xtream-help-text">
            <span className="help-icon">üí°</span>
            <span>Formate t√´ mb√´shtetura:</span>
            <span className="help-badge">M3U</span>
            <span className="help-badge">M3U8</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default M3UUploader;
