import React from 'react';

function LiveTVContent({ channels, playStream, favorites, toggleFavorite, theme, ImageComponent }) {
  if (!channels || channels.length === 0) {
    return null;
  }

  return (
    <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 xl:grid-cols-6 gap-4">
      {channels.map((channel) => (
        <div
          key={channel.stream_id}
          className={`rounded-xl overflow-hidden cursor-pointer transition-all duration-300 transform hover:scale-[1.02] hover:shadow-2xl group ${
            theme === 'dark' ? 'bg-gray-800' : 'bg-white shadow'
          }`}
          onClick={() => playStream(channel)}
        >
          <div className="relative aspect-video">
            <ImageComponent
              src={channel.logo}
              alt={channel.name}
              className="w-full h-full"
            />
            <div className="absolute inset-0 bg-gradient-to-t from-black via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity"></div>
            
            <button
              onClick={(e) => {
                e.stopPropagation();
                toggleFavorite(channel.stream_id);
              }}
              className="absolute top-2 right-2 bg-black bg-opacity-60 p-1.5 rounded-full z-10"
            >
              {favorites.includes(channel.stream_id) ? (
                <span className="text-yellow-400 text-lg">⭐</span>
              ) : (
                <span className="text-white text-lg">☆</span>
              )}
            </button>
            
            {channel.is_hd && (
              <span className="absolute top-2 left-2 px-2 py-0.5 bg-green-600 text-white text-xs font-bold rounded">
                HD
              </span>
            )}
            
            <div className="absolute bottom-2 left-2 right-2">
              <div className="text-xs px-2 py-1 bg-black bg-opacity-70 rounded truncate">
                {channel.category || 'Live TV'}
              </div>
            </div>
          </div>
          
          <div className="p-3">
            <h3 className="font-semibold truncate text-sm">{channel.name}</h3>
            <div className="flex justify-between items-center mt-2">
              <span className="text-xs text-gray-400">
                {channel.added_at ? new Date(channel.added_at).toLocaleDateString() : 'New'}
              </span>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  playStream(channel);
                }}
                className="text-xs px-2 py-1 bg-blue-600 hover:bg-blue-500 rounded"
              >
                Play
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default LiveTVContent;
