// components/SearchModal.jsx
import React, { useState, useEffect, useCallback, useRef } from 'react';

function SearchModal({ 
  isOpen, 
  query, 
  setQuery, 
  results = [], 
  onClose, 
  onSelectStream,
  onToggleFavorite,
  favorites = []
}) {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const inputRef = useRef(null);
  const listRef = useRef(null);

  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
      setSelectedIndex(0);
    }
  }, [isOpen]);

  const handleKeyDown = useCallback((e) => {
    if (e.key === 'ArrowDown') {
      e.preventDefault();
      setSelectedIndex(prev => Math.min(prev + 1, results.length - 1));
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      setSelectedIndex(prev => Math.max(prev - 1, 0));
    } else if (e.key === 'Enter' && results[selectedIndex]) {
      e.preventDefault();
      onSelectStream(results[selectedIndex]);
    } else if (e.key === 'Escape') {
      onClose();
    }
  }, [results, selectedIndex, onSelectStream, onClose]);

  useEffect(() => {
    if (isOpen) {
      window.addEventListener('keydown', handleKeyDown);
      return () => window.removeEventListener('keydown', handleKeyDown);
    }
  }, [isOpen, handleKeyDown]);

  useEffect(() => {
    // Scroll selected item into view
    if (listRef.current && selectedIndex >= 0) {
      const selectedElement = listRef.current.children[selectedIndex];
      if (selectedElement) {
        selectedElement.scrollIntoView({ block: 'nearest' });
      }
    }
  }, [selectedIndex]);

  if (!isOpen) return null;

  return (
    <div className="modal-overlay search-modal-overlay" onClick={onClose}>
      <div className="modal-content search-modal" onClick={(e) => e.stopPropagation()}>
        <div className="search-header">
          <span className="search-icon-large">üîç</span>
          <input
            ref={inputRef}
            type="text"
            className="search-input"
            placeholder="K√´rko kanale, filma, seriale..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
          <button className="search-close-btn" onClick={onClose}>
            √ó
          </button>
        </div>

        <div className="search-hint">
          <span>‚Üë‚Üì Navigo</span>
          <span>Enter Zgjedh</span>
          <span>Esc Mbyll</span>
        </div>

        <div className="search-results" ref={listRef}>
          {results.length === 0 ? (
            <div className="search-no-results">
              {query ? 'Nuk u gjet asnj√´ rezultat' : 'Filloni t√´ shkruani p√´r t√´ k√´rkuar...'}
            </div>
          ) : (
            results.map((item, index) => {
              const itemId = item.stream_id || item.series_id || item.id;
              const itemName = item.name || item.title;
              const itemLogo = item.logo || item.poster || item.cover;
              const itemType = item.type || 
                (item.stream_id ? 'live' : item.series_id ? 'series' : 'movie');
              
              return (
                <div
                  key={itemId || index}
                  className={`search-result-item ${selectedIndex === index ? 'selected' : ''}`}
                  onClick={() => onSelectStream(item)}
                  onMouseEnter={() => setSelectedIndex(index)}
                >
                  // components/SearchModal.jsx - Pjesa e logos e rregulluar

<img 
  src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || '?')}&size=40&background=f1f5f9&color=334155`}
  alt={itemName}
  className="search-result-logo"
  onError={(e) => {
    e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || '?')}&size=40&background=f1f5f9&color=334155`;
  }}
/>
                  <div className="search-result-info">
                    <div className="search-result-name">{itemName}</div>
                    <div className="search-result-meta">
                      <span className={`search-result-type ${itemType}`}>
                        {itemType === 'live' ? 'üì∫ Live' : 
                         itemType === 'movie' ? 'üé¨ Film' : 
                         'üì∫ Serial'}
                      </span>
                      {item.category && (
                        <span className="search-result-category">{item.category}</span>
                      )}
                      {item.year && (
                        <span className="search-result-year">{item.year}</span>
                      )}
                    </div>
                  </div>
                  <button 
                    className={`search-fav-btn ${favorites.includes(itemId) ? 'active' : ''}`}
                    onClick={(e) => {
                      e.stopPropagation();
                      onToggleFavorite(itemId);
                    }}
                  >
                    {favorites.includes(itemId) ? '‚òÖ' : '‚òÜ'}
                  </button>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

export default SearchModal;
