import React, { useState } from "react";
import {
  getLive,
  fetchM3U,
  buildStream
} from "./services/iptvService";

function App() {
  const [mode, setMode] = useState("m3u");

  // Xtream
  const [server, setServer] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  // M3U
  const [m3uUrl, setM3uUrl] = useState("");

  const [channels, setChannels] = useState([]);
  const [currentStream, setCurrentStream] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // =========================
  // LOAD DATA
  // =========================
  const loadData = async () => {
    setError("");
    setLoading(true);
    setChannels([]);
    setCurrentStream("");

    try {
      if (mode === "m3u") {
        if (!m3uUrl) throw new Error("M3U URL mungon");
        const text = await fetchM3U(m3uUrl);
        parseM3U(text);
      } else {
        if (!server || !username || !password) {
          throw new Error("Xtream credentials mungojnë");
        }
        const data = await getLive(server, username, password);
        setChannels(data);
      }
    } catch (err) {
      console.error(err);
      setError(err.message || "Gabim në ngarkim");
    }

    setLoading(false);
  };

  // =========================
  // PARSE M3U
  // =========================
  const parseM3U = (text) => {
    const lines = text.split("\n");
    const list = [];

    for (let i = 0; i < lines.length; i++) {
      if (lines[i].startsWith("#EXTINF")) {
        const name = lines[i].split(",")[1]?.trim();
        const url = lines[i + 1]?.trim();
        if (url) {
          list.push({
            name,
            stream_url: url
          });
        }
      }
    }

    setChannels(list);
  };

  // =========================
  // PLAY
  // =========================
  const playChannel = (ch) => {
    if (mode === "m3u") {
      setCurrentStream(ch.stream_url);
    } else {
      const url = buildStream(
        server,
        username,
        password,
        "live",
        ch.stream_id
      );
      setCurrentStream(url);
    }
  };

  // =========================
  // UI
  // =========================
  return (
    <div style={{ display: "flex", height: "100vh", fontFamily: "Arial" }}>
      {/* SIDEBAR */}
      <div style={{ width: 320, padding: 10, borderRight: "1px solid #ccc" }}>
        <h3>IPTV Player</h3>

        <select
          value={mode}
          onChange={(e) => setMode(e.target.value)}
          style={{ width: "100%" }}
        >
          <option value="m3u">M3U URL</option>
          <option value="xtream">Xtream Codes</option>
        </select>

        {mode === "m3u" && (
          <input
            style={{ width: "100%", marginTop: 6 }}
            placeholder="https://example.com/playlist.m3u"
            value={m3uUrl}
            onChange={(e) => setM3uUrl(e.target.value)}
          />
        )}

        {mode === "xtream" && (
          <>
            <input
              style={{ width: "100%", marginTop: 6 }}
              placeholder="http://server:port"
              value={server}
              onChange={(e) => setServer(e.target.value)}
            />
            <input
              style={{ width: "100%", marginTop: 6 }}
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <input
              style={{ width: "100%", marginTop: 6 }}
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </>
        )}

        <button
          style={{ marginTop: 10, width: "100%" }}
          onClick={loadData}
        >
          {loading ? "Loading..." : "Load"}
        </button>

        {error && <p style={{ color: "red" }}>{error}</p>}

        <hr />

        {/* CHANNEL LIST */}
        <div style={{ overflowY: "auto", maxHeight: "70vh" }}>
          {channels.map((ch, i) => (
            <div
              key={i}
              onClick={() => playChannel(ch)}
              style={{
                padding: 6,
                cursor: "pointer",
                borderBottom: "1px solid #eee"
              }}
            >
              {ch.name || ch.stream_name}
            </div>
          ))}
        </div>
      </div>

      {/* PLAYER */}
      <div style={{ flex: 1, background: "#000" }}>
        {currentStream ? (
          <video
            src={currentStream}
            controls
            autoPlay
            style={{ width: "100%", height: "100%" }}
          />
        ) : (
          <div style={{ color: "#fff", padding: 20 }}>
            Zgjidh një kanal
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
