import React, { useState, useEffect, useRef } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import HlsPlayer from "./components/HlsPlayer";
import SearchModal from "./components/SearchModal";
import SettingsModal from "./components/SettingsModal";
import ErrorBoundary from "./components/ErrorBoundary";
import { getLive, getMovies, getSeries } from "./services/iptvService";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  /* =======================
     STATE – LOGIN
  ======================= */
  const [server, setServer] = useState(localStorage.getItem("server") || "");
  const [user, setUser] = useState(localStorage.getItem("user") || "");
  const [pass, setPass] = useState(localStorage.getItem("pass") || "");
  const [m3uUrl, setM3uUrl] = useState(localStorage.getItem("m3uUrl") || "");

  /* =======================
     STATE – DATA
  ======================= */
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);

  /* =======================
     PLAYER
  ======================= */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  /* =======================
     UI
  ======================= */
  const [activeTab, setActiveTab] = useState("live");
  const [loading, setLoading] = useState(false);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showSettingsModal, setShowSettingsModal] = useState(false);

  /* =======================
     SEARCH (SAFE)
  ======================= */
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const abortRef = useRef(null);

  /* =======================
     SAVE TO LOCALSTORAGE
  ======================= */
  useEffect(() => {
    localStorage.setItem("server", server);
    localStorage.setItem("user", user);
    localStorage.setItem("pass", pass);
    localStorage.setItem("m3uUrl", m3uUrl);
  }, [server, user, pass, m3uUrl]);

  /* =======================
     SEARCH LOGIC (NO ERROR)
  ======================= */
  useEffect(() => {
    if (!searchQuery.trim()) {
      setSearchResults([]);
      return;
    }

    const q = searchQuery.toLowerCase();
    const results = [];

    channels.forEach(c => {
      if (c?.name?.toLowerCase().includes(q)) {
        results.push({ ...c, type: "live" });
      }
    });

    movies.forEach(m => {
      if (m?.name?.toLowerCase().includes(q)) {
        results.push({ ...m, type: "movie" });
      }
    });

    series.forEach(s => {
      if (s?.name?.toLowerCase().includes(q)) {
        results.push({ ...s, type: "series" });
      }
    });

    setSearchResults(results);
  }, [searchQuery, channels, movies, series]);

  /* =======================
     FETCH DATA (XTREAM / M3U)
  ======================= */
  const fetchData = async () => {
    try {
      setLoading(true);

      if (abortRef.current) abortRef.current.abort();
      abortRef.current = new AbortController();

      setChannels([]);
      setMovies([]);
      setSeries([]);
      setCurrentStream(null);

      // ===== XTREAM =====
      if (server && user && pass) {
        const [live, mov, ser] = await Promise.all([
          getLive(server, user, pass).catch(() => []),
          getMovies(server, user, pass).catch(() => []),
          getSeries(server, user, pass).catch(() => [])
        ]);

        setChannels(Array.isArray(live) ? live.slice(0, 500) : []);
        setMovies(Array.isArray(mov) ? mov.slice(0, 500) : []);
        setSeries(Array.isArray(ser) ? ser.slice(0, 500) : []);

        toast.success("Xtream u ngarkua me sukses");
        return;
      }

      // ===== M3U =====
      if (m3uUrl) {
        const res = await fetch(m3uUrl);
        const text = await res.text();
        const lines = text.split("\n");
        const parsed = [];

        for (let i = 0; i < lines.length; i++) {
          if (lines[i].startsWith("#EXTINF")) {
            parsed.push({
              stream_id: `m3u_${i}`,
              name: lines[i].split(",")[1] || "Channel",
              stream_url: lines[i + 1],
              logo: "https://ui-avatars.com/api/?name=TV&size=128"
            });
          }
        }

        setChannels(parsed.slice(0, 500));
        toast.success("M3U u ngarkua me sukses");
        return;
      }

      toast.warning("Vendos Xtream ose M3U URL");
    } catch (e) {
      console.error(e);
      toast.error("Gabim gjatë ngarkimit");
    } finally {
      setLoading(false);
    }
  };

  /* =======================
     RENDER
  ======================= */
  return (
    <ErrorBoundary>
      <ToastContainer />

      <SearchModal
        isOpen={showSearchModal}
        query={searchQuery}
        setQuery={setSearchQuery}
        results={searchResults || []}
        onClose={() => setShowSearchModal(false)}
        onSelectStream={(item) => {
          setCurrentStream(item.stream_url);
          setIsPlaying(true);
          setShowSearchModal(false);
        }}
      />

      <SettingsModal
        isOpen={showSettingsModal}
        onClose={() => setShowSettingsModal(false)}
      />

      <div className="app">
        <div className="top-bar">
          <button onClick={fetchData} disabled={loading}>
            {loading ? "Duke ngarkuar..." : "Ngarko"}
          </button>
          <button onClick={() => setShowSearchModal(true)}>Kërko</button>
        </div>

        {activeTab === "live" && (
          <LiveTVContent
            channels={channels}
            onPlay={(c) => {
              setCurrentStream(c.stream_url);
              setIsPlaying(true);
            }}
          />
        )}

        {activeTab === "movies" && (
          <MoviesContent movies={movies} />
        )}

        {activeTab === "series" && (
          <SeriesContent series={series} />
        )}

        {currentStream && (
          <HlsPlayer
            src={currentStream}
            isPlaying={isPlaying}
            onPlayPause={() => setIsPlaying(!isPlaying)}
          />
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
