// components/EPGInfo.jsx
import React from 'react';

function EPGInfo({ channel, currentProgram, nextProgram }) {
  if (!channel) return null;

  return (
    <div className="epg-info">
      <div className="epg-header">
        <span className="epg-icon">üì∫</span>
        <span className="epg-title">Electronic Program Guide</span>
      </div>
      
      <div className="epg-grid">
        <div className="epg-item">
          <span className="epg-label">Kanali</span>
          <span className="epg-value">{channel.name || 'Pa em√´r'}</span>
        </div>
        
        {channel.category && (
          <div className="epg-item">
            <span className="epg-label">Kategoria</span>
            <span className="epg-value">{channel.category}</span>
          </div>
        )}
      </div>

      {currentProgram ? (
        <div className="epg-program current">
          <div className="epg-program-title">üî¥ DUKE U TRANSMETUAR</div>
          <div className="epg-program-name">{currentProgram.title || 'Pa titull'}</div>
          <div className="epg-program-time">
            {currentProgram.start || '??:??'} - {currentProgram.end || '??:??'}
          </div>
          {currentProgram.description && (
            <div className="epg-program-desc">{currentProgram.description}</div>
          )}
        </div>
      ) : (
        <div className="epg-program current">
          <div className="epg-program-title">üî¥ DUKE U TRANSMETUAR</div>
          <div className="epg-program-name">Nuk ka informacion EPG</div>
          <div className="epg-program-time">--:-- - --:--</div>
        </div>
      )}

      {nextProgram ? (
        <div className="epg-program next">
          <div className="epg-program-title">‚è≠Ô∏è N√ã VAZHDIM</div>
          <div className="epg-program-name">{nextProgram.title || 'Pa titull'}</div>
          <div className="epg-program-time">
            {nextProgram.start || '??:??'} - {nextProgram.end || '??:??'}
          </div>
        </div>
      ) : (
        <div className="epg-program next">
          <div className="epg-program-title">‚è≠Ô∏è N√ã VAZHDIM</div>
          <div className="epg-program-name">Nuk ka informacion EPG</div>
          <div className="epg-program-time">--:-- - --:--</div>
        </div>
      )}
    </div>
  );
}

export default EPGInfo;
