import React, { useState, useEffect, useMemo, useCallback } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import SearchModal from "./components/SearchModal";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import "./App.css";

function App() {
  /* =====================
     DATA STATE
  ===================== */
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [isLoading, setIsLoading] = useState({
    channels: false,
    movies: false,
    series: false
  });
  const [error, setError] = useState(null);

  /* =====================
     PLAYER STATE
  ===================== */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playerError, setPlayerError] = useState(null);

  /* =====================
     UI STATE
  ===================== */
  const [activeTab, setActiveTab] = useState("live");
  const [showSearchModal, setShowSearchModal] = useState(false);

  /* =====================
     SEARCH STATE
  ===================== */
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  /* =====================
     MEMOIZED DATA
  ===================== */
  const safeChannels = useMemo(() => Array.isArray(channels) ? channels : [], [channels]);
  const safeMovies = useMemo(() => Array.isArray(movies) ? movies : [], [movies]);
  const safeSeries = useMemo(() => Array.isArray(series) ? series : [], [series]);

  /* =====================
     SEARCH LOGIC
  ===================== */
  useEffect(() => {
    if (!searchQuery || searchQuery.trim() === "") {
      setSearchResults([]);
      return;
    }

    const q = searchQuery.toLowerCase();
    const results = [];

    safeChannels.forEach((c) => {
      if (c?.name?.toLowerCase().includes(q)) {
        results.push({ ...c, type: "Live" });
      }
    });

    safeMovies.forEach((m) => {
      if (m?.name?.toLowerCase().includes(q)) {
        results.push({ ...m, type: "Movie" });
      }
    });

    safeSeries.forEach((s) => {
      if (s?.name?.toLowerCase().includes(q)) {
        results.push({ ...s, type: "Series" });
      }
    });

    setSearchResults(results);
  }, [searchQuery, safeChannels, safeMovies, safeSeries]);

  /* =====================
     DUMMY DATA (TEST)
     Z√´vend√´so me Xtream/M3U
  ===================== */
  useEffect(() => {
    const loadDummyData = async () => {
      setIsLoading(prev => ({ ...prev, channels: true }));
      try {
        // Simulim loading
        await new Promise(resolve => setTimeout(resolve, 500));
        
        setChannels([
          {
            stream_id: 1,
            name: "Test Channel 1",
            stream_url: "https://test-streams.mux.dev/x36xhzz/x36xhzz.m3u8",
            logo: "https://via.placeholder.com/50",
            category: "Sports"
          },
          {
            stream_id: 2,
            name: "Test Channel 2",
            stream_url: "https://bitdash-a.akamaihd.net/content/sintel/hls/video/1500kbit.m3u8",
            logo: "https://via.placeholder.com/50",
            category: "Movies"
          },
          {
            stream_id: 3,
            name: "Test Channel 3",
            stream_url: "https://cph-p2p-msl.akamaized.net/hls/live/2000341/test/master.m3u8",
            logo: "https://via.placeholder.com/50",
            category: "News"
          }
        ]);

        setMovies([
          {
            stream_id: 101,
            name: "Test Movie 1",
            stream_url: "https://bitdash-a.akamaihd.net/content/sintel/hls/video/1500kbit.m3u8",
            poster: "https://via.placeholder.com/300x450",
            year: "2023",
            rating: "8.5"
          }
        ]);

        setSeries([
          {
            series_id: 201,
            name: "Test Series 1",
            cover: "https://via.placeholder.com/300x450",
            seasons: 3,
            rating: "9.0"
          }
        ]);

        setError(null);
      } catch (err) {
        setError("Gabim gjat√´ ngarkimit t√´ t√´ dh√´nave testuese");
        console.error(err);
      } finally {
        setIsLoading(prev => ({ ...prev, channels: false }));
      }
    };

    loadDummyData();
  }, []);

  /* =====================
     PLAYER HANDLERS
  ===================== */
  const handlePlay = useCallback((stream) => {
    try {
      if (!stream?.stream_url) {
        setPlayerError("URL e stream-it nuk √´sht√´ e vlefshme");
        return;
      }

      setCurrentStream(stream.stream_url);
      setIsPlaying(true);
      setPlayerError(null);
    } catch (err) {
      setPlayerError("Gabim gjat√´ fillimit t√´ playback-ut");
      console.error(err);
    }
  }, []);

  const handleClosePlayer = useCallback(() => {
    setCurrentStream(null);
    setIsPlaying(false);
    setPlayerError(null);
  }, []);

  const handlePlayPause = useCallback(() => {
    setIsPlaying(prev => !prev);
  }, []);

  /* =====================
     SEARCH HANDLERS
  ===================== */
  const handleOpenSearch = useCallback(() => {
    setShowSearchModal(true);
  }, []);

  const handleCloseSearch = useCallback(() => {
    setShowSearchModal(false);
    setSearchQuery("");
    setSearchResults([]);
  }, []);

  const handleSelectStream = useCallback((item) => {
    if (item?.stream_url) {
      handlePlay(item);
      handleCloseSearch();
    } else {
      setPlayerError("Ky item nuk ka stream URL");
    }
  }, [handlePlay, handleCloseSearch]);

  /* =====================
     KEYBOARD SHORTCUTS
  ===================== */
  useEffect(() => {
    const handleKeyDown = (e) => {
      // Ctrl+K p√´r k√´rkim
      if (e.ctrlKey && e.key === 'k') {
        e.preventDefault();
        setShowSearchModal(true);
      }
      
      // Escape p√´r t√´ mbyllur player-in
      if (e.key === 'Escape' && currentStream) {
        handleClosePlayer();
      }

      // Alt+1,2,3 p√´r tabs
      if (e.altKey && e.key === '1') {
        e.preventDefault();
        setActiveTab("live");
      }
      if (e.altKey && e.key === '2') {
        e.preventDefault();
        setActiveTab("movies");
      }
      if (e.altKey && e.key === '3') {
        e.preventDefault();
        setActiveTab("series");
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [currentStream, handleClosePlayer]);

  /* =====================
     CLEANUP
  ===================== */
  useEffect(() => {
    return () => {
      handleClosePlayer();
    };
  }, [handleClosePlayer]);

  /* =====================
     RENDER
  ===================== */
  return (
    <ErrorBoundary>
      <div className="app">
        {/* Error Messages */}
        {error && (
          <div className="error-banner">
            <span className="error-icon">‚ö†Ô∏è</span>
            <span className="error-message">{error}</span>
            <button 
              className="error-close" 
              onClick={() => setError(null)}
              aria-label="Mbyll"
            >
              √ó
            </button>
          </div>
        )}

        {playerError && (
          <div className="error-banner player-error">
            <span className="error-icon">üé¨</span>
            <span className="error-message">{playerError}</span>
            <button 
              className="error-close" 
              onClick={() => setPlayerError(null)}
              aria-label="Mbyll"
            >
              √ó
            </button>
          </div>
        )}

        {/* Search Modal */}
        <SearchModal
          isOpen={showSearchModal}
          query={searchQuery}
          setQuery={setSearchQuery}
          results={searchResults}
          onClose={handleCloseSearch}
          onSelectStream={handleSelectStream}
        />

        {/* Header */}
        <header className="app-header">
          <div className="header-left">
            <h1 className="app-title">IPTV Player</h1>
            <button 
              className="search-btn"
              onClick={handleOpenSearch}
              aria-label="K√´rko (Ctrl+K)"
            >
              <span className="search-icon">üîç</span>
              <span className="search-text">K√´rko</span>
              <span className="search-shortcut">Ctrl+K</span>
            </button>
          </div>

          <nav className="main-nav">
            <button 
              className={`nav-btn ${activeTab === "live" ? "active" : ""}`}
              onClick={() => setActiveTab("live")}
              aria-label="Live TV (Alt+1)"
            >
              üì∫ Live
              <span className="nav-shortcut">Alt+1</span>
            </button>
            <button 
              className={`nav-btn ${activeTab === "movies" ? "active" : ""}`}
              onClick={() => setActiveTab("movies")}
              aria-label="Movies (Alt+2)"
            >
              üé¨ Filma
              <span className="nav-shortcut">Alt+2</span>
            </button>
            <button 
              className={`nav-btn ${activeTab === "series" ? "active" : ""}`}
              onClick={() => setActiveTab("series")}
              aria-label="Series (Alt+3)"
            >
              üì∫ Seriale
              <span className="nav-shortcut">Alt+3</span>
            </button>
          </nav>
        </header>

        {/* Main Content */}
        <main className="main-content">
          {activeTab === "live" && (
            <LiveTVContent
              channels={safeChannels}
              onPlay={handlePlay}
              isLoading={isLoading.channels}
            />
          )}

          {activeTab === "movies" && (
            <MoviesContent 
              movies={safeMovies} 
              isLoading={isLoading.movies}
              onPlay={handlePlay}
            />
          )}

          {activeTab === "series" && (
            <SeriesContent 
              series={safeSeries} 
              isLoading={isLoading.series}
              onPlay={handlePlay}
            />
          )}
        </main>

        {/* HLS Player */}
        {currentStream && (
          <div className="player-container">
            <HlsPlayer
              src={currentStream}
              isPlaying={isPlaying}
              onPlayPause={handlePlayPause}
              onClose={handleClosePlayer}
              onError={(err) => setPlayerError(err)}
            />
          </div>
        )}

        {/* Loading Overlay */}
        {Object.values(isLoading).some(v => v) && (
          <div className="loading-overlay">
            <div className="spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
