import React, { memo } from 'react';

const ChannelItem = memo(({ 
  item, 
  isFavorite, 
  isPlaying, 
  onPlay, 
  onToggleFavorite, 
  theme,
  activeTab 
}) => {
  const itemId = item.stream_id || item.series_id || item.id;
  const itemName = item.name || item.title;
  const itemLogo = item.logo || item.poster || item.cover;
  const itemCategory = item.category || item.category_name || item.genre || 
    (activeTab === 'live' ? 'Live' : 
     activeTab === 'movies' ? 'Film' : 'Serial');

  return (
    <div 
      className={`smart-channel-item ${isPlaying ? 'playing' : ''}`}
      onClick={() => onPlay(item)}
    >
      <img 
        src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || 'TV')}&size=36&background=${theme === 'dark' ? 'f1f5f9' : 'e2e8f0'}&color=${theme === 'dark' ? '334155' : '0f172a'}`}
        alt={itemName}
        className="smart-channel-logo"
        loading="lazy"
        onError={(e) => {
          e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || 'TV')}&size=36&background=${theme === 'dark' ? 'f1f5f9' : 'e2e8f0'}&color=${theme === 'dark' ? '334155' : '0f172a'}`;
        }}
      />
      
      <div className="smart-channel-info">
        <div className="smart-channel-name">{itemName}</div>
        <div className="smart-channel-meta">
          <span className="smart-channel-category">{itemCategory}</span>
          {item.year && <span className="smart-channel-year">{item.year}</span>}
          {item.rating && <span className="smart-channel-rating">⭐ {item.rating}</span>}
          {item.season_count > 0 && (
            <span className="smart-channel-seasons">{item.season_count} sezone</span>
          )}
          {isPlaying && (
            <span className="smart-now-indicator">
              <span className="dot"></span>
              Duke u luajtur
            </span>
          )}
        </div>
      </div>

      <button 
        className={`smart-fav-btn ${isFavorite ? 'active' : ''}`}
        onClick={(e) => {
          e.stopPropagation();
          onToggleFavorite(itemId);
        }}
      >
        {isFavorite ? '★' : '☆'}
      </button>
    </div>
  );
});

const ChannelList = memo(({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem,
  theme,
  activeTab 
}) => {
  if (!items || items.length === 0) {
    return (
      <div className="smart-no-channels">
        {activeTab === 'live' ? 'Nuk ka kanale' :
         activeTab === 'movies' ? 'Nuk ka filma' : 'Nuk ka seriale'} 
         në këtë kategori
      </div>
    );
  }

  return (
    <div className="smart-channels-list">
      {items.map((item) => {
        const itemId = item.stream_id || item.series_id || item.id;
        const isFavorite = favorites.includes(itemId);
        const isPlaying = selectedItem?.stream_id === itemId || 
                         selectedItem?.series_id === itemId;
        
        return (
          <ChannelItem
            key={itemId}
            item={item}
            isFavorite={isFavorite}
            isPlaying={isPlaying}
            onPlay={onPlay}
            onToggleFavorite={onToggleFavorite}
            theme={theme}
            activeTab={activeTab}
          />
        );
      })}
    </div>
  );
});

export default ChannelList;
