import React, { useState } from "react";
import { Check } from "lucide-react";

export default function Xtream({ loadXtreamChannels }) {
  const [host, setHost] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLoad = () => {
    if (host && username && password) loadXtreamChannels(host, username, password);
  };

  return (
    <div className="p-6 rounded-2xl bg-gray-900 border border-gray-700 max-w-lg mx-auto space-y-4">
      <h3 className="text-xl font-semibold">Ngarko Kanalet nga Xtream</h3>
      <input type="text" placeholder="Host" value={host} onChange={e => setHost(e.target.value)}
        className="w-full p-3 bg-gray-800 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"/>
      <input type="text" placeholder="Username" value={username} onChange={e => setUsername(e.target.value)}
        className="w-full p-3 bg-gray-800 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"/>
      <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)}
        className="w-full p-3 bg-gray-800 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"/>
      <button onClick={handleLoad} className="w-full py-3 bg-blue-600 hover:bg-blue-700 rounded-xl font-medium flex items-center justify-center gap-2">
        <Check size={20}/> Ngarko Kanalet
      </button>
    </div>
  );
}
