import React from "react";

export default function SearchModal(props = {}) {
  const isOpen = props.isOpen === true;
  const query = typeof props.query === "string" ? props.query : "";
  const setQuery = typeof props.setQuery === "function" ? props.setQuery : () => {};
  const onClose = typeof props.onClose === "function" ? props.onClose : () => {};
  const onSelectStream =
    typeof props.onSelectStream === "function"
      ? props.onSelectStream
      : () => {};

  // üîí MBROJTJA FINALE
  const results = Array.isArray(props.results) ? props.results : [];

  if (!isOpen) return null;

  return (
    <div
      style={{
        position: "fixed",
        inset: 0,
        background: "rgba(0,0,0,0.7)",
        zIndex: 9999,
        display: "flex",
        alignItems: "center",
        justifyContent: "center"
      }}
    >
      <div
        style={{
          background: "#111",
          width: "90%",
          maxWidth: 700,
          borderRadius: 8,
          padding: 16
        }}
      >
        <div style={{ display: "flex", justifyContent: "space-between" }}>
          <h3 style={{ color: "#fff" }}>K√´rko</h3>
          <button onClick={onClose}>‚úï</button>
        </div>

        <input
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="K√´rko..."
          style={{
            width: "100%",
            padding: 10,
            margin: "10px 0"
          }}
        />

        {/* ‚ùó ASNJ√ã .length PA ARRAY */}
        {results.length === 0 ? (
          <p style={{ color: "#aaa", textAlign: "center" }}>
            Nuk ka rezultate
          </p>
        ) : (
          <div style={{ maxHeight: 400, overflowY: "auto" }}>
            {results.map((item, i) => (
              <div
                key={item?.stream_id ?? i}
                onClick={() => onSelectStream(item)}
                style={{
                  padding: 10,
                  cursor: "pointer",
                  borderBottom: "1px solid #333",
                  color: "#fff"
                }}
              >
                {item?.name ?? "Pa em√´r"}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
