// components/XtreamLogin.jsx
import React, { useState } from 'react';

function XtreamLogin({ onLogin, onClose, isLoading }) {
  const [credentials, setCredentials] = useState({
    server: '',
    username: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!credentials.server.trim()) {
      newErrors.server = 'Server URL √´sht√´ i detyruesh√´m';
    } else if (!credentials.server.match(/^https?:\/\//)) {
      newErrors.server = 'URL duhet t√´ filloj√´ me http:// ose https://';
    }
    
    if (!credentials.username.trim()) {
      newErrors.username = 'Username √´sht√´ i detyruesh√´m';
    }
    
    if (!credentials.password.trim()) {
      newErrors.password = 'Password √´sht√´ i detyruesh√´m';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // N√´ handleXtreamLogin - z√´vend√´so me k√´t√´:

const handleXtreamLogin = useCallback(async (credentials) => {
  setIsLoading(prev => ({ ...prev, general: true }));
  setError(null);
  
  try {
    console.log('üöÄ Xtream Login:', credentials);
    
    // 1. Login
    const login = await xtreamLogin(credentials.server, credentials.username, credentials.password);
    
    if (!login.success) {
      throw new Error(login.error || 'Kredencialet e gabuara');
    }

    console.log('‚úÖ Login success:', login);

    // 2. Merr kategorit√´
    console.log('üìÅ Fetching categories...');
    const allCategories = await getAllCategories(credentials.server, credentials.username, credentials.password);
    console.log('‚úÖ Categories loaded:', allCategories);

    // 3. Merr content-in
    console.log('üì¶ Fetching content...');
    const content = await getAllContent(credentials.server, credentials.username, credentials.password);
    console.log('‚úÖ Content loaded:', {
      live: content.live.length,
      movies: content.movies.length,
      series: content.series.length
    });

    // 4. Nd√´rto stream URLs
    const liveWithUrls = content.live.map(ch => ({
      ...ch,
      stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, ch.stream_id, 'live'),
      type: 'live'
    }));

    const moviesWithUrls = content.movies.map(m => ({
      ...m,
      stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, m.stream_id, 'movie', m.container_extension),
      type: 'movie'
    }));

    // 5. Update state
    setChannels(liveWithUrls);
    setMovies(moviesWithUrls);
    setSeries(content.series);
    setCategories(allCategories);
    
    setXtreamCredentials({ 
      ...credentials, 
      isLoggedIn: true,
      auth: login.data
    });
    
    setSourceType("xtream");
    setShowXtreamLogin(false);
    setShowM3UModal(false);
    setActiveTab("live");
    setSelectedCategory("T√´ gjitha");
    
    setSuccess(`‚úÖ U lidh√´t! 
      üì∫ ${liveWithUrls.length} kanale, 
      üé¨ ${moviesWithUrls.length} filma, 
      üì∫ ${content.series.length} seriale`);
    
  } catch (err) {
    console.error('‚ùå Xtream login error:', err);
    setError(`Xtream gabim: ${err.message}`);
  } finally {
    setIsLoading(prev => ({ ...prev, general: false }));
  }
}, []);

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Xtream Codes Login</h2>
          <button className="modal-close-btn" onClick={onClose}>√ó</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="server">Server URL</label>
            <input
              type="url"
              id="server"
              name="server"
              placeholder="https://example.com:8080"
              value={credentials.server}
              onChange={handleChange}
              className={errors.server ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.server && <span className="error-message">{errors.server}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              className={errors.username ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.username && <span className="error-message">{errors.username}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>

          <div className="info-box">
            <span className="info-icon">‚ÑπÔ∏è</span>
            <span className="info-text">
              Futni URL-n√´ e plot√´ t√´ serverit (p.sh.: https://example.com:8080)
            </span>
          </div>

          <div className="modal-actions">
            <button 
              type="submit" 
              className="btn-primary"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="spinner-small"></span>
                  Duke u lidhur...
                </>
              ) : (
                'Lidhu'
              )}
            </button>
            <button 
              type="button" 
              className="btn-secondary"
              onClick={onClose}
              disabled={isLoading}
            >
              Anulo
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default XtreamLogin;
