// src/components/IPTVPlayer.js - Player modern p√´r HLS
import React, { useRef, useEffect } from 'react';
import Hls from 'hls.js';

const IPTVPlayer = ({ streamUrl, isPlaying, onPlay, onPause }) => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);

  // Inicializon ose ndalon HLS
  useEffect(() => {
    if (!streamUrl || !videoRef.current) return;

    // Kontrollon n√´se stream-i √´sht√´ HLS
    const isHls = streamUrl.toLowerCase().includes('.m3u8');
    
    // N√´se HLS.js √´sht√´ i disponuesh√´m dhe stream-i √´sht√´ HLS
    if (isHls && Hls.isSupported()) {
      // Shkat√´rron instanc√´n e vjet√´r
      if (hlsRef.current) {
        hlsRef.current.destroy();
      }

      // Krijon nj√´ instanc√´ t√´ re HLS
      const hls = new Hls({
        enableWorker: true,
        lowLatencyMode: true,
        backBufferLength: 90,
        maxBufferLength: 30,
        maxMaxBufferLength: 60,
        maxBufferSize: 60 * 1000 * 1000,
        maxBufferHole: 0.5,
        maxFragLookUpTolerance: 0.2,
        liveSyncDurationCount: 3,
        liveMaxLatencyDurationCount: 10,
        liveDurationInfinity: true,
        enableSoftwareAES: true,
        manifestLoadingTimeOut: 10000,
        manifestLoadingMaxRetry: 3,
        manifestLoadingRetryDelay: 500,
        manifestLoadingMaxRetryTimeout: 10000,
        levelLoadingTimeOut: 10000,
        levelLoadingMaxRetry: 3,
        levelLoadingRetryDelay: 500,
        levelLoadingMaxRetryTimeout: 10000,
        fragLoadingTimeOut: 20000,
        fragLoadingMaxRetry: 3,
        fragLoadingRetryDelay: 500,
        fragLoadingMaxRetryTimeout: 10000,
        startFragPrefetch: true,
        testBandwidth: true
      });

      hlsRef.current = hls;

      // Ngarkon stream-in
      hls.loadSource(streamUrl);
      hls.attachMedia(videoRef.current);

      // Event listeners p√´r HLS
      hls.on(Hls.Events.MANIFEST_PARSED, () => {
        console.log('‚úÖ HLS manifest u analizua me sukses');
        if (isPlaying && videoRef.current) {
          videoRef.current.play().catch(e => console.error('Gabim n√´ play:', e));
        }
      });

      hls.on(Hls.Events.ERROR, (event, data) => {
        console.error('‚ùå Gabim HLS:', data);
        if (data.fatal) {
          switch (data.type) {
            case Hls.ErrorTypes.NETWORK_ERROR:
              console.log('Trying to recover from network error...');
              hls.startLoad();
              break;
            case Hls.ErrorTypes.MEDIA_ERROR:
              console.log('Trying to recover from media error...');
              hls.recoverMediaError();
              break;
            default:
              console.log('Cannot recover, destroying HLS instance...');
              hls.destroy();
              break;
          }
        }
      });

      // Pastrim n√´ unmount
      return () => {
        if (hlsRef.current) {
          hlsRef.current.destroy();
        }
      };
    } 
    // N√´se HLS nuk mb√´shtetet, p√´rdor video element direkt
    else if (videoRef.current) {
      videoRef.current.src = streamUrl;
      videoRef.current.load();
    }

  }, [streamUrl]);

  // Kontrollon play/pause
  useEffect(() => {
    if (!videoRef.current) return;

    if (isPlaying) {
      videoRef.current.play().catch(e => {
        console.error('Gabim n√´ fillimin e luajtjes:', e);
        onPause?.();
      });
    } else {
      videoRef.current.pause();
    }
  }, [isPlaying, onPause]);

  // Event handlers p√´r video element
  const handlePlay = () => {
    onPlay?.();
  };

  const handlePause = () => {
    onPause?.();
  };

  const handleError = (e) => {
    console.error('‚ùå Gabim video:', e);
    console.error('Video error details:', videoRef.current?.error);
  };

  return (
    <div className="relative w-full h-full bg-black">
      <video
        ref={videoRef}
        className="w-full h-full object-contain"
        controls
        autoPlay={isPlaying}
        playsInline
        onPlay={handlePlay}
        onPause={handlePause}
        onError={handleError}
        crossOrigin="anonymous"
        preload="auto"
      >
        <source src={streamUrl} type="application/x-mpegURL" />
        <source src={streamUrl} type="video/mp4" />
        Shfletuesi juaj nuk mb√´shtet video HTML5.
      </video>
      
      {/* Overlay p√´r status */}
      {!streamUrl && (
        <div className="absolute inset-0 flex items-center justify-center bg-gradient-to-br from-gray-900 to-black">
          <div className="text-center">
            <div className="text-4xl mb-4">üì∫</div>
            <div className="text-gray-400 mb-4">Zgjidhni nj√´ kanal p√´r t√´ filluar shikimin</div>
          </div>
        </div>
      )}
    </div>
  );
};

export default IPTVPlayer;
