// src/components/HlsPlayer.jsx
import React, { useEffect, useRef, useState, useCallback } from 'react';
import Hls from 'hls.js';
import './HlsPlayer.css';

const HlsPlayer = ({ 
  src, 
  isPlaying, 
  onPlayPause, 
  onClose, 
  onError,
  theme,
  currentStreamInfo,
  currentEpg,
  epgData,
  onEpgUpdate
}) => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const [error, setError] = useState(null);
  const [retryCount, setRetryCount] = useState(0);
  const [isBuffering, setIsBuffering] = useState(false);
  const [useNativeControls, setUseNativeControls] = useState(false);
  const bufferTimerRef = useRef(null);
  const errorReportedRef = useRef(false);
  const MAX_RETRIES = 3;

  // Clean up buffer timer
  useEffect(() => {
    return () => {
      if (bufferTimerRef.current) {
        clearTimeout(bufferTimerRef.current);
      }
    };
  }, []);

  // Funksion p√´r t√´ raportuar error vet√´m nj√´ her√´
  const reportError = useCallback((errorData) => {
    if (!errorReportedRef.current && onError) {
      errorReportedRef.current = true;
      onError(errorData);
      
      // Reset after 2 seconds
      setTimeout(() => {
        errorReportedRef.current = false;
      }, 2000);
    }
  }, [onError]);

  // Funksion p√´r t√´ luajtur videon
  const playVideo = useCallback(async () => {
    if (!videoRef.current) return false;
    
    try {
      await videoRef.current.play();
      console.log('‚ñ∂Ô∏è Playing successfully');
      setIsBuffering(false);
      return true;
    } catch (err) {
      console.warn('Play failed:', err);
      if (err.name === 'NotAllowedError') {
        // Autoplay u bllokua - useri duhet t√´ klikoj√´ play
        setIsReady(true);
      }
      return false;
    }
  }, []);

  // Initialize player
  useEffect(() => {
    if (!src || !videoRef.current) return;

    let hls = null;
    setIsReady(false);
    setError(null);
    setIsBuffering(false);
    errorReportedRef.current = false;

    // Clean up previous instance
    if (hlsRef.current) {
      hlsRef.current.destroy();
      hlsRef.current = null;
    }

    // Detekto llojin e stream-it
    const isXtreamStream = src.includes('/live/') || src.includes('/movie/') || src.includes('/series/');
    const isDirectFile = src.endsWith('.mp4') || src.endsWith('.ts') || src.endsWith('.mkv') || src.endsWith('.avi') || src.endsWith('.mov');
    const isHlsStream = src.includes('.m3u8') || src.includes('playlist.m3u8');
    const isMpegDash = src.includes('.mpd');

    console.log('üé¨ Loading stream:', { 
      src: src.substring(0, 100), 
      isXtreamStream, 
      isDirectFile, 
      isHlsStream,
      isMpegDash 
    });

    // P√´r Xtream streams ose direkt file
    if (isXtreamStream || isDirectFile || !isHlsStream) {
      console.log('üé¨ Using direct video playback');
      
      // Set source dhe load
      videoRef.current.src = src;
      videoRef.current.load();
      
      // Event handlers
      const onCanPlay = () => {
        console.log('‚úÖ Video can play');
        setIsReady(true);
        if (isPlaying) {
          playVideo();
        }
      };

      const onLoadedMetadata = () => {
        console.log('‚úÖ Video metadata loaded');
        setIsReady(true);
      };

      const onWaiting = () => {
        console.log('‚è≥ Buffering...');
        setIsBuffering(true);
        
        if (bufferTimerRef.current) {
          clearTimeout(bufferTimerRef.current);
        }
        
        bufferTimerRef.current = setTimeout(() => {
          console.warn('Buffering timeout');
          setIsBuffering(false);
        }, 15000);
      };

      const onPlaying = () => {
        console.log('‚ñ∂Ô∏è Playing');
        setIsBuffering(false);
        if (bufferTimerRef.current) {
          clearTimeout(bufferTimerRef.current);
        }
      };

      const onPause = () => {
        console.log('‚è∏Ô∏è Paused');
      };

      const onEnded = () => {
        console.log('‚èπÔ∏è Ended');
        if (onPlayPause) onPlayPause();
      };

      const onStalled = () => {
        console.warn('‚ö†Ô∏è Stalled');
        setIsBuffering(true);
      };

      const onError = () => {
        const videoError = videoRef.current?.error;
        console.error('üé¨ Video error:', {
          code: videoError?.code,
          message: videoError?.message
        });

        // Mos shfaq error p√´r aborte normale
        if (videoError?.code === 4) {
          setError('Formati i videos nuk mb√´shtetet');
          reportError({ type: 'videoError', code: 4, details: 'Format not supported' });
        } else if (videoError?.code === 3) {
          setError('Gabim n√´ dekodimin e videos');
          reportError({ type: 'videoError', code: 3, details: 'Decode error' });
        } else if (videoError?.code === 2) {
          setError('Gabim n√´ rrjet');
          reportError({ type: 'videoError', code: 2, details: 'Network error' });
        } else if (videoError?.code === 1) {
          console.log('Video loading aborted');
          // Mos raporto error p√´r abort
        } else if (videoError) {
          setError('Gabim n√´ ngarkimin e videos');
          reportError({ type: 'videoError', code: videoError.code, details: videoError.message });
        }
      };

      // Add event listeners
      videoRef.current.addEventListener('canplay', onCanPlay);
      videoRef.current.addEventListener('loadedmetadata', onLoadedMetadata);
      videoRef.current.addEventListener('waiting', onWaiting);
      videoRef.current.addEventListener('playing', onPlaying);
      videoRef.current.addEventListener('pause', onPause);
      videoRef.current.addEventListener('ended', onEnded);
      videoRef.current.addEventListener('stalled', onStalled);
      videoRef.current.addEventListener('error', onError);
      
      // Check if already loaded
      if (videoRef.current.readyState >= 3) {
        setIsReady(true);
        if (isPlaying) {
          playVideo();
        }
      }
      
      return () => {
        if (videoRef.current) {
          videoRef.current.removeEventListener('canplay', onCanPlay);
          videoRef.current.removeEventListener('loadedmetadata', onLoadedMetadata);
          videoRef.current.removeEventListener('waiting', onWaiting);
          videoRef.current.removeEventListener('playing', onPlaying);
          videoRef.current.removeEventListener('pause', onPause);
          videoRef.current.removeEventListener('ended', onEnded);
          videoRef.current.removeEventListener('stalled', onStalled);
          videoRef.current.removeEventListener('error', onError);
          videoRef.current.pause();
          videoRef.current.removeAttribute('src');
          videoRef.current.load();
        }
      };
    }

    // P√´r HLS streams
    if (isHlsStream) {
      if (Hls.isSupported()) {
        try {
          console.log('üé¨ Using HLS.js');
          
          hls = new Hls({
            enableWorker: true,
            lowLatencyMode: false,
            backBufferLength: 60,
            maxBufferLength: 30,
            maxMaxBufferLength: 60,
            maxBufferSize: 30 * 1000 * 1000,
            maxBufferHole: 0.5,
            manifestLoadingTimeOut: 15000,
            manifestLoadingMaxRetry: 5,
            manifestLoadingRetryDelay: 1000,
            manifestLoadingMaxRetryTimeout: 30000,
            levelLoadingTimeOut: 15000,
            levelLoadingMaxRetry: 4,
            levelLoadingRetryDelay: 1000,
            levelLoadingMaxRetryTimeout: 30000,
            fragLoadingTimeOut: 20000,
            fragLoadingMaxRetry: 4,
            fragLoadingRetryDelay: 1000,
            fragLoadingMaxRetryTimeout: 30000,
            startLevel: -1,
            debug: false,
          });

          hlsRef.current = hls;

          hls.on(Hls.Events.MEDIA_ATTACHED, () => {
            console.log('‚úÖ HLS media attached');
            hls.loadSource(src);
          });

          hls.on(Hls.Events.MANIFEST_PARSED, (event, data) => {
            console.log('‚úÖ HLS manifest parsed, levels:', data.levels.length);
            setIsReady(true);
            setError(null);
            
            if (isPlaying) {
              playVideo();
            }
          });

          hls.on(Hls.Events.ERROR, (event, data) => {
            console.error('‚ùå HLS error:', data);
            
            if (data.fatal) {
              switch (data.type) {
                case Hls.ErrorTypes.NETWORK_ERROR:
                  console.log('Network error, trying to recover...');
                  hls.startLoad();
                  break;
                case Hls.ErrorTypes.MEDIA_ERROR:
                  console.log('Media error, trying to recover...');
                  hls.recoverMediaError();
                  break;
                default:
                  console.log('Fatal error, falling back to direct video...');
                  setUseNativeControls(true);
                  if (videoRef.current) {
                    videoRef.current.src = src;
                    videoRef.current.load();
                  }
                  break;
              }
            }
            
            // Raporto error por jo n√´ m√´nyr√´ ciklike
            if (data.fatal && data.type !== Hls.ErrorTypes.OTHER_ERROR) {
              reportError({ type: 'hlsError', details: data.type });
            }
          });

          hls.attachMedia(videoRef.current);
        } catch (err) {
          console.error('Failed to initialize HLS:', err);
          videoRef.current.src = src;
          videoRef.current.load();
        }
      } else if (videoRef.current.canPlayType('application/vnd.apple.mpegurl')) {
        console.log('üé¨ Using Safari native HLS');
        videoRef.current.src = src;
        videoRef.current.load();
        
        const onLoadedMetadata = () => {
          setIsReady(true);
          if (isPlaying) {
            playVideo();
          }
        };
        
        videoRef.current.addEventListener('loadedmetadata', onLoadedMetadata);
        
        return () => {
          videoRef.current?.removeEventListener('loadedmetadata', onLoadedMetadata);
        };
      }
    }

    return () => {
      if (hls) {
        hls.destroy();
        hlsRef.current = null;
      }
    };
  }, [src, isPlaying, playVideo, reportError]); // Shtuar var√´sit√´ e nevojshme

  // Handle play/pause separately
  useEffect(() => {
    if (!videoRef.current || !isReady) return;

    if (isPlaying) {
      playVideo();
    } else {
      videoRef.current.pause();
    }
  }, [isPlaying, isReady, playVideo]);

  const handleRetry = useCallback(() => {
    if (retryCount >= MAX_RETRIES) {
      setError('Nuk mund t√´ luhet stream-i. Provo nj√´ tjet√´r.');
      return;
    }

    setRetryCount(prev => prev + 1);
    setError(null);
    setUseNativeControls(true);
    errorReportedRef.current = false;
    
    // Reload video
    if (videoRef.current) {
      const currentSrc = src;
      videoRef.current.src = '';
      videoRef.current.load();
      
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.src = currentSrc;
          videoRef.current.load();
          
          setTimeout(() => {
            if (videoRef.current && isPlaying) {
              videoRef.current.play().catch(e => {
                console.warn('Retry failed:', e);
              });
            }
          }, 500);
        }
      }, 100);
    }
  }, [retryCount, src, isPlaying]);

  const handleFullscreen = useCallback(() => {
    if (videoRef.current) {
      if (videoRef.current.requestFullscreen) {
        videoRef.current.requestFullscreen();
      } else if (videoRef.current.webkitRequestFullscreen) {
        videoRef.current.webkitRequestFullscreen();
      } else if (videoRef.current.msRequestFullscreen) {
        videoRef.current.msRequestFullscreen();
      }
    }
  }, []);

  if (!src) return null;

  return (
    <div className={`hls-player theme-${theme}`}>
      <div className="video-container">
        <video
          ref={videoRef}
          className="video-element"
          playsInline
          controls={true}
          preload="auto"
          poster={currentStreamInfo?.logo}
        />
        
        {/* Loading Indicator */}
        {!isReady && !error && (
          <div className="player-loading">
            <div className="loading-spinner"></div>
            <p>Duke ngarkuar stream-in...</p>
          </div>
        )}
        
        {/* Buffering Indicator */}
        {isBuffering && isReady && isPlaying && (
          <div className="player-buffering">
            <div className="buffering-spinner"></div>
            <p>Duke bufferuar...</p>
          </div>
        )}
        
        {/* Error Display */}
        {error && (
          <div className="player-error">
            <span className="error-icon">‚ö†Ô∏è</span>
            <p>{error}</p>
            <button onClick={handleRetry} className="retry-btn">
              Provo p√´rs√´ri
            </button>
            <button 
              onClick={() => window.open(src, '_blank')} 
              className="direct-link-btn"
            >
              Hap direkt
            </button>
          </div>
        )}
        
        {/* Play Button Overlay */}
        {!isPlaying && isReady && !error && (
          <button className="play-btn-overlay" onClick={onPlayPause}>
            ‚ñ∂
          </button>
        )}

        {/* Fullscreen Button */}
        {isReady && !error && (
          <button className="fullscreen-btn" onClick={handleFullscreen}>
            ‚õ∂
          </button>
        )}
      </div>

      {/* Channel Info */}
      {currentStreamInfo && (
        <div className="player-info">
          <div className="channel-info">
            {currentStreamInfo.logo && (
              <img 
                src={currentStreamInfo.logo} 
                alt={currentStreamInfo.name}
                className="channel-logo"
                onError={(e) => {
                  e.target.onerror = null;
                  e.target.style.display = 'none';
                }}
              />
            )}
            <div className="channel-details">
              <span className="channel-name">{currentStreamInfo.name}</span>
              <span className="channel-category">{currentStreamInfo.category}</span>
            </div>
          </div>
          
          {currentEpg && (
            <div className="epg-info">
              <span className="epg-title">{currentEpg.title}</span>
              <span className="epg-time">
                {new Date(currentEpg.start_timestamp * 1000).toLocaleTimeString([], { 
                  hour: '2-digit', 
                  minute: '2-digit' 
                })}
              </span>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HlsPlayer;
