import { useState, useEffect } from "react";
import {
  Tv, Film, Layers, Heart, Folder, Settings, Search,
  Play, Pause, Volume2, Maximize, Clock, Home, 
  ChevronRight, ChevronLeft, X, Plus, Trash2, 
  Edit, Globe, Server, Star, ListVideo,
  AlertCircle, Check, ExternalLink
} from "lucide-react";
import IPTVPlayer from "./components/IPTVPlayer";
import { fetchChannelsFromM3U, testPlaylistConnection, testXtreamConnection } from "./services/iptvService";

export default function App() {
  const [section, setSection] = useState("live");
  const [isPlaying, setIsPlaying] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState(() => 
    JSON.parse(localStorage.getItem("iptvFavorites") || "[]")
  );
  const [playlists, setPlaylists] = useState(() => 
    JSON.parse(localStorage.getItem("iptvPlaylists") || "[]")
  );
  const [activePlaylistId, setActivePlaylistId] = useState(() => 
    localStorage.getItem("activePlaylistId") || null
  );
  const [channels, setChannels] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentStream, setCurrentStream] = useState(null);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [showPlaylistModal, setShowPlaylistModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [currentPlaylist, setCurrentPlaylist] = useState({
    id: null, name: "", type: "m3u", url: "", 
    username: "", password: "", serverUrl: ""
  });
  const [showToast, setShowToast] = useState({ show: false, message: "", type: "success" });
  const [volume, setVolume] = useState(100);
  const [isMuted, setIsMuted] = useState(false);

  // Toast notification
  const showNotification = (message, type = "success") => {
    setShowToast({ show: true, message, type });
    setTimeout(() => setShowToast({ show: false, message: "", type: "success" }), 3000);
  };

  // Save to localStorage
  const saveToLocalStorage = (key, value) => {
    try {
      localStorage.setItem(key, JSON.stringify(value));
    } catch (error) {
      console.error("Gabim gjatë ruajtjes:", error);
      showNotification("Gabim gjatë ruajtjes së të dhënave", "error");
    }
  };

  // Toggle favorite
  const toggleFavorite = (channelId) => {
    const newFavorites = favorites.includes(channelId)
      ? favorites.filter(id => id !== channelId)
      : [...favorites, channelId];
    setFavorites(newFavorites);
    saveToLocalStorage("iptvFavorites", newFavorites);
    
    if (newFavorites.includes(channelId)) {
      showNotification("U shtua në të preferuara");
    } else {
      showNotification("U hoq nga të preferuarat");
    }
  };

  // Load channels from playlist
  const loadChannels = async (playlistId) => {
    const playlist = playlists.find(p => p.id === playlistId);
    if (!playlist) {
      showNotification("Lista nuk u gjet", "error");
      return;
    }
    
    setIsLoading(true);
    setCurrentStream(null);
    setCurrentChannel(null);

    try {
      let m3uUrl = playlist.url;
      
      // Test connection before loading
      if (playlist.type === "m3u") {
        const isConnected = await testPlaylistConnection(m3uUrl);
        if (!isConnected) {
          showNotification("Nuk mund të lidhem me listën. Kontrollo URL-në.", "warning");
        }
      } else if (playlist.type === "xtream") {
        const cleanServer = playlist.serverUrl.replace(/\/$/, '');
        m3uUrl = `${cleanServer}/get.php?username=${playlist.username}&password=${playlist.password}&type=m3u_plus`;
        
        const isConnected = await testXtreamConnection(
          playlist.serverUrl,
          playlist.username,
          playlist.password
        );
        if (!isConnected) {
          showNotification("Kredencialet Xtream nuk janë valide", "warning");
        }
      }

      const fetchedChannels = await fetchChannelsFromM3U(m3uUrl);
      const validChannels = fetchedChannels.filter(ch => ch.streamUrl?.trim() !== '');
      
      setChannels(validChannels);
      setActivePlaylistId(playlistId);
      localStorage.setItem("activePlaylistId", playlistId);

      if (validChannels.length > 0) {
        setCurrentStream(validChannels[0].streamUrl);
        setCurrentChannel(validChannels[0]);
        setIsPlaying(true);
        showNotification(`U ngarkuan ${validChannels.length} kanale`);
      } else {
        showNotification("Lista nuk përmban kanale valide", "warning");
      }
    } catch (error) {
      console.error("Gabim gjatë ngarkimit:", error);
      showNotification(`Gabim: ${error.message}`, "error");
    } finally {
      setIsLoading(false);
    }
  };

  // Change channel
  const changeChannel = (channel) => {
    setCurrentStream(channel.streamUrl);
    setCurrentChannel(channel);
    setIsPlaying(true);
    showNotification(`Duke luajtur: ${channel.name}`);
  };

  // Navigate to next/prev channel
  const navigateChannel = (direction) => {
    if (!channels.length || !currentChannel) return;
    
    const currentIndex = channels.findIndex(ch => ch.streamUrl === currentChannel.streamUrl);
    let newIndex;
    
    if (direction === 'next') {
      newIndex = (currentIndex + 1) % channels.length;
    } else {
      newIndex = (currentIndex - 1 + channels.length) % channels.length;
    }
    
    changeChannel(channels[newIndex]);
  };

  // Save playlist
  const savePlaylist = async () => {
    // Validation
    if (!currentPlaylist.name.trim()) {
      showNotification("Vendos emër për listën!", "error");
      return;
    }
    
    if (currentPlaylist.type === "m3u" && !currentPlaylist.url.trim()) {
      showNotification("Vendos URL për listën M3U!", "error");
      return;
    }
    
    if (currentPlaylist.type === "xtream") {
      if (!currentPlaylist.serverUrl.trim() || 
          !currentPlaylist.username.trim() || 
          !currentPlaylist.password.trim()) {
        showNotification("Plotëso të gjitha fushat për Xtream!", "error");
        return;
      }
      
      // Test Xtream connection
      try {
        const isValid = await testXtreamConnection(
          currentPlaylist.serverUrl,
          currentPlaylist.username,
          currentPlaylist.password
        );
        
        if (!isValid) {
          showNotification("Kredencialet Xtream nuk janë valide!", "warning");
          if (!confirm("Lidhja dështoi. Deshiron të vazhdosh prapëseprapë?")) {
            return;
          }
        }
      } catch (error) {
        showNotification("Gabim gjatë testimit të kredencialeve", "warning");
      }
    }

    let newPlaylists = [...playlists];
    if (modalMode === "add") {
      const newId = Date.now().toString();
      newPlaylists.push({ 
        ...currentPlaylist, 
        id: newId, 
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      });
      showNotification("Lista u shtua me sukses!");
    } else {
      newPlaylists = newPlaylists.map(p => 
        p.id === currentPlaylist.id 
          ? { ...currentPlaylist, updatedAt: new Date().toISOString() }
          : p
      );
      showNotification("Lista u përditësua me sukses!");
    }

    setPlaylists(newPlaylists);
    saveToLocalStorage("iptvPlaylists", newPlaylists);

    if (modalMode === "add" && newPlaylists.length === 1) {
      loadChannels(newPlaylists[0].id);
    }

    setShowPlaylistModal(false);
    setCurrentPlaylist({ 
      id: null, name: "", type: "m3u", url: "", 
      username: "", password: "", serverUrl: "" 
    });
  };

  // Delete playlist
  const deletePlaylist = (playlistId, e) => {
    e?.stopPropagation();
    if (!confirm("Je i sigurtë që do ta fshish këtë listë?")) return;
    
    const newPlaylists = playlists.filter(p => p.id !== playlistId);
    setPlaylists(newPlaylists);
    saveToLocalStorage("iptvPlaylists", newPlaylists);

    if (activePlaylistId === playlistId) {
      setActivePlaylistId(null);
      setChannels([]);
      setCurrentStream(null);
      setCurrentChannel(null);
      localStorage.removeItem("activePlaylistId");
    }
    
    showNotification("Lista u fshi me sukses!");
  };

  // Edit playlist
  const editPlaylist = (playlist, e) => {
    e?.stopPropagation();
    setCurrentPlaylist(playlist);
    setModalMode("edit");
    setShowPlaylistModal(true);
  };

  // Add new playlist
  const addNewPlaylist = () => {
    setCurrentPlaylist({ 
      id: null, name: "", type: "m3u", url: "", 
      username: "", password: "", serverUrl: "" 
    });
    setModalMode("add");
    setShowPlaylistModal(true);
  };

  // Volume control
  const toggleMute = () => {
    setIsMuted(!isMuted);
  };

  const changeVolume = (e) => {
    const newVolume = parseInt(e.target.value);
    setVolume(newVolume);
    setIsMuted(newVolume === 0);
  };

  // Load active playlist on mount
  useEffect(() => {
    if (activePlaylistId) {
      loadChannels(activePlaylistId);
    }
  }, []);

  // Keyboard shortcuts
  useEffect(() => {
    const handleKeyPress = (e) => {
      // Space to play/pause
      if (e.code === 'Space' && currentStream) {
        e.preventDefault();
        setIsPlaying(!isPlaying);
      }
      // Arrow keys for channel navigation
      if (e.code === 'ArrowRight') {
        navigateChannel('next');
      }
      if (e.code === 'ArrowLeft') {
        navigateChannel('prev');
      }
      // F key for favorite
      if (e.code === 'KeyF' && currentChannel) {
        toggleFavorite(currentChannel.streamUrl);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [currentStream, isPlaying, currentChannel]);

  const activePlaylist = playlists.find(p => p.id === activePlaylistId);

  // Filter channels based on section and search
  const filteredChannels = channels.filter(channel => {
    const matchesSearch = channel.name?.toLowerCase()
      .includes(searchQuery.toLowerCase());
    
    if (section === "fav") {
      return matchesSearch && favorites.includes(channel.streamUrl);
    }
    
    return matchesSearch;
  });

  return (
    <div className="flex h-screen bg-gradient-to-br from-[#0b0f14] to-[#1a1f2e] text-white overflow-hidden">
      {/* Toast Notification */}
      {showToast.show && (
        <div className={`fixed top-4 right-4 z-50 px-6 py-3 rounded-lg shadow-lg transition-all duration-300 ${
          showToast.type === "success" ? "bg-green-600" : 
          showToast.type === "error" ? "bg-red-600" : 
          "bg-yellow-600"
        }`}>
          <div className="flex items-center gap-2">
            {showToast.type === "success" ? <Check size={20} /> : <AlertCircle size={20} />}
            <span>{showToast.message}</span>
          </div>
        </div>
      )}

      {/* Sidebar */}
      <aside className="w-64 bg-gradient-to-b from-[#111827] to-[#1f2937] flex flex-col p-4 gap-2 border-r border-gray-800">
        <div className="flex items-center gap-3 mb-6 p-2">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
            <Tv size={24} />
          </div>
          <h1 className="text-xl font-bold">IPTV Player</h1>
        </div>
        
        <NavItem 
          icon={<Tv />} 
          label="Live TV" 
          active={section === "live"} 
          onClick={() => setSection("live")} 
        />
        <NavItem 
          icon={<Film />} 
          label="Filma" 
          active={section === "movies"} 
          onClick={() => setSection("movies")} 
        />
        <NavItem 
          icon={<Layers />} 
          label="Seriale" 
          active={section === "series"} 
          onClick={() => setSection("series")} 
        />
        <NavItem 
          icon={<Heart />} 
          label="Të preferuarat" 
          active={section === "fav"} 
          onClick={() => setSection("fav")} 
          badge={favorites.length} 
        />
        <NavItem 
          icon={<Folder />} 
          label="Listat" 
          active={section === "lists"} 
          onClick={() => setSection("lists")} 
          badge={playlists.length} 
        />
        
        <div className="mt-auto pt-4 border-t border-gray-800">
          <NavItem 
            icon={<Settings />} 
            label="Cilësimet" 
            active={section === "settings"} 
            onClick={() => setSection("settings")} 
          />
        </div>
      </aside>

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <header className="p-4 border-b border-gray-800 bg-gradient-to-r from-[#111827]/80 to-[#1f2937]/80 backdrop-blur-sm">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="relative w-96">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Kërko kanale..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2.5 bg-gray-900/50 border border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                {searchQuery && (
                  <button
                    onClick={() => setSearchQuery("")}
                    className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white"
                  >
                    <X size={18} />
                  </button>
                )}
              </div>
              
              {/* Keyboard shortcuts help */}
              <div className="text-sm text-gray-400 hidden lg:block">
                <span className="px-2 py-1 bg-gray-800 rounded">Space</span> Play/Pause • 
                <span className="px-2 py-1 bg-gray-800 rounded mx-1">← →</span> Kanale •
                <span className="px-2 py-1 bg-gray-800 rounded mx-1">F</span> Favorite
              </div>
            </div>
            
            <div className="flex items-center gap-4">
              {activePlaylist && (
                <div className="flex items-center gap-2 bg-gray-800/50 px-3 py-1.5 rounded-lg">
                  <div className={`p-1.5 rounded-full ${activePlaylist.type === 'm3u' ? 'bg-blue-500/20' : 'bg-green-500/20'}`}>
                    {activePlaylist.type === 'm3u' ? <Globe size={14} /> : <Server size={14} />}
                  </div>
                  <span className="text-green-400 font-medium">{activePlaylist.name}</span>
                  <span className="text-gray-400 text-sm">({channels.length} kanale)</span>
                </div>
              )}
            </div>
          </div>
        </header>

        <main className="flex-1 p-6 overflow-y-auto">
          {section === "live" && (
            <LiveTVContent
              channels={filteredChannels}
              favorites={favorites}
              toggleFavorite={toggleFavorite}
              currentStream={currentStream}
              changeChannel={changeChannel}
              isLoading={isLoading}
              isPlaying={isPlaying}
              setIsPlaying={setIsPlaying}
              loadChannels={loadChannels}
              playlists={playlists}
              activePlaylist={activePlaylist}
              setShowPlaylistModal={setShowPlaylistModal}
              setModalMode={setModalMode}
              addNewPlaylist={addNewPlaylist}
            />
          )}
          
          {section === "movies" && (
            <MoviesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
            />
          )}
          
          {section === "series" && (
            <SeriesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
            />
          )}
          
          {section === "fav" && (
            <FavoritesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
              channels={channels}
              changeChannel={changeChannel}
            />
          )}
          
          {section === "lists" && (
            <PlaylistsContent 
              playlists={playlists} 
              activePlaylistId={activePlaylistId} 
              loadChannels={loadChannels} 
              deletePlaylist={deletePlaylist} 
              editPlaylist={editPlaylist}
              addNewPlaylist={addNewPlaylist}
            />
          )}
          
          {section === "settings" && (
            <SettingsContent 
              playlists={playlists} 
              activePlaylistId={activePlaylistId} 
              loadChannels={loadChannels} 
              favorites={favorites}
              setFavorites={setFavorites}
              setPlaylists={setPlaylists}
              setActivePlaylistId={setActivePlaylistId}
              setChannels={setChannels}
              setCurrentStream={setCurrentStream}
              setCurrentChannel={setCurrentChannel}
            />
          )}
        </main>

        {/* Player section */}
        <div className="border-t border-gray-800 bg-[#111827]">
          {currentStream ? (
            <div className="h-64">
              <IPTVPlayer
                streamUrl={currentStream}
                isPlaying={isPlaying}
                onPlay={() => setIsPlaying(true)}
                onPause={() => setIsPlaying(false)}
              />
            </div>
          ) : (
            <div className="p-6 text-center text-gray-400">
              <Tv size={48} className="mx-auto mb-3 opacity-50" />
              <p>Zgjidh një kanal për të filluar shikimin</p>
              {playlists.length === 0 && (
                <button
                  onClick={addNewPlaylist}
                  className="mt-3 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg font-medium"
                >
                  Shto Listën të Parë
                </button>
              )}
            </div>
          )}
          
          {/* Player controls */}
          <div className="p-4 bg-[#0d1117]">
            <div className="flex items-center justify-between max-w-6xl mx-auto">
              <div className="flex items-center gap-4">
                {currentChannel && (
                  <>
                    <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center overflow-hidden">
                      {currentChannel.logo ? (
                        <img 
                          src={currentChannel.logo} 
                          alt={currentChannel.name} 
                          className="w-full h-full object-cover"
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.parentElement.innerHTML = `<Tv size={24} />`;
                          }}
                        />
                      ) : (
                        <Tv size={24} />
                      )}
                    </div>
                    <div className="min-w-0">
                      <div className="font-medium truncate max-w-xs">{currentChannel.name}</div>
                      <div className="text-sm text-gray-400 flex items-center gap-2">
                        <span>Live TV</span>
                        {currentChannel.group && (
                          <>
                            <span>•</span>
                            <span className="px-2 py-0.5 bg-gray-800 rounded text-xs">
                              {currentChannel.group}
                            </span>
                          </>
                        )}
                      </div>
                    </div>
                  </>
                )}
              </div>
              
              <div className="flex items-center gap-6">
                <div className="flex items-center gap-2">
                  <button 
                    onClick={() => navigateChannel('prev')}
                    className="p-2 hover:bg-gray-800 rounded-lg transition"
                    title="Kanalin e mëparshëm (←)"
                  >
                    <ChevronLeft size={20} />
                  </button>
                  
                  <button 
                    onClick={() => setIsPlaying(!isPlaying)} 
                    className="p-3 bg-blue-600 hover:bg-blue-700 rounded-xl transition"
                    title={isPlaying ? "Pause (Space)" : "Play (Space)"}
                  >
                    {isPlaying ? <Pause size={20} /> : <Play size={20} />}
                  </button>
                  
                  <button 
                    onClick={() => navigateChannel('next')}
                    className="p-2 hover:bg-gray-800 rounded-lg transition"
                    title="Kanalin pasardhës (→)"
                  >
                    <ChevronRight size={20} />
                  </button>
                </div>
                
                <div className="flex items-center gap-2">
                  <button 
                    onClick={toggleMute}
                    className="p-2 hover:bg-gray-800 rounded-lg transition"
                    title={isMuted ? "Unmute" : "Mute"}
                  >
                    <Volume2 size={20} />
                  </button>
                  
                  <input
                    type="range"
                    min="0"
                    max="100"
                    value={isMuted ? 0 : volume}
                    onChange={changeVolume}
                    className="w-24 accent-blue-600"
                  />
                </div>
                
                {currentChannel && (
                  <button 
                    onClick={() => toggleFavorite(currentChannel.streamUrl)}
                    className={`p-2 rounded-lg transition ${favorites.includes(currentChannel.streamUrl) ? 'text-red-500 bg-red-500/10' : 'text-gray-400 hover:bg-gray-800'}`}
                    title="Shto në të preferuara (F)"
                  >
                    <Heart size={20} fill={favorites.includes(currentChannel.streamUrl) ? 'currentColor' : 'none'} />
                  </button>
                )}
              </div>
              
              <div className="flex items-center gap-4">
                <button className="p-2 hover:bg-gray-800 rounded-lg transition" title="Fullscreen">
                  <Maximize size={20} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Playlist Modal */}
      {showPlaylistModal && (
        <PlaylistModal
          currentPlaylist={currentPlaylist}
          setCurrentPlaylist={setCurrentPlaylist}
          modalMode={modalMode}
          savePlaylist={savePlaylist}
          closeModal={() => {
            setShowPlaylistModal(false);
            setCurrentPlaylist({ 
              id: null, name: "", type: "m3u", url: "", 
              username: "", password: "", serverUrl: "" 
            });
          }}
        />
      )}
    </div>
  );
}

// ---------- Helper Components ----------
function NavItem({ icon, label, active, onClick, badge }) {
  return (
    <button
      className={`flex items-center gap-3 p-3 w-full text-left rounded-lg transition-all duration-200 ${active ? "bg-blue-600/30 text-blue-400 border-l-4 border-blue-500" : "hover:bg-gray-800 text-gray-300 border-l-4 border-transparent"}`}
      onClick={onClick}
    >
      <div className={active ? "text-blue-400" : "text-gray-400"}>
        {icon}
      </div>
      <span className="flex-1 font-medium">{label}</span>
      {badge > 0 && (
        <span className="text-xs bg-blue-600 rounded-full px-2 py-0.5 min-w-[20px] text-center">
          {badge}
        </span>
      )}
    </button>
  );
}

// ---------- Live TV Content Component ----------
function LiveTVContent({ 
  channels, 
  favorites, 
  toggleFavorite, 
  changeChannel, 
  isLoading, 
  playlists,
  activePlaylist,
  addNewPlaylist 
}) {
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-400">Duke ngarkuar kanalet...</p>
          <p className="text-gray-500 text-sm mt-1">Kjo mund të zgjasë disa sekonda</p>
        </div>
      </div>
    );
  }

  if (playlists.length === 0) {
    return (
      <div className="text-center py-12">
        <Folder size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Mirësevini në IPTV Player</h3>
        <p className="text-gray-400 mb-6">Shto listën tuaj të parë IPTV për të filluar</p>
        <button
          onClick={addNewPlaylist}
          className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 rounded-xl font-medium flex items-center gap-2 mx-auto transition-all"
        >
          <Plus size={20} />
          Shto Listë të Re
        </button>
      </div>
    );
  }

  if (!activePlaylist) {
    return (
      <div className="text-center py-12">
        <ListVideo size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Zgjidh një listë aktive</h3>
        <p className="text-gray-400">Shko te seksioni "Listat" dhe kliko "Aktivizo" në listën që dëshiron</p>
      </div>
    );
  }

  if (channels.length === 0) {
    return (
      <div className="text-center py-12">
        <AlertCircle size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Nuk u gjetën kanale</h3>
        <p className="text-gray-400">Lista është bosh ose ka ndodhur një gabim gjatë ngarkimit</p>
        <button
          onClick={() => window.location.reload()}
          className="mt-4 px-4 py-2 bg-gray-800 hover:bg-gray-700 rounded-lg font-medium"
        >
          Rifresko Faqen
        </button>
      </div>
    );
  }

  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-1">Kanale Live</h2>
          <p className="text-gray-400">{channels.length} kanale të disponueshme në {activePlaylist.name}</p>
        </div>
        <div className="flex items-center gap-2 text-sm text-gray-400">
          <Heart size={16} className="text-red-500" />
          <span>{favorites.length} të preferuara</span>
        </div>
      </div>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-6 gap-4">
        {channels.map((channel) => (
          <ChannelCard
            key={channel.streamUrl}
            channel={channel}
            isFavorite={favorites.includes(channel.streamUrl)}
            onToggleFavorite={() => toggleFavorite(channel.streamUrl)}
            onClick={() => changeChannel(channel)}
          />
        ))}
      </div>
    </div>
  );
}

// ---------- Channel Card Component ----------
function ChannelCard({ channel, isFavorite, onToggleFavorite, onClick }) {
  return (
    <div
      className="bg-gray-800/50 rounded-xl overflow-hidden border border-gray-700 hover:border-blue-500/50 hover:bg-gray-800 transition-all duration-300 cursor-pointer group relative"
      onClick={onClick}
    >
      <div className="p-4">
        <div className="flex items-start justify-between mb-3">
          <div className="w-12 h-12 bg-gradient-to-br from-blue-500/20 to-purple-600/20 rounded-lg flex items-center justify-center overflow-hidden">
            {channel.logo ? (
              <img 
                src={channel.logo} 
                alt={channel.name} 
                className="w-10 h-10 rounded object-cover"
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.parentElement.innerHTML = '<Tv size={20} class="text-blue-400" />';
                }}
              />
            ) : (
              <Tv size={20} className="text-blue-400" />
            )}
          </div>
          <button
            onClick={(e) => {
              e.stopPropagation();
              onToggleFavorite();
            }}
            className="p-1.5 rounded-full hover:bg-gray-700/50 transition z-10 relative"
          >
            <Heart
              size={18}
              className={`transition-colors ${isFavorite ? 'text-red-500 fill-red-500' : 'text-gray-400 group-hover:text-red-400'}`}
            />
          </button>
        </div>
        
        <h3 className="font-medium truncate text-sm">{channel.name}</h3>
        {channel.group && (
          <div className="flex items-center gap-1 mt-2">
            <span className="text-xs text-gray-400 bg-gray-900/50 px-2 py-0.5 rounded">
              {channel.group}
            </span>
            {channel.country && (
              <span className="text-xs text-gray-400 bg-gray-900/50 px-2 py-0.5 rounded">
                {channel.country}
              </span>
            )}
          </div>
        )}
      </div>
      
      <div className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-blue-500 to-purple-600 opacity-0 group-hover:opacity-100 transition-opacity"></div>
    </div>
  );
}

// ---------- Other Content Components ----------
function MoviesContent() { 
  return (
    <div className="text-center py-12">
      <Film size={64} className="mx-auto mb-4 text-gray-600" />
      <h2 className="text-2xl font-bold mb-2">Filma</h2>
      <p className="text-gray-400">Seksioni i filmave do të jetë në dispozicion së shpejti</p>
      <p className="text-gray-500 text-sm mt-2">Po punojmë për të shtuar katalogun e filmave</p>
    </div>
  );
}

function SeriesContent() { 
  return (
    <div className="text-center py-12">
      <Layers size={64} className="mx-auto mb-4 text-gray-600" />
      <h2 className="text-2xl font-bold mb-2">Seriale</h2>
      <p className="text-gray-400">Seksioni i serialeve do të jetë në dispozicion së shpejti</p>
      <p className="text-gray-500 text-sm mt-2">Po punojmë për të shtuar seritë TV</p>
    </div>
  );
}

function FavoritesContent({ favorites, channels, changeChannel }) {
  const favoriteChannels = channels.filter(ch => favorites.includes(ch.streamUrl));
  
  if (favorites.length === 0) {
    return (
      <div className="text-center py-12">
        <Heart size={64} className="mx-auto mb-4 text-gray-600" />
        <h2 className="text-2xl font-bold mb-2">Nuk ka të preferuara</h2>
        <p className="text-gray-400">Shto kanale në të preferuarat duke klikuar ikonën e zemrës</p>
        <div className="mt-4 p-4 bg-gray-800/30 rounded-lg inline-block">
          <p className="text-sm text-gray-400">Shiko kanalet dhe kliko <Heart size={14} className="inline text-red-500" /> për të shtuar</p>
        </div>
      </div>
    );
  }
  
  if (favoriteChannels.length === 0) {
    return (
      <div className="text-center py-12">
        <Heart size={64} className="mx-auto mb-4 text-gray-600" />
        <h2 className="text-2xl font-bold mb-2">Të preferuarat nuk janë në listën aktive</h2>
        <p className="text-gray-400">Ngarko listën që përmban kanalet e tua të preferuara</p>
      </div>
    );
  }
  
  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-1">Kanale të Preferuara</h2>
          <p className="text-gray-400">{favoriteChannels.length} nga {favorites.length} të preferuara janë në listën aktive</p>
        </div>
        <div className="flex items-center gap-2">
          <Heart size={20} className="text-red-500" />
          <span className="text-red-400 font-medium">{favoriteChannels.length}</span>
        </div>
      </div>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 2xl:grid-cols-6 gap-4">
        {favoriteChannels.map((channel) => (
          <div
            key={channel.streamUrl}
            className="bg-gray-800/50 rounded-xl overflow-hidden border-2 border-red-500/20 hover:border-red-500/40 hover:bg-gray-800 transition-all cursor-pointer group relative"
            onClick={() => changeChannel(channel)}
          >
            <div className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="w-12 h-12 bg-gradient-to-br from-red-500/20 to-pink-600/20 rounded-lg flex items-center justify-center overflow-hidden">
                  {channel.logo ? (
                    <img 
                      src={channel.logo} 
                      alt={channel.name} 
                      className="w-10 h-10 rounded object-cover"
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.parentElement.innerHTML = '<Tv size={20} class="text-red-400" />';
                      }}
                    />
                  ) : (
                    <Tv size={20} className="text-red-400" />
                  )}
                </div>
                <div className="p-1.5 rounded-full bg-red-500/20">
                  <Heart size={18} className="text-red-500 fill-red-500" />
                </div>
              </div>
              
              <h3 className="font-medium truncate text-sm">{channel.name}</h3>
              {channel.group && (
                <div className="flex items-center gap-1 mt-2">
                  <span className="text-xs text-gray-400 bg-gray-900/50 px-2 py-0.5 rounded">
                    {channel.group}
                  </span>
                </div>
              )}
            </div>
            
            <div className="absolute bottom-0 left-0 right-0 h-1 bg-gradient-to-r from-red-500 to-pink-600"></div>
          </div>
        ))}
      </div>
    </div>
  );
}

function PlaylistsContent({ 
  playlists, 
  activePlaylistId, 
  loadChannels, 
  deletePlaylist, 
  editPlaylist,
  addNewPlaylist 
}) {
  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-1">Listat IPTV</h2>
          <p className="text-gray-400">Menaxho listat tuaja të kanaleve</p>
        </div>
        <button
          onClick={addNewPlaylist}
          className="px-5 py-2.5 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 rounded-xl font-medium flex items-center gap-2 transition-all"
        >
          <Plus size={20} />
          Shto Listë të Re
        </button>
      </div>
      
      {playlists.length === 0 ? (
        <div className="text-center py-12">
          <Folder size={64} className="mx-auto mb-4 text-gray-600" />
          <h3 className="text-xl font-bold mb-2">Nuk ka lista të shtuara</h3>
          <p className="text-gray-400 mb-6">Shto listën tuaj të parë IPTV për të filluar</p>
          <button
            onClick={addNewPlaylist}
            className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 rounded-xl font-medium flex items-center gap-2 mx-auto transition-all"
          >
            <Plus size={20} />
            Shto Listë të Re
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {playlists.map((playlist) => (
            <PlaylistCard
              key={playlist.id}
              playlist={playlist}
              isActive={activePlaylistId === playlist.id}
              onActivate={() => loadChannels(playlist.id)}
              onEdit={(e) => editPlaylist(playlist, e)}
              onDelete={(e) => deletePlaylist(playlist.id, e)}
            />
          ))}
        </div>
      )}
    </div>
  );
}

// ---------- Playlist Card Component ----------
function PlaylistCard({ playlist, isActive, onActivate, onEdit, onDelete }) {
  return (
    <div
      className={`bg-gray-800/50 rounded-xl p-5 border-2 transition-all ${isActive ? 'border-blue-500 bg-blue-500/10' : 'border-gray-700 hover:border-gray-600 hover:bg-gray-800/70'}`}
    >
      <div className="flex items-start justify-between mb-4">
        <div className={`p-3 rounded-lg ${playlist.type === 'm3u' ? 'bg-blue-500/20' : 'bg-green-500/20'}`}>
          {playlist.type === 'm3u' ? <Globe size={24} /> : <Server size={24} />}
        </div>
        <div className="flex gap-2">
          <button
            onClick={onEdit}
            className="p-2 hover:bg-gray-700 rounded-lg transition"
            title="Edito"
          >
            <Edit size={16} />
          </button>
          <button
            onClick={onDelete}
            className="p-2 hover:bg-red-500/20 hover:text-red-400 rounded-lg transition"
            title="Fshi"
          >
            <Trash2 size={16} />
          </button>
        </div>
      </div>
      
      <h3 className="font-bold text-lg mb-1 truncate">{playlist.name}</h3>
      <div className="flex items-center gap-2 mb-2">
        <span className={`text-xs font-medium px-2 py-0.5 rounded-full ${playlist.type === 'm3u' ? 'bg-blue-500/20 text-blue-400' : 'bg-green-500/20 text-green-400'}`}>
          {playlist.type === 'm3u' ? 'M3U' : 'Xtream'}
        </span>
        {playlist.updatedAt && (
          <span className="text-xs text-gray-400 bg-gray-900/50 px-2 py-0.5 rounded">
            Updated
          </span>
        )}
      </div>
      
      {playlist.type === 'm3u' ? (
        <div className="text-sm text-gray-300 truncate mb-4" title={playlist.url}>
          <ExternalLink size={12} className="inline mr-1" />
          {playlist.url.substring(0, 40)}...
        </div>
      ) : (
        <div className="text-sm text-gray-300 mb-4 space-y-1">
          <div className="truncate">
            <Server size={12} className="inline mr-1" />
            {playlist.serverUrl}
          </div>
          <div>
            <span className="text-gray-400">User:</span> {playlist.username}
          </div>
        </div>
      )}
      
      <div className="flex items-center justify-between pt-4 border-t border-gray-800">
        <span className="text-xs text-gray-400">
          {new Date(playlist.createdAt).toLocaleDateString()}
        </span>
        <button
          onClick={onActivate}
          className={`px-4 py-1.5 rounded-lg font-medium transition-all ${isActive ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-700 hover:bg-gray-600'}`}
        >
          {isActive ? '✅ Aktive' : 'Aktivizo'}
        </button>
      </div>
    </div>
  );
}

function SettingsContent({ 
  favorites, 
  setFavorites, 
  playlists, 
  setPlaylists,
  setActivePlaylistId,
  setChannels,
  setCurrentStream,
  setCurrentChannel 
}) {
  const clearFavorites = () => {
    if (confirm("Je i sigurtë që do t'i fshish të gjitha të preferuarat?")) {
      setFavorites([]);
      localStorage.removeItem("iptvFavorites");
    }
  };
  
  const clearPlaylists = () => {
    if (confirm("Do të fshish të gjitha listat. Je i sigurtë?")) {
      setPlaylists([]);
      setActivePlaylistId(null);
      setChannels([]);
      setCurrentStream(null);
      setCurrentChannel(null);
      localStorage.removeItem("iptvPlaylists");
      localStorage.removeItem("activePlaylistId");
    }
  };
  
  const clearAllData = () => {
    if (confirm("Kjo do të fshijë TË GJITHË të dhënat (listat, të preferuarat, historinë). Je i sigurtë?")) {
      localStorage.clear();
      window.location.reload();
    }
  };
  
  const exportData = () => {
    const data = {
      playlists,
      favorites,
      exportDate: new Date().toISOString(),
      version: "1.0.0"
    };
    
    const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `iptv-backup-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };
  
  const importData = () => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = '.json';
    
    input.onchange = (e) => {
      const file = e.target.files[0];
      const reader = new FileReader();
      
      reader.onload = (event) => {
        try {
          const data = JSON.parse(event.target.result);
          
          if (data.playlists && data.favorites) {
            if (confirm("Importimi do të mbivendosë të dhënat aktuale. Vazhdo?")) {
              setPlaylists(data.playlists);
              setFavorites(data.favorites);
              localStorage.setItem("iptvPlaylists", JSON.stringify(data.playlists));
              localStorage.setItem("iptvFavorites", JSON.stringify(data.favorites));
              alert("Të dhënat u importuan me sukses!");
            }
          } else {
            alert("Skedari nuk është valid!");
          }
        } catch (error) {
          alert("Gabim gjatë importimit: " + error.message);
        }
      };
      
      reader.readAsText(file);
    };
    
    input.click();
  };
  
  return (
    <div className="max-w-4xl">
      <h2 className="text-2xl font-bold mb-6">Cilësime & Menaxhim</h2>
      
      <div className="space-y-6">
        {/* Backup & Restore */}
        <div className="bg-gray-800/50 rounded-xl p-5">
          <h3 className="font-bold text-lg mb-4 flex items-center gap-2">
            <Folder size={20} />
            Backup & Restore
          </h3>
          <p className="text-gray-400 mb-4">Ruaj ose rikthe të dhënat tuaja</p>
          <div className="flex gap-3">
            <button
              onClick={exportData}
              className="px-4 py-2.5 bg-green-600 hover:bg-green-700 rounded-lg font-medium flex items-center gap-2"
            >
              <Download size={18} />
              Eksporto të Dhënat
            </button>
            <button
              onClick={importData}
              className="px-4 py-2.5 bg-blue-600 hover:bg-blue-700 rounded-lg font-medium flex items-center gap-2"
            >
              <Upload size={18} />
              Importo të Dhënat
            </button>
          </div>
        </div>
        
        {/* Data Management */}
        <div className="bg-gray-800/50 rounded-xl p-5">
          <h3 className="font-bold text-lg mb-4">Menaxhim i të Dhënave</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between p-3 bg-gray-900/30 rounded-lg">
              <div>
                <p className="font-medium">Të preferuarat</p>
                <p className="text-sm text-gray-400">{favorites.length} kanale të ruajtura</p>
              </div>
              <button
                onClick={clearFavorites}
                className="px-4 py-2 bg-red-500/20 text-red-400 hover:bg-red-500/30 rounded-lg font-medium"
                disabled={favorites.length === 0}
              >
                Fshi të gjitha
              </button>
            </div>
            
            <div className="flex items-center justify-between p-3 bg-gray-900/30 rounded-lg">
              <div>
                <p className="font-medium">Listat IPTV</p>
                <p className="text-sm text-gray-400">{playlists.length} lista të ruajtura</p>
              </div>
              <button
                onClick={clearPlaylists}
                className="px-4 py-2 bg-red-500/20 text-red-400 hover:bg-red-500/30 rounded-lg font-medium"
                disabled={playlists.length === 0}
              >
                Fshi të gjitha
              </button>
            </div>
          </div>
        </div>
        
        {/* About */}
        <div className="bg-gray-800/50 rounded-xl p-5">
          <h3 className="font-bold text-lg mb-4">Rreth aplikacionit</h3>
          <div className="space-y-3 text-gray-300">
            <p><strong className="text-blue-400">IPTV Player</strong> - Version 1.0.0</p>
            <p>Një aplikacion i plotë për shikimin e kanaleve IPTV</p>
            <p className="text-sm text-gray-400">Zhvilluar me React, Tailwind CSS dhe HLS.js</p>
            <div className="pt-3 border-t border-gray-800">
              <p className="text-sm">Funksionalitetet:</p>
              <ul className="text-sm text-gray-400 list-disc list-inside mt-1 ml-2">
                <li>Mbështetje për M3U dhe Xtream Codes</li>
                <li>Sistem i të preferuarave</li>
                <li>Kërkim dhe filtër kanalesh</li>
                <li>Player me kontroll të plotë</li>
                <li>Backup dhe restore të dhënash</li>
              </ul>
            </div>
          </div>
        </div>
        
        {/* Danger Zone */}
        <div className="bg-gray-800/50 rounded-xl p-5 border-2 border-red-500/20">
          <h3 className="font-bold text-lg mb-4 text-red-400">⛔ Zona e rrezikshme</h3>
          <p className="text-gray-300 mb-4">Këto veprime nuk mund të zhbëhen dhe do të fshijnë të gjitha të dhënat</p>
          <button
            onClick={clearAllData}
            className="px-4 py-2.5 bg-red-600 hover:bg-red-700 rounded-lg font-medium w-full"
          >
            Fshi të gjitha të dhënat dhe rifresko
          </button>
        </div>
      </div>
    </div>
  );
}

// ---------- Playlist Modal Component ----------
function PlaylistModal({ currentPlaylist, setCurrentPlaylist, modalMode, savePlaylist, closeModal }) {
  const [isTesting, setIsTesting] = useState(false);
  const [testResult, setTestResult] = useState(null);

  const testConnection = async () => {
    if (currentPlaylist.type === "m3u" && !currentPlaylist.url.trim()) {
      setTestResult({ success: false, message: "Vendos URL-në fillimisht" });
      return;
    }
    
    if (currentPlaylist.type === "xtream" && 
        (!currentPlaylist.serverUrl || !currentPlaylist.username || !currentPlaylist.password)) {
      setTestResult({ success: false, message: "Plotëso të gjitha fushat" });
      return;
    }
    
    setIsTesting(true);
    setTestResult(null);
    
    try {
      let success = false;
      let message = "";
      
      if (currentPlaylist.type === "m3u") {
        success = await testPlaylistConnection(currentPlaylist.url);
        message = success ? "Lidhja u krye me sukses!" : "Nuk mund të lidhem me URL-në";
      } else {
        success = await testXtreamConnection(
          currentPlaylist.serverUrl,
          currentPlaylist.username,
          currentPlaylist.password
        );
        message = success ? "Kredencialet janë valide!" : "Kredencialet nuk janë valide";
      }
      
      setTestResult({ success, message });
    } catch (error) {
      setTestResult({ success: false, message: "Gabim gjatë testimit: " + error.message });
    } finally {
      setIsTesting(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50 p-4">
      <div className="bg-gradient-to-b from-gray-900 to-gray-950 p-6 rounded-xl w-full max-w-md border border-gray-800 shadow-2xl">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold">
            {modalMode === "add" ? "Shto Listë të Re" : "Edito Listë"}
          </h2>
          <button
            onClick={closeModal}
            className="p-1.5 hover:bg-gray-800 rounded-lg transition"
          >
            <X size={20} />
          </button>
        </div>
        
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Emri i Listës *</label>
            <input
              type="text"
              placeholder="P.sh. Lista ime IPTV"
              className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-blue-500 focus:outline-none focus:ring-2 focus:ring-blue-500/50"
              value={currentPlaylist.name}
              onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, name: e.target.value })}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium mb-2">Lloji i Listës</label>
            <div className="flex gap-3">
              <button
                type="button"
                onClick={() => setCurrentPlaylist({ ...currentPlaylist, type: "m3u" })}
                className={`flex-1 p-3 rounded-lg border transition-all ${currentPlaylist.type === "m3u" ? 'border-blue-500 bg-blue-500/10 ring-2 ring-blue-500/20' : 'border-gray-700 bg-gray-800 hover:border-gray-600'}`}
              >
                <div className="flex items-center gap-2 justify-center">
                  <Globe size={18} />
                  <span>M3U URL</span>
                </div>
              </button>
              <button
                type="button"
                onClick={() => setCurrentPlaylist({ ...currentPlaylist, type: "xtream" })}
                className={`flex-1 p-3 rounded-lg border transition-all ${currentPlaylist.type === "xtream" ? 'border-green-500 bg-green-500/10 ring-2 ring-green-500/20' : 'border-gray-700 bg-gray-800 hover:border-gray-600'}`}
              >
                <div className="flex items-center gap-2 justify-center">
                  <Server size={18} />
                  <span>Xtream</span>
                </div>
              </button>
            </div>
          </div>
          
          {currentPlaylist.type === "m3u" ? (
            <div>
              <div className="flex items-center justify-between mb-2">
                <label className="block text-sm font-medium">URL M3U *</label>
                <button
                  type="button"
                  onClick={testConnection}
                  disabled={isTesting}
                  className="text-xs px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded-lg"
                >
                  {isTesting ? "Duke testuar..." : "Testo Lidhjen"}
                </button>
              </div>
              <input
                type="text"
                placeholder="https://example.com/playlist.m3u"
                className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-blue-500 focus:outline-none focus:ring-2 focus:ring-blue-500/50"
                value={currentPlaylist.url}
                onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, url: e.target.value })}
              />
            </div>
          ) : (
            <>
              <div>
                <div className="flex items-center justify-between mb-2">
                  <label className="block text-sm font-medium">URL Serveri *</label>
                  <button
                    type="button"
                    onClick={testConnection}
                    disabled={isTesting}
                    className="text-xs px-3 py-1 bg-gray-700 hover:bg-gray-600 rounded-lg"
                  >
                    {isTesting ? "Duke testuar..." : "Testo Lidhjen"}
                  </button>
                </div>
                <input
                  type="text"
                  placeholder="http://server.example.com:8080"
                  className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none focus:ring-2 focus:ring-green-500/50"
                  value={currentPlaylist.serverUrl}
                  onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, serverUrl: e.target.value })}
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Username *</label>
                  <input
                    type="text"
                    placeholder="përdoruesi"
                    className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none"
                    value={currentPlaylist.username}
                    onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, username: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Password *</label>
                  <input
                    type="password"
                    placeholder="••••••••"
                    className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none"
                    value={currentPlaylist.password}
                    onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, password: e.target.value })}
                  />
                </div>
              </div>
            </>
          )}
          
          {/* Test Result */}
          {testResult && (
            <div className={`p-3 rounded-lg ${testResult.success ? 'bg-green-500/20 border border-green-500/30' : 'bg-red-500/20 border border-red-500/30'}`}>
              <div className="flex items-center gap-2">
                {testResult.success ? <Check size={16} className="text-green-500" /> : <AlertCircle size={16} className="text-red-500" />}
                <span className={testResult.success ? 'text-green-400' : 'text-red-400'}>
                  {testResult.message}
                </span>
              </div>
            </div>
          )}
        </div>
        
        <div className="flex justify-end gap-3 mt-8 pt-6 border-t border-gray-800">
          <button
            onClick={closeModal}
            className="px-5 py-2.5 bg-gray-800 hover:bg-gray-700 rounded-lg font-medium transition-colors"
          >
            Anulo
          </button>
          <button
            onClick={savePlaylist}
            className={`px-5 py-2.5 rounded-lg font-medium transition-colors ${modalMode === "add" ? 'bg-blue-600 hover:bg-blue-700' : 'bg-green-600 hover:bg-green-700'}`}
          >
            {modalMode === "add" ? "Shto Listë" : "Ruaj Ndryshimet"}
          </button>
        </div>
      </div>
    </div>
  );
}

// ---------- Missing Icons ----------
function Download({ size = 24 }) {
  return (
    <svg xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
      <polyline points="7 10 12 15 17 10"/>
      <line x1="12" y1="15" x2="12" y2="3"/>
    </svg>
  );
}

function Upload({ size = 24 }) {
  return (
    <svg xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
      <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
      <polyline points="17 8 12 3 7 8"/>
      <line x1="12" y1="3" x2="12" y2="15"/>
    </svg>
  );
}
