import React, { useState } from 'react';
import './M3UUploader.css';

const M3UUploader = ({ onLoad, onClose, isLoading, theme = 'dark' }) => {
  const [url, setUrl] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!url) {
      setError('Shkruani URL-nÃ«');
      return;
    }
    onLoad({ type: 'url', url });
  };

  return (
    <div className={`xtream-modal-overlay theme-${theme}`}>
      <div className="xtream-modal">
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">ðŸ“‹</span>
            <h2>M3U Playlist</h2>
          </div>
          <button className="xtream-close-btn" onClick={onClose}>Ã—</button>
        </div>
        <div className="xtream-modal-body">
          <form onSubmit={handleSubmit}>
            <div className="xtream-form-group">
              <label>M3U URL</label>
              <input
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="https://example.com/playlist.m3u"
                disabled={isLoading}
              />
              {error && <div className="error">{error}</div>}
            </div>
            <div className="xtream-form-actions">
              <button type="button" onClick={onClose} disabled={isLoading}>
                Anulo
              </button>
              <button type="submit" disabled={!url || isLoading}>
                {isLoading ? 'Duke ngarkuar...' : 'Ngarko'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

