// Në M3UUploader.js, ndrysho handleSubmit:

const handleSubmit = async (e) => {
  e.preventDefault();
  setError('');

  if (activeTab === 'file') {
    if (!file) {
      setError('Zgjidhni një skedar M3U');
      return;
    }

    try {
      const content = await file.text();
      
      // Verifiko që është M3U valid
      if (!content.includes('#EXTM3U')) {
        throw new Error('Skedari nuk është M3U playlist valid');
      }
      
      onLoad({
        type: 'file',
        content,
        channels: null
      });
    } catch (err) {
      setError('Nuk mund të lexohet skedari: ' + err.message);
    }
  } else {
    if (!url) {
      setError('Shkruani URL-në e M3U');
      return;
    }

    try {
      // Validimi i URL
      let urlToFetch = url.trim();
      if (!urlToFetch.startsWith('http://') && !urlToFetch.startsWith('https://')) {
        urlToFetch = 'https://' + urlToFetch;
      }
      
      // Testo URL
      new URL(urlToFetch);
      
      onLoad({
        type: 'url',
        url: urlToFetch,
        channels: null
      });
    } catch (err) {
      setError('URL jo e vlefshme');
    }
  }
};
