import { useState, useMemo, useCallback } from 'react';

export const useIPTVData = () => {
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState({
    live: [],
    movies: [],
    series: [],
    all: []
  });

  const safeChannels = useMemo(() => {
    if (!Array.isArray(channels)) return [];
    return channels.filter(c => c && typeof c === 'object');
  }, [channels]);

  const safeMovies = useMemo(() => {
    if (!Array.isArray(movies)) return [];
    return movies.filter(m => m && typeof m === 'object');
  }, [movies]);

  const safeSeries = useMemo(() => {
    if (!Array.isArray(series)) return [];
    return series.filter(s => s && typeof s === 'object');
  }, [series]);

  const clearData = useCallback(() => {
    setChannels([]);
    setMovies([]);
    setSeries([]);
    setCategories({ live: [], movies: [], series: [], all: [] });
  }, []);

  return {
    channels, setChannels,
    movies, setMovies,
    series, setSeries,
    categories, setCategories,
    safeChannels, safeMovies, safeSeries,
    clearData
  };
};
