// components/XtreamLogin.jsx
import React, { useState, useCallback, useEffect } from 'react';

function XtreamLogin({ onLogin, onClose, isLoading, theme = 'dark' }) {
  const [credentials, setCredentials] = useState({
    server: '',
    username: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [activeField, setActiveField] = useState(null);

  const validateForm = useCallback(() => {
    const newErrors = {};
    
    if (!credentials.server.trim()) {
      newErrors.server = 'Server URL √´sht√´ i detyruesh√´m';
    } else {
      // Validim i thjesht√´ i URL-s√´
      const urlPattern = /^(https?:\/\/)?([\da-z\.-]+)\.([a-z\.]{2,6})(:[0-9]{1,5})?([\/\w \.-]*)*\/?$/i;
      if (!urlPattern.test(credentials.server.trim())) {
        newErrors.server = 'URL nuk √´sht√´ e vlefshme';
      }
    }
    
    if (!credentials.username.trim()) {
      newErrors.username = 'Username √´sht√´ i detyruesh√´m';
    } else if (credentials.username.length < 3) {
      newErrors.username = 'Username duhet t√´ ket√´ t√´ pakt√´n 3 karaktere';
    }
    
    if (!credentials.password.trim()) {
      newErrors.password = 'Password √´sht√´ i detyruesh√´m';
    } else if (credentials.password.length < 4) {
      newErrors.password = 'Password duhet t√´ ket√´ t√´ pakt√´n 4 karaktere';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  }, [credentials]);

  const handleSubmit = useCallback((e) => {
    e.preventDefault();
    if (validateForm()) {
      onLogin(credentials);
    }
  }, [credentials, onLogin, validateForm]);

  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
    // Fshij error-in kur fillon t√´ shkruash
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  }, [errors]);

  const handleClose = useCallback(() => {
    onClose();
  }, [onClose]);

  const togglePassword = useCallback(() => {
    setShowPassword(prev => !prev);
  }, []);

  // Reset errors when modal opens
  useEffect(() => {
    setErrors({});
    setCredentials({ server: '', username: '', password: '' });
  }, []);

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="xtream-modal" onClick={(e) => e.stopPropagation()}>
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üì°</span>
            <h2>Xtream Codes Login</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={handleClose}
            disabled={isLoading}
            aria-label="Mbyll"
          >
            √ó
          </button>
        </div>

        <div className="xtream-modal-body">
          <div className="xtream-info-box">
            <span className="info-icon">‚ÑπÔ∏è</span>
            <div className="info-text">
              <strong>Si t√´ lidheni?</strong>
              <p>Futni URL-n√´ e serverit, username dhe password nga Xtream Codes.</p>
              <span className="info-example">Shembull: http://example.com:8080</span>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="xtream-form">
            <div className={`xtream-form-group ${activeField === 'server' ? 'focused' : ''} ${errors.server ? 'error' : ''}`}>
              <label htmlFor="server">
                <span className="field-icon">üåê</span>
                Server URL
              </label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="server"
                  name="server"
                  placeholder="http://example.com:8080"
                  value={credentials.server}
                  onChange={handleChange}
                  onFocus={() => setActiveField('server')}
                  onBlur={() => setActiveField(null)}
                  className={errors.server ? 'error' : ''}
                  disabled={isLoading}
                  autoComplete="off"
                  autoFocus
                />
                {credentials.server && !errors.server && (
                  <span className="input-valid">‚úì</span>
                )}
              </div>
              {errors.server && (
                <span className="error-message">
                  <span className="error-icon">‚ö†Ô∏è</span>
                  {errors.server}
                </span>
              )}
            </div>

            <div className={`xtream-form-group ${activeField === 'username' ? 'focused' : ''} ${errors.username ? 'error' : ''}`}>
              <label htmlFor="username">
                <span className="field-icon">üë§</span>
                Username
              </label>
              <div className="input-wrapper">
                <input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="Username"
                  value={credentials.username}
                  onChange={handleChange}
                  onFocus={() => setActiveField('username')}
                  onBlur={() => setActiveField(null)}
                  className={errors.username ? 'error' : ''}
                  disabled={isLoading}
                  autoComplete="off"
                />
                {credentials.username && !errors.username && (
                  <span className="input-valid">‚úì</span>
                )}
              </div>
              {errors.username && (
                <span className="error-message">
                  <span className="error-icon">‚ö†Ô∏è</span>
                  {errors.username}
                </span>
              )}
            </div>

            <div className={`xtream-form-group ${activeField === 'password' ? 'focused' : ''} ${errors.password ? 'error' : ''}`}>
              <label htmlFor="password">
                <span className="field-icon">üîí</span>
                Password
              </label>
              <div className="input-wrapper">
                <input
                  type={showPassword ? 'text' : 'password'}
                  id="password"
                  name="password"
                  placeholder="Password"
                  value={credentials.password}
                  onChange={handleChange}
                  onFocus={() => setActiveField('password')}
                  onBlur={() => setActiveField(null)}
                  className={errors.password ? 'error' : ''}
                  disabled={isLoading}
                  autoComplete="off"
                />
                <button 
                  type="button"
                  className="password-toggle"
                  onClick={togglePassword}
                  tabIndex="-1"
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
                {credentials.password && !errors.password && (
                  <span className="input-valid">‚úì</span>
                )}
              </div>
              {errors.password && (
                <span className="error-message">
                  <span className="error-icon">‚ö†Ô∏è</span>
                  {errors.password}
                </span>
              )}
            </div>

            <div className="xtream-form-actions">
              <button 
                type="submit" 
                className="xtream-btn xtream-btn-primary"
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke u lidhur...
                  </>
                ) : (
                  <>
                    <span className="btn-icon">üîå</span>
                    Lidhu
                  </>
                )}
              </button>
              <button 
                type="button" 
                className="xtream-btn xtream-btn-secondary"
                onClick={handleClose}
