import React, { useState, useEffect } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import SearchModal from "./components/SearchModal";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";

function App() {
  /* =====================
     DATA STATE (SAFE)
  ===================== */
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);

  /* =====================
     PLAYER
  ===================== */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  /* =====================
     UI
  ===================== */
  const [activeTab, setActiveTab] = useState("live");
  const [showSearchModal, setShowSearchModal] = useState(false);

  /* =====================
     SEARCH (KRITIKE ‚Äì SAFE)
  ===================== */
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  /* =====================
     SEARCH LOGIC
  ===================== */
  useEffect(() => {
    if (!searchQuery || searchQuery.trim() === "") {
      setSearchResults([]);
      return;
    }

    const q = searchQuery.toLowerCase();
    const results = [];

    (channels || []).forEach((c) => {
      if (c?.name?.toLowerCase().includes(q)) {
        results.push({ ...c, type: "Live" });
      }
    });

    (movies || []).forEach((m) => {
      if (m?.name?.toLowerCase().includes(q)) {
        results.push({ ...m, type: "Movie" });
      }
    });

    (series || []).forEach((s) => {
      if (s?.name?.toLowerCase().includes(q)) {
        results.push({ ...s, type: "Series" });
      }
    });

    setSearchResults(results);
  }, [searchQuery, channels, movies, series]);

  /* =====================
     DUMMY DATA (TEST)
     (hiqe kur lidh Xtream/M3U)
  ===================== */
  useEffect(() => {
    setChannels([
      {
        stream_id: 1,
        name: "Test Channel",
        stream_url: "https://test-streams.mux.dev/x36xhzz/x36xhzz.m3u8",
        logo: ""
      }
    ]);
  }, []);

  /* =====================
     RENDER
  ===================== */
  return (
    <ErrorBoundary>
      <SearchModal
        isOpen={showSearchModal}
        query={searchQuery}
        setQuery={setSearchQuery}
        results={Array.isArray(searchResults) ? searchResults : []}
        onClose={() => setShowSearchModal(false)}
        onSelectStream={(item) => {
          setCurrentStream(item.stream_url);
          setIsPlaying(true);
          setShowSearchModal(false);
        }}
      />

      <div className="app">
        <header style={{ padding: 10 }}>
          <button onClick={() => setShowSearchModal(true)}>üîç K√´rko</button>
          <button onClick={() => setActiveTab("live")}>Live</button>
          <button onClick={() => setActiveTab("movies")}>Movies</button>
          <button onClick={() => setActiveTab("series")}>Series</button>
        </header>

        {activeTab === "live" && (
          <LiveTVContent
            channels={Array.isArray(channels) ? channels : []}
            onPlay={(c) => {
              setCurrentStream(c.stream_url);
              setIsPlaying(true);
            }}
          />
        )}

        {activeTab === "movies" && (
          <MoviesContent movies={Array.isArray(movies) ? movies : []} />
        )}

        {activeTab === "series" && (
          <SeriesContent series={Array.isArray(series) ? series : []} />
        )}

        {currentStream && (
          <HlsPlayer
            src={currentStream}
            isPlaying={isPlaying}
            onPlayPause={() => setIsPlaying(!isPlaying)}
          />
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
