// src/components/ChannelCard.jsx
import React, { memo } from 'react';
import { getItemName, getItemLogo, getItemId } from '../utils/streamHelpers';
import './ChannelCard.css';

const ChannelCard = memo(({
  item,
  onPlay,
  onToggleFavorite,
  isFavorite,
  isSelected,
  theme,
  activeTab
}) => {
  const name = getItemName(item);
  const logo = getItemLogo(item);
  const id = getItemId(item);
  
  const handlePlay = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onPlay(item);
  };

  const handleFavoriteClick = (e) => {
    e.preventDefault();
    e.stopPropagation();
    onToggleFavorite();
  };

  return (
    <div 
      className={`channel-card theme-${theme} ${isSelected ? 'selected' : ''}`}
      onClick={handlePlay}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => e.key === 'Enter' && handlePlay(e)}
    >
      <div className="channel-card-logo-container">
        {logo ? (
          <img 
            src={logo} 
            alt={name}
            className="channel-card-logo"
            loading="lazy"
            onError={(e) => {
              e.target.onerror = null;
              e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&size=64&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`;
            }}
          />
        ) : (
          <div className="channel-card-logo-placeholder">
            <span className="placeholder-icon">
              {activeTab === 'live' ? 'üì∫' : activeTab === 'movies' ? 'üé¨' : 'üì∫'}
            </span>
          </div>
        )}
      </div>

      <div className="channel-card-info">
        <h3 className="channel-card-name" title={name}>
          {name}
        </h3>
        
        <div className="channel-card-meta">
          {item.rating && (
            <span className="channel-card-rating">
              ‚≠ê {item.rating}
            </span>
          )}
          {item.year && (
            <span className="channel-card-year">
              {item.year}
            </span>
          )}
        </div>
      </div>

      <div className="channel-card-actions">
        <button
          className={`favorite-btn ${isFavorite ? 'active' : ''}`}
          onClick={handleFavoriteClick}
          aria-label={isFavorite ? 'Hiq nga favoritet' : 'Shto n√´ favoritet'}
          title={isFavorite ? 'Hiq nga favoritet' : 'Shto n√´ favoritet'}
        >
          {isFavorite ? '‚ù§Ô∏è' : 'ü§ç'}
        </button>
        
        <button
          className="play-btn"
          onClick={handlePlay}
          aria-label="Luaj"
          title="Luaj"
        >
          ‚ñ∂
        </button>
      </div>

      {item.type === 'live' && (
        <div className="live-indicator">
          <span className="live-dot"></span>
          LIVE
        </div>
      )}
    </div>
  );
});

export default ChannelCard;
