import React, { useRef, useEffect, useState } from 'react';
import Hls from 'hls.js';

const IPTVPlayer = ({ streamUrl, isPlaying, onPlay, onPause, className = '' }) => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (!streamUrl) return;

    const initializePlayer = () => {
      // Clean up previous instance
      if (hlsRef.current) {
        hlsRef.current.destroy();
        hlsRef.current = null;
      }

      const video = videoRef.current;
      if (!video) return;

      setIsLoading(true);
      setError(null);

      // Check if HLS is supported
      if (Hls.isSupported()) {
        const hls = new Hls({
          enableWorker: true,
          lowLatencyMode: true,
          backBufferLength: 90,
          maxBufferLength: 30,
          maxBufferSize: 60 * 1000 * 1000, // 60MB
          maxMaxBufferLength: 600,
          liveSyncDurationCount: 3,
          liveMaxLatencyDurationCount: 10,
        });

        hlsRef.current = hls;

        hls.loadSource(streamUrl);
        hls.attachMedia(video);

        hls.on(Hls.Events.MANIFEST_PARSED, () => {
          setIsLoading(false);
          if (isPlaying) {
            video.play().catch(e => {
              console.error('Play error:', e);
              setError('Nuk mund të startohet video. Provo të rifreskosh faqen.');
            });
          }
        });

        hls.on(Hls.Events.ERROR, (event, data) => {
          console.error('HLS error:', data);
          if (data.fatal) {
            switch (data.type) {
              case Hls.ErrorTypes.NETWORK_ERROR:
                setError('Gabim në rrjet. Provo të rifreskosh.');
                hls.startLoad();
                break;
              case Hls.ErrorTypes.MEDIA_ERROR:
                setError('Gabim me media. Duke u riprovuar...');
                hls.recoverMediaError();
                break;
              default:
                setError('Gabim i panjohur. Provo kanalin tjetër.');
                hls.destroy();
                break;
            }
          }
        });
      } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
        // Safari native HLS support
        video.src = streamUrl;
        video.addEventListener('loadedmetadata', () => {
          setIsLoading(false);
          if (isPlaying) {
            video.play().catch(e => {
              console.error('Play error:', e);
              setError('Nuk mund të startohet video.');
            });
          }
        });
        
        video.addEventListener('error', () => {
          setError('Gabim gjatë ngarkimit të videos.');
          setIsLoading(false);
        });
      } else {
        setError('Shfletuesi yt nuk mbështet HLS streaming.');
        setIsLoading(false);
      }
    };

    initializePlayer();

    return () => {
      if (hlsRef.current) {
        hlsRef.current.destroy();
        hlsRef.current = null;
      }
    };
  }, [streamUrl]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isPlaying) {
      video.play().catch(e => {
        console.error('Play error:', e);
        onPause?.();
      });
    } else {
      video.pause();
    }
  }, [isPlaying, onPause]);

  const handlePlay = () => {
    onPlay?.();
  };

  const handlePause = () => {
    onPause?.();
  };

  const handleRetry = () => {
    setError(null);
    setIsLoading(true);
    window.location.reload();
  };

  return (
    <div className={`relative w-full h-full bg-black rounded-lg overflow-hidden ${className}`}>
      {isLoading && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/80 z-10">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto mb-4"></div>
            <p className="text-white">Duke ngarkuar stream...</p>
          </div>
        </div>
      )}

      {error && (
        <div className="absolute inset-0 flex items-center justify-center bg-black/90 z-20">
          <div className="text-center p-6">
            <div className="text-red-500 text-4xl mb-4">⚠️</div>
            <p className="text-white text-lg mb-2">Gabim gjatë shfaqjes</p>
            <p className="text-gray-300 mb-4">{error}</p>
            <button
              onClick={handleRetry}
              className="px-6 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg font-medium"
            >
              Provo Përsëri
            </button>
          </div>
        </div>
      )}

      <video
        ref={videoRef}
        className="w-full h-full object-contain"
        controls={false}
        playsInline
        preload="auto"
        onPlay={handlePlay}
        onPause={handlePause}
        onError={(e) => {
          console.error('Video error:', e);
          setError('Video error occurred');
          setIsLoading(false);
        }}
        onLoadedData={() => setIsLoading(false)}
      />

      {!error && !isLoading && (
        <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-black/80 to-transparent p-4 opacity-0 hover:opacity-100 transition-opacity">
          <div className="flex items-center justify-between">
            <div className="text-white text-sm">
              IPTV Stream
            </div>
            <div className="text-white text-sm">
              {isPlaying ? 'Duke luajtur' : 'Pauzuar'}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default IPTVPlayer;
