import React, { useState, useCallback } from 'react';
import './M3UUploader.css';

const M3UUploader = ({ onLoad, onClose, isLoading, theme = 'dark' }) => {
  const [activeTab, setActiveTab] = useState('file');
  const [file, setFile] = useState(null);
  const [url, setUrl] = useState('');
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.name.endsWith('.m3u') || selectedFile.name.endsWith('.m3u8')) {
        setFile(selectedFile);
        setError('');
      } else {
        setError('Zgjidhni nj√´ skedar .m3u ose .m3u8');
        setFile(null);
      }
    }
  };

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      if (droppedFile.name.endsWith('.m3u') || droppedFile.name.endsWith('.m3u8')) {
        setFile(droppedFile);
        setError('');
      } else {
        setError('Zgjidhni nj√´ skedar .m3u ose .m3u8');
        setFile(null);
      }
    }
  }, []);

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (activeTab === 'file') {
      if (!file) {
        setError('Zgjidhni nj√´ skedar M3U');
        return;
      }

      try {
        const content = await file.text();
        onLoad({
          type: 'file',
          content,
          channels: null
        });
      } catch (err) {
        setError('Nuk mund t√´ lexohet skedari');
      }
    } else {
      if (!url) {
        setError('Shkruani URL-n√´ e M3U');
        return;
      }

      try {
        new URL(url);
        onLoad({
          type: 'url',
          url,
          channels: null
        });
      } catch (err) {
        setError('URL jo e vlefshme');
      }
    }
  };

  const removeFile = () => {
    setFile(null);
  };

  return (
    <div className={`xtream-modal-overlay theme-${theme}`}>
      <div className="xtream-modal">
        <div className="xtream-modal-header">
          <div className="xtream-modal-title">
            <span className="xtream-icon">üìã</span>
            <h2>M3U Playlist</h2>
          </div>
          <button 
            className="xtream-close-btn" 
            onClick={onClose}
            disabled={isLoading}
          >
            √ó
          </button>
        </div>

        <div className="xtream-modal-body">
          {/* Tabs */}
          <div className="m3u-source-tabs">
            <button
              className={`m3u-source-tab ${activeTab === 'file' ? 'active' : ''}`}
              onClick={() => setActiveTab('file')}
              type="button"
              disabled={isLoading}
            >
              <span className="tab-icon">üìÅ</span>
              <span>Nga skedari</span>
            </button>
            <button
              className={`m3u-source-tab ${activeTab === 'url' ? 'active' : ''}`}
              onClick={() => setActiveTab('url')}
              type="button"
              disabled={isLoading}
            >
              <span className="tab-icon">üåê</span>
              <span>Nga URL</span>
            </button>
          </div>

          <form onSubmit={handleSubmit}>
            {activeTab === 'file' ? (
              /* File Upload */
              <div className="xtream-form-group">
                <div 
                  className={`m3u-dropzone ${file ? 'has-file' : ''} ${error ? 'error' : ''}`}
                  onDrop={handleDrop}
                  onDragOver={handleDragOver}
                  onClick={() => !file && document.getElementById('m3u-file-input')?.click()}
                >
                  <input
                    id="m3u-file-input"
                    type="file"
                    accept=".m3u,.m3u8"
                    onChange={handleFileChange}
                    disabled={isLoading}
                    style={{ display: 'none' }}
                  />
                  
                  {file ? (
                    <>
                      <div className="dropzone-icon">‚úÖ</div>
                      <div className="dropzone-text">
                        <strong>{file.name}</strong>
                        <span>{(file.size / 1024).toFixed(1)} KB</span>
                      </div>
                      <button
                        type="button"
                        className="dropzone-remove"
                        onClick={(e) => {
                          e.stopPropagation();
                          removeFile();
                        }}
                      >
                        √ó
                      </button>
                    </>
                  ) : (
                    <>
                      <div className="dropzone-icon">üìÇ</div>
                      <div className="dropzone-text">
                        <strong>Zgjidhni ose hidhni skedarin</strong>
                        <span>.m3u ose .m3u8</span>
                      </div>
                    </>
                  )}
                </div>
                {error && <div className="xtream-input-error">{error}</div>}
              </div>
            ) : (
              /* URL Input */
              <div className="xtream-form-group">
                <label htmlFor="m3u-url">
                  <span className="label-icon">üîó</span>
                  M3U URL
                </label>
                <input
                  id="m3u-url"
                  type="url"
                  className="xtream-form-input"
                  placeholder="https://example.com/playlist.m3u"
                  value={url}
                  onChange={(e) => setUrl(e.target.value)}
                  disabled={isLoading}
                />
                <div className="xtream-input-hint">
                  URL e playlist-it M3U
                </div>
                {error && <div className="xtream-input-error">{error}</div>}
              </div>
            )}

            {/* Form Actions */}
            <div className="xtream-form-actions">
              <button
                type="button"
                className="xtream-btn xtream-btn-secondary"
                onClick={onClose}
                disabled={isLoading}
              >
                Anulo
              </button>
              <button
                type="submit"
                className="xtream-btn xtream-btn-primary"
                disabled={
                  isLoading || 
                  (activeTab === 'file' ? !file : !url)
                }
              >
                {isLoading ? (
                  <>
                    <span className="btn-spinner"></span>
                    Duke ngarkuar...
                  </>
                ) : (
                  'Ngarko'
                )}
              </button>
            </div>
          </form>

          {/* Help Text - FIXED */}
          <div className="xtream-help-text">
            <span className="help-badge">üì∫</span>
            <span>M3U playlist me kanale live TV</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default M3UUploader;
