// src/components/ChannelList.jsx
import React, { useMemo } from 'react';
import { getItemName, getItemLogo, getStreamType, getItemCategory } from '../utils/streamHelpers';
import { STREAM_TYPES } from '../constants/iptv';
import './ChannelList.css';

const ChannelList = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem, 
  theme,
  activeTab 
}) => {
  
  // Grupimi i kanaleve sipas kategorive
 // NÃ« ChannelList.jsx, shto log pÃ«r tÃ« parÃ« kategoritÃ«

const groupedItems = useMemo(() => {
  if (!items || !items.length) {
    console.log('ðŸ“ Nuk ka items pÃ«r tÃ« grupuar');
    return {};
  }
  
  console.log(`ðŸ“ Duke grupuar ${items.length} items...`);
  
  const groups = items.reduce((groups, item) => {
    const category = getItemCategory(item, activeTab);
    if (!groups[category]) {
      groups[category] = [];
    }
    groups[category].push(item);
    return groups;
  }, {});
  
  console.log('ðŸ“ Grupet e formuara:', Object.keys(groups));
  return groups;
}, [items, activeTab]);

  const handlePlay = (item) => {
    onPlay(item);
  };

  const handleKeyDown = (e, item) => {
    if (e.key === 'Enter' || e.key === ' ') {
      e.preventDefault();
      handlePlay(item);
    }
  };

  // NÃ«se nuk ka items, shfaq mesazh
  if (!items || items.length === 0) {
    return (
      <div className={`channel-list-empty theme-${theme}`}>
        <div className="empty-icon">ðŸ“º</div>
        <h3>Nuk ka kanale</h3>
        <p>Nuk u gjet asnjÃ« kanal nÃ« kÃ«tÃ« kategori</p>
      </div>
    );
  }

  // Shfaq kanalet e grupuara sipas kategorive
  return (
    <div className={`channel-list theme-${theme}`}>
      {Object.entries(groupedItems).map(([category, categoryItems]) => (
        <div key={category} className="channel-category-section">
          <div className="channel-category-header">
            <h3 className="channel-category-title">
              <span className="category-icon">
                {activeTab === 'live' ? 'ðŸ“º' : activeTab === 'movies' ? 'ðŸŽ¬' : 'ðŸ“º'}
              </span>
              {category}
              <span className="category-count">{categoryItems.length}</span>
            </h3>
          </div>
          
          <div className="channel-grid">
            {categoryItems.map((item) => {
              const itemId = item.stream_id || item.id || item.series_id;
              const itemName = getItemName(item);
              const itemLogo = getItemLogo(item);
              const streamType = getStreamType(item, activeTab);
              const isFavorite = favorites.includes(itemId);
              const isSelected = selectedItem?.stream_id === itemId || selectedItem?.id === itemId;
              
              return (
                <div
                  key={itemId || Math.random()}
                  className={`channel-card ${isSelected ? 'selected' : ''}`}
                  onClick={() => handlePlay(item)}
                  onKeyDown={(e) => handleKeyDown(e, item)}
                  role="button"
                  tabIndex={0}
                  aria-label={`Luaj ${itemName}`}
                >
                  <div className="channel-card-image">
                    <img
                      src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`}
                      alt={itemName}
                      loading="lazy"
                      onError={(e) => {
                        e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`;
                      }}
                    />
                    <div className="channel-card-badge">
                      {streamType === STREAM_TYPES.LIVE ? 'LIVE' : 
                       streamType === STREAM_TYPES.MOVIE ? 'FILM' : 
                       streamType === STREAM_TYPES.SERIES ? 'SERIAL' : 'HD'}
                    </div>
                  </div>
                  
                  <div className="channel-card-content">
                    <h4 className="channel-card-title">{itemName}</h4>
                    <div className="channel-card-footer">
                      <span className="channel-card-category">
                        {category}
                      </span>
                      <button
                        className={`channel-card-favorite ${isFavorite ? 'active' : ''}`}
                        onClick={(e) => {
                          e.stopPropagation();
                          onToggleFavorite(itemId);
                        }}
                        aria-label={isFavorite ? 'Hiq nga favoritet' : 'Shto nÃ« favoritet'}
                      >
                        {isFavorite ? 'â˜…' : 'â˜†'}
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ChannelList;
