import React, { useState, useEffect, useMemo, useCallback } from "react";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import XtreamLogin from "./components/XtreamLogin";
import M3UUploader from "./components/M3UUploader";
import SearchModal from "./components/SearchModal";
import EPGInfo from "./components/EPGInfo";
import { 
  xtreamLogin, 
  getAllContent, 
  getAllCategories, 
  buildStreamUrl,
  getLiveCategories,
  getMovieCategories,
  getSeriesCategories,
  getEPG
} from './services/xtream';
import "./App.css";

function App() {
  /* =====================
     DATA STATE - LIVE, MOVIES, SERIES
  ===================== */
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState({
    live: [],
    movies: [],
    series: [],
    all: []
  });
  const [isLoading, setIsLoading] = useState({
    channels: false,
    movies: false,
    series: false,
    general: false
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  /* =====================
     XTREAM CREDENTIALS
  ===================== */
  const [xtreamCredentials, setXtreamCredentials] = useState({
    server: "",
    username: "",
    password: "",
    isLoggedIn: false,
    expires: null,
    activeConnections: null,
    maxConnections: null,
    auth: null
  });

  /* =====================
     M3U STATE
  ===================== */
  const [m3uUrl, setM3uUrl] = useState("");
  const [m3uActive, setM3uActive] = useState(false);

  /* =====================
     PLAYER STATE
  ===================== */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playerError, setPlayerError] = useState(null);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);
  const [epgData, setEpgData] = useState([]);
  const [currentEpg, setCurrentEpg] = useState(null);

  /* =====================
     UI STATE - TABS
  ===================== */
  const [activeTab, setActiveTab] = useState("live");
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showXtreamLogin, setShowXtreamLogin] = useState(false);
  const [showM3UModal, setShowM3UModal] = useState(true);
  const [sourceType, setSourceType] = useState(null);
  
  /* =====================
     SIDEBAR STATE
  ===================== */
  const [selectedCategory, setSelectedCategory] = useState("TÃ« gjitha");
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState([]);
  const [recentItems, setRecentItems] = useState([]);

  /* =====================
     SAFE DATA
  ===================== */
  const safeChannels = useMemo(() => {
    if (!Array.isArray(channels)) return [];
    return channels.filter(c => c && typeof c === 'object');
  }, [channels]);

  const safeMovies = useMemo(() => {
    if (!Array.isArray(movies)) return [];
    return movies.filter(m => m && typeof m === 'object');
  }, [movies]);

  const safeSeries = useMemo(() => {
    if (!Array.isArray(series)) return [];
    return series.filter(s => s && typeof s === 'object');
  }, [series]);

  /* =====================
     KATEGORITE - NGA XTREAM API
  ===================== */
  const currentCategories = useMemo(() => {
    if (activeTab === 'live') {
      return ['TÃ« gjitha', ...categories.live.map(c => c.name || c.title).filter(Boolean)];
    } else if (activeTab === 'movies') {
      return ['TÃ« gjitha', ...categories.movies.map(c => c.name || c.title).filter(Boolean)];
    } else if (activeTab === 'series') {
      return ['TÃ« gjitha', ...categories.series.map(c => c.name || c.title).filter(Boolean)];
    }
    return ['TÃ« gjitha'];
  }, [activeTab, categories]);

  /* =====================
     FILTRO SIPAS KATEGORISE
  ===================== */
  const filteredItems = useMemo(() => {
    if (activeTab === "live") {
      if (!safeChannels.length) return [];
      if (selectedCategory === "TÃ« gjitha") return safeChannels;
      return safeChannels.filter(ch => 
        ch.category === selectedCategory || 
        ch.category_name === selectedCategory ||
        ch.genre === selectedCategory
      );
    } else if (activeTab === "movies") {
      if (!safeMovies.length) return [];
      if (selectedCategory === "TÃ« gjitha") return safeMovies;
      return safeMovies.filter(m => 
        m.category === selectedCategory || 
        m.category_name === selectedCategory ||
        m.genre === selectedCategory
      );
    } else if (activeTab === "series") {
      if (!safeSeries.length) return [];
      if (selectedCategory === "TÃ« gjitha") return safeSeries;
      return safeSeries.filter(s => 
        s.category === selectedCategory || 
        s.category_name === selectedCategory ||
        s.genre === selectedCategory
      );
    }
    return [];
  }, [activeTab, selectedCategory, safeChannels, safeMovies, safeSeries]);

  /* =====================
     KERKIMI
  ===================== */
  const searchedItems = useMemo(() => {
    if (!searchQuery) return filteredItems;
    const q = searchQuery.toLowerCase();
    return filteredItems.filter(item => 
      item.name?.toLowerCase().includes(q) ||
      item.title?.toLowerCase().includes(q)
    );
  }, [filteredItems, searchQuery]);

  /* =====================
     XTREAM LOGIN - PLOT ME KATEGORI
  ===================== */
  const handleXtreamLogin = useCallback(async (credentials) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      // 1. Login
      const login = await xtreamLogin(credentials.server, credentials.username, credentials.password);
      
      if (!login.success) {
        throw new Error(login.error || 'Kredencialet e gabuara');
      }

      // 2. Merr tÃ« gjitha kategoritÃ«
      const allCategories = await getAllCategories(credentials.server, credentials.username, credentials.password);
      
      // 3. Merr tÃ« gjithÃ« content-in
      const content = await getAllContent(credentials.server, credentials.username, credentials.password);

      // 4. Nderto stream URLs per live dhe movies
      const liveWithUrls = content.live.map(ch => ({
        ...ch,
        stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, ch.stream_id, 'live'),
        type: 'live'
      }));

      const moviesWithUrls = content.movies.map(m => ({
        ...m,
        stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, m.stream_id, 'movie', m.container_extension),
        type: 'movie'
      }));

      const seriesWithInfo = content.series.map(s => ({
        ...s,
        type: 'series'
      }));

      // 5. Update state
      setChannels(liveWithUrls);
      setMovies(moviesWithUrls);
      setSeries(seriesWithInfo);
      setCategories(allCategories);
      setXtreamCredentials({ 
        ...credentials, 
        isLoggedIn: true,
        auth: login.data
      });
      setSourceType("xtream");
      setShowXtreamLogin(false);
      setShowM3UModal(false);
      setActiveTab("live");
      setSelectedCategory("TÃ« gjitha");
      
      setSuccess(`âœ… U lidhÃ«t! 
        ðŸ“º ${liveWithUrls.length} kanale, 
        ðŸŽ¬ ${moviesWithUrls.length} filma, 
        ðŸ“º ${seriesWithInfo.length} seriale
        ðŸ·ï¸ ${allCategories.live.length} kategori live,
        ðŸ·ï¸ ${allCategories.movies.length} kategori filmash,
        ðŸ·ï¸ ${allCategories.series.length} kategori serialesh`);
      
    } catch (err) {
      setError(`Xtream gabim: ${err.message}`);
      console.error(err);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     M3U LOAD - VETEM LIVE
  ===================== */
  const handleM3ULoad = useCallback(async (source) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      let m3uText = '';

      if (source.type === 'file') {
        m3uText = source.content;
      } else {
        let url = source.url.trim();
        if (!url.startsWith('http')) url = 'https://' + url;
        const proxyUrl = `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`;
        const response = await fetch(proxyUrl);
        if (!response.ok) throw new Error('Nuk u ngarkua M3U');
        m3uText = await response.text();
      }

      const lines = m3uText.split('\n');
      const channels = [];
      let currentChannel = null;
      let id = 1;
      const categoriesSet = new Set();

      lines.forEach(line => {
        line = line.trim();
        if (line.startsWith('#EXTINF:')) {
          currentChannel = {
            type: 'live',
            stream_id: `m3u_${id}`
          };
          
          // Emri
          const nameMatch = line.split(',').pop();
          if (nameMatch) currentChannel.name = nameMatch.trim();
          
          // Logo
          const logoMatch = line.match(/tvg-logo="([^"]*)"/);
          if (logoMatch) currentChannel.logo = logoMatch[1];
          
          // Kategoria
          const groupMatch = line.match(/group-title="([^"]*)"/);
          if (groupMatch) {
            currentChannel.category = groupMatch[1];
            currentChannel.category_name = groupMatch[1];
            categoriesSet.add(groupMatch[1]);
          }
          
        } else if (line && !line.startsWith('#') && currentChannel) {
          currentChannel.stream_url = line;
          currentChannel.name = currentChannel.name || `Kanali ${id}`;
          currentChannel.category = currentChannel.category || 'TÃ« tjera';
          currentChannel.category_name = currentChannel.category;
          
          channels.push({ ...currentChannel });
          id++;
          currentChannel = null;
        }
      });

      // Krijo kategorite nga M3U
      const m3uCategories = Array.from(categoriesSet).map((cat, index) => ({
        id: index + 1,
        category_id: index + 1,
        name: cat,
        title: cat,
        type: 'live'
      }));

      setChannels(channels);
      setMovies([]);
      setSeries([]);
      setCategories({
        live: m3uCategories,
        movies: [],
        series: [],
        all: m3uCategories.map(c => ({ ...c, type: 'live' }))
      });
      setSourceType("m3u");
      setShowM3UModal(false);
      setActiveTab("live");
      setSelectedCategory("TÃ« gjitha");
      setSuccess(`âœ… ${channels.length} kanale u ngarkuan nga M3U`);
      
    } catch (err) {
      setError(`M3U gabim: ${err.message}`);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     PLAY ITEM
  ===================== */
  const handlePlay = useCallback(async (item) => {
    if (!item) return;

    try {
      let streamUrl = item.stream_url;
      
      // Per Xtream, sigurohu qe URL eshte e sakte
      if (sourceType === "xtream" && xtreamCredentials.isLoggedIn) {
        const { server, username, password } = xtreamCredentials;
        
        if (item.type === 'live' || activeTab === 'live') {
          streamUrl = buildStreamUrl(server, username, password, item.stream_id, 'live');
        } else if (item.type === 'movie' || activeTab === 'movies') {
          streamUrl = buildStreamUrl(server, username, password, item.stream_id, 'movie', item.container_extension || 'mp4');
        } else if (item.type === 'series' || activeTab === 'series') {
          streamUrl = buildStreamUrl(server, username, password, item.series_id || item.stream_id, 'series');
        }
      }

      if (!streamUrl) {
        throw new Error('Stream URL nuk eshte i vlefshem');
      }

      setCurrentStream(streamUrl);
      setCurrentStreamInfo({
        id: item.stream_id || item.series_id,
        name: item.name || item.title,
        logo: item.logo || item.poster || item.cover,
        category: item.category || item.category_name || item.genre,
        type: item.type || activeTab
      });
      setSelectedItem(item);
      setIsPlaying(true);
      setIsPlayerVisible(true);
      
      // Shto ne te fundit
      setRecentItems(prev => {
        const filtered = prev.filter(i => 
          (i.stream_id || i.series_id) !== (item.stream_id || item.series_id)
        );
        return [item, ...filtered].slice(0, 10);
      });

      // Merr EPG per kanalet live
      if ((item.type === 'live' || activeTab === 'live') && sourceType === 'xtream') {
        try {
          const epg = await getEPG(
            xtreamCredentials.server,
            xtreamCredentials.username,
            xtreamCredentials.password,
            item.stream_id
          );
          setEpgData(epg);
          
          // Gjej programin aktual
          const now = Math.floor(Date.now() / 1000);
          const current = epg.find(p => 
            p.start_timestamp <= now && p.end_timestamp >= now
          );
          setCurrentEpg(current);
        } catch (err) {
          console.error('EPG error:', err);
        }
      }
      
    } catch (err) {
      setPlayerError(err.message);
    }
  }, [sourceType, xtreamCredentials, activeTab]);

  /* =====================
     FAVORITES
  ===================== */
  const toggleFavorite = useCallback((id) => {
    setFavorites(prev => 
      prev.includes(id) 
        ? prev.filter(fid => fid !== id)
        : [...prev, id]
    );
  }, []);

  /* =====================
     LOGOUT
  ===================== */
  const handleLogout = useCallback(() => {
    setChannels([]);
    setMovies([]);
    setSeries([]);
    setCategories({ live: [], movies: [], series: [], all: [] });
    setXtreamCredentials({ server: "", username: "", password: "", isLoggedIn: false });
    setM3uActive(false);
    setSourceType(null);
    setCurrentStream(null);
    setIsPlayerVisible(false);
    setShowM3UModal(true);
    setActiveTab("live");
    setSelectedCategory("TÃ« gjitha");
    setRecentItems([]);
    setEpgData([]);
    setCurrentEpg(null);
  }, []);

  /* =====================
     TOGGLE SIDEBAR
  ===================== */
  const toggleSidebar = useCallback(() => {
    setIsSidebarOpen(prev => !prev);
  }, []);

  /* =====================
     GET CATEGORY COUNT
  ===================== */
  const getCategoryCount = useCallback((category) => {
    if (category === "TÃ« gjitha") {
      if (activeTab === 'live') return safeChannels.length;
      if (activeTab === 'movies') return safeMovies.length;
      if (activeTab === 'series') return safeSeries.length;
    }
    
    if (activeTab === 'live') {
      return safeChannels.filter(ch => 
        ch.category === category || 
        ch.category_name === category
      ).length;
    }
    if (activeTab === 'movies') {
      return safeMovies.filter(m => 
        m.category === category || 
        m.category_name === category ||
        m.genre === category
      ).length;
    }
    if (activeTab === 'series') {
      return safeSeries.filter(s => 
        s.category === category || 
        s.category_name === category ||
        s.genre === category
      ).length;
    }
    return 0;
  }, [activeTab, safeChannels, safeMovies, safeSeries]);

  /* =====================
     INITIAL LOAD
  ===================== */
  useEffect(() => {
    setShowM3UModal(true);
  }, []);

  /* =====================
     WELCOME SCREEN
  ===================== */
  if (!sourceType) {
    return (
      <ErrorBoundary>
        <div className="welcome-screen">
          <div className="welcome-container">
            <h1 className="welcome-title">IPTV Player</h1>
            <p className="welcome-subtitle">Zgjidh burimin e tÃ« dhÃ«nave</p>
            
            <div className="welcome-buttons">
              <button 
                className="welcome-btn xtream" 
                onClick={() => {
                  setShowXtreamLogin(true);
                  setShowM3UModal(false);
                }}
              >
                <span className="welcome-icon">ðŸ“¡</span>
                <span className="welcome-btn-title">Xtream Codes</span>
                <span className="welcome-btn-desc">Lidhu me server Xtream</span>
              </button>
              
              <button 
                className="welcome-btn m3u" 
                onClick={() => {
                  setShowM3UModal(true);
                  setShowXtreamLogin(false);
                }}
              >
                <span className="welcome-icon">ðŸ“‹</span>
                <span className="welcome-btn-title">M3U Playlist</span>
                <span className="welcome-btn-desc">URL ose skedar lokal</span>
              </button>
            </div>

            {showXtreamLogin && (
              <XtreamLogin
                onLogin={handleXtreamLogin}
                onClose={() => setShowXtreamLogin(false)}
                isLoading={isLoading.general}
              />
            )}

            {showM3UModal && (
              <M3UUploader
                onLoad={handleM3ULoad}
                onClose={() => setShowM3UModal(false)}
                isLoading={isLoading.general}
              />
            )}
          </div>
        </div>
      </ErrorBoundary>
    );
  }

  /* =====================
     MAIN APP - LIVE, MOVIES, SERIES
  ===================== */
  return (
    <ErrorBoundary>
      <div className="app">
        {/* MODALS */}
        {showXtreamLogin && (
          <XtreamLogin
            onLogin={handleXtreamLogin}
            onClose={() => setShowXtreamLogin(false)}
            isLoading={isLoading.general}
          />
        )}

        {showM3UModal && (
          <M3UUploader
            onLoad={handleM3ULoad}
            onClose={() => setShowM3UModal(false)}
            isLoading={isLoading.general}
          />
        )}

        <SearchModal
          isOpen={showSearchModal}
          query={searchQuery}
          setQuery={setSearchQuery}
          results={searchedItems.slice(0, 30)}
          onClose={() => {
            setShowSearchModal(false);
            setSearchQuery("");
          }}
          onSelectStream={handlePlay}
          onToggleFavorite={toggleFavorite}
          favorites={favorites}
        />

        {/* MESSAGES */}
        {error && (
          <div className="error-toast">
            <span>âš ï¸ {error}</span>
            <button onClick={() => setError(null)}>Ã—</button>
          </div>
        )}

        {success && (
          <div className="success-toast">
            <span>âœ… {success}</span>
            <button onClick={() => setSuccess(null)}>Ã—</button>
          </div>
        )}

        {playerError && (
          <div className="error-toast player-error">
            <span>ðŸŽ¬ {playerError}</span>
            <button onClick={() => setPlayerError(null)}>Ã—</button>
          </div>
        )}

        {/* HEADER */}
        <header className="smart-header">
          <div className="smart-header-left">
            <button className="smart-menu-btn" onClick={toggleSidebar}>
              {isSidebarOpen ? 'â—€' : 'â–¶'}
            </button>
            <h1 className="smart-logo">IPTV</h1>
            <div className="smart-source">
              {sourceType === "xtream" && (
                <span className="smart-badge xtream">
                  <span className="badge-dot"></span>
                  {xtreamCredentials.username}
                </span>
              )}
              {sourceType === "m3u" && (
                <span className="smart-badge m3u">
                  <span className="badge-dot"></span>
                  M3U â€¢ {safeChannels.length}
                </span>
              )}
            </div>
          </div>

          <div className="smart-header-center">
            <div className="smart-search" onClick={() => setShowSearchModal(true)}>
              <span className="smart-search-icon">ðŸ”</span>
              <span className="smart-search-text">KÃ«rko {activeTab === 'live' ? 'kanale' : activeTab === 'movies' ? 'filma' : 'seriale'}...</span>
              <span className="smart-search-hint">Ctrl+K</span>
            </div>
          </div>

          <div className="smart-header-right">
            <button className="smart-icon-btn" onClick={handleLogout} title="Ã‡kyÃ§u">
              <span className="icon">ðŸšª</span>
            </button>
          </div>
        </header>

        {/* TABS - LIVE, MOVIES, SERIES */}
        <div className="smart-tabs">
          {safeChannels.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === 'live' ? 'active' : ''}`}
              onClick={() => {
                setActiveTab('live');
                setSelectedCategory('TÃ« gjitha');
              }}
            >
              <span className="tab-icon">ðŸ“º</span>
              <span className="tab-text">Live TV</span>
              <span className="tab-count">{safeChannels.length}</span>
            </button>
          )}
          {safeMovies.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === 'movies' ? 'active' : ''}`}
              onClick={() => {
                setActiveTab('movies');
                setSelectedCategory('TÃ« gjitha');
              }}
            >
              <span className="tab-icon">ðŸŽ¬</span>
              <span className="tab-text">Filma</span>
              <span className="tab-count">{safeMovies.length}</span>
            </button>
          )}
          {safeSeries.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === 'series' ? 'active' : ''}`}
              onClick={() => {
                setActiveTab('series');
                setSelectedCategory('TÃ« gjitha');
              }}
            >
              <span className="tab-icon">ðŸ“º</span>
              <span className="tab-text">Seriale</span>
              <span className="tab-count">{safeSeries.length}</span>
            </button>
          )}
        </div>

        {/* PLAYER SECTION */}
        {isPlayerVisible && currentStream && (
          <section className="smart-player-section">
            <div className="smart-player-container">
              <div className="smart-player-header">
                <div className="smart-now-playing">
                  <img 
                    src={currentStreamInfo?.logo || `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=1e293b&color=fff`}
                    alt={currentStreamInfo?.name}
                    className="smart-now-logo"
                    onError={(e) => {
                      e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=1e293b&color=fff`;
                    }}
                  />
                  <div className="smart-now-info">
                    <span className="smart-now-label">
                      {currentStreamInfo?.type === 'live' ? 'LIVE' : 
                       currentStreamInfo?.type === 'movie' ? 'FILM' : 
                       currentStreamInfo?.type === 'series' ? 'SERIAL' : 'DUKE U LUATUR'}
                    </span>
                    <span className="smart-now-name">{currentStreamInfo?.name}</span>
                    {currentStreamInfo?.category && (
                      <span className="smart-now-category">{currentStreamInfo.category}</span>
                    )}
                  </div>
                </div>
                <button className="smart-close-btn" onClick={() => setIsPlayerVisible(false)}>
                  âœ•
                </button>
              </div>
              
              <HlsPlayer
                src={currentStream}
                isPlaying={isPlaying}
                onPlayPause={() => setIsPlaying(!isPlaying)}
                onClose={() => setIsPlayerVisible(false)}
                onError={(err) => setPlayerError(err)}
              />

              {/* EPG per Live TV */}
              {activeTab === 'live' && currentEpg && (
                <EPGInfo 
                  channel={currentStreamInfo}
                  currentProgram={{
                    title: currentEpg.title,
                    start: new Date(currentEpg.start_timestamp * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    end: new Date(currentEpg.end_timestamp * 1000).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
                    description: currentEpg.description
                  }}
                  nextProgram={epgData.find(p => p.start_timestamp > currentEpg.end_timestamp)}
                />
              )}
            </div>
          </section>
        )}

        {/* MAIN LAYOUT - SIDEBAR + CONTENT */}
        <div className="smart-layout">
          {/* SIDEBAR - KATEGORITE */}
          <aside className={`smart-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
            <div className="smart-categories">
              <div className="smart-categories-header">
                <h3>KATEGORITÃ‹</h3>
                <span className="smart-category-count">
                  {activeTab === 'live' ? categories.live.length :
                   activeTab === 'movies' ? categories.movies.length :
                   categories.series.length}
                </span>
              </div>
              
              {currentCategories.length > 1 ? (
                <ul className="smart-category-list">
                  {currentCategories.map((category) => {
                    const count = getCategoryCount(category);
                    if (count === 0 && category !== 'TÃ« gjitha') return null;
                    
                    return (
                      <li 
                        key={category}
                        className={`smart-category-item ${selectedCategory === category ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(category)}
                      >
                        <span className="smart-category-icon">
                          {category === 'TÃ« gjitha' ? 'ðŸ“‹' : 
                           activeTab === 'live' ? 'ðŸ“º' : 
                           activeTab === 'movies' ? 'ðŸŽ¬' : 'ðŸ“º'}
                        </span>
                        <span className="smart-category-name">
                          {category === 'TÃ« gjitha' 
                            ? activeTab === 'live' ? 'TÃ« gjitha kanalet' :
                              activeTab === 'movies' ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                            : category}
                        </span>
                        <span className="smart-category-badge">{count}</span>
                      </li>
                    );
                  })}
                </ul>
              ) : (
                <div className="smart-no-categories">
                  Nuk ka kategori
                </div>
              )}
            </div>

            {/* ITEMET E KATEGORISE */}
            <div className="smart-channels">
              <div className="smart-channels-header">
                <div className="smart-channels-title">
                  <span className="smart-channel-icon">
                    {activeTab === 'live' ? 'ðŸ“º' : activeTab === 'movies' ? 'ðŸŽ¬' : 'ðŸ“º'}
                  </span>
                  <span>
                    {selectedCategory === 'TÃ« gjitha' 
                      ? activeTab === 'live' ? 'TÃ« gjitha kanalet' :
                        activeTab === 'movies' ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                      : selectedCategory}
                  </span>
                </div>
                <span className="smart-channel-count">{filteredItems.length}</span>
              </div>
              
              <div className="smart-channels-list">
                {filteredItems.map((item) => {
                  const itemId = item.stream_id || item.series_id || item.id;
                  const itemName = item.name || item.title;
                  const itemLogo = item.logo || item.poster || item.cover;
                  const itemCategory = item.category || item.category_name || item.genre || 
                    (activeTab === 'live' ? 'Live' : 
                     activeTab === 'movies' ? 'Film' : 'Serial');
                  
                  return (
                    <div 
                      key={itemId}
                      className={`smart-channel-item ${selectedItem?.stream_id === itemId || selectedItem?.series_id === itemId ? 'playing' : ''}`}
                      onClick={() => handlePlay(item)}
                    >
                      <img 
                        src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || 'TV')}&size=36&background=f1f5f9&color=334155`}
                        alt={itemName}
                        className="smart-channel-logo"
                        loading="lazy"
                        onError={(e) => {
                          e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || 'TV')}&size=36&background=f1f5f9&color=334155`;
                        }}
                      />
                      
                      <div className="smart-channel-info">
                        <div className="smart-channel-name">{itemName}</div>
                        <div className="smart-channel-meta">
                          <span className="smart-channel-category">
                            {itemCategory}
                          </span>
                          {item.year && (
                            <span className="smart-channel-year">{item.year}</span>
                          )}
                          {item.rating && (
                            <span className="smart-channel-rating">â­ {item.rating}</span>
                          )}
                          {item.season_count > 0 && (
                            <span className="smart-channel-seasons">{item.season_count} sezone</span>
                          )}
                          {item.duration && (
                            <span className="smart-channel-duration">{Math.floor(item.duration / 60)} min</span>
                          )}
                          {(selectedItem?.stream_id === itemId || selectedItem?.series_id === itemId) && (
                            <span className="smart-now-indicator">
                              <span className="dot"></span>
 Duke u luajtur
                            </span>
                          )}
                        </div>
                      </div>

                      <button 
                        className={`smart-fav-btn ${favorites.includes(itemId) ? 'active' : ''}`}
                        onClick={(e) => {
                          e.stopPropagation();
                          toggleFavorite(itemId);
                        }}
                      >
                        {favorites.includes(itemId) ? 'â˜…' : 'â˜†'}
                      </button>
                    </div>
                  );
                })}

                {filteredItems.length === 0 && (
                  <div className="smart-no-channels">
                    {activeTab === 'live' ? 'Nuk ka kanale' :
                     activeTab === 'movies' ? 'Nuk ka filma' : 'Nuk ka seriale'} 
                     nÃ« kÃ«tÃ« kategori
                  </div>
                )}
              </div>
            </div>

            {/* RECENT ITEMS */}
            {recentItems.length > 0 && (
              <div className="smart-recent">
                <div className="smart-recent-header">
                  <h4>TÃ‹ FUNDIT</h4>
                  <span className="smart-recent-count">{recentItems.length}</span>
                </div>
                <div className="smart-recent-list">
                  {recentItems.slice(0, 5).map((item) => {
                    const itemId = item.stream_id || item.series_id || item.id;
                    const itemName = item.name || item.title;
                    const itemLogo = item.logo || item.poster || item.cover;
                    
                    return (
                      <div 
                        key={itemId}
                        className="smart-recent-item"
                        onClick={() => handlePlay(item)}
                      >
                        <img 
                          src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName || 'TV')}&size=24&background=e2e8f0&color=334155`}
                          alt={itemName}
                          className="smart-recent-logo"
                        />
                        <span className="smart-recent-name">{itemName}</span>
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* SIDEBAR FOOTER */}
            <div className="smart-sidebar-footer">
              <div className="smart-stats">
                <div className="smart-stat-item">
                  <span className="stat-icon">ðŸ“º</span>
                  <span className="stat-label">Live</span>
                  <span className="stat-value">{safeChannels.length}</span>
                </div>
                <div className="smart-stat-item">
                  <span className="stat-icon">ðŸŽ¬</span>
                  <span className="stat-label">Filma</span>
                  <span className="stat-value">{safeMovies.length}</span>
                </div>
                <div className="smart-stat-item">
                  <span className="stat-icon">ðŸ“º</span>
                  <span className="stat-label">Seriale</span>
                  <span className="stat-value">{safeSeries.length}</span>
                </div>
                {favorites.length > 0 && (
                  <div className="smart-stat-item">
                    <span className="stat-icon">â­</span>
                    <span className="stat-label">Favorite</span>
                    <span className="stat-value">{favorites.length}</span>
                  </div>
                )}
              </div>
            </div>
          </aside>

          {/* CONTENT AREA - BOSH */}
          <main className="smart-content"></main>
        </div>

        {/* LOADING */}
        {isLoading.general && (
          <div className="smart-loading">
            <div className="smart-spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
