// services/xtream.jsx
import React from 'react';

/**
 * Nderto URL-në e Xtream API
 */
const buildUrl = (server, user, pass, action = '') => {
  let baseUrl = server.trim();

  if (baseUrl.endsWith('/')) baseUrl = baseUrl.slice(0, -1);
  if (!baseUrl.startsWith('http://') && !baseUrl.startsWith('https://')) {
    baseUrl = 'http://' + baseUrl;
  }

  return action
    ? `${baseUrl}/player_api.php?username=${encodeURIComponent(
        user
      )}&password=${encodeURIComponent(pass)}&action=${action}`
    : `${baseUrl}/player_api.php?username=${encodeURIComponent(
        user
      )}&password=${encodeURIComponent(pass)}`;
};

/**
 * Fetch me CORS proxy
 */
const fetchWithCORSProxy = async (url) => {
  const proxies = [
    `https://corsproxy.io/?${encodeURIComponent(url)}`,
    `https://api.codetabs.com/v1/proxy?quest=${encodeURIComponent(url)}`,
    `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`,
  ];

  for (const proxyUrl of proxies) {
    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000);

      const response = await fetch(proxyUrl, {
        signal: controller.signal,
        headers: { Accept: 'application/json' },
      });

      clearTimeout(timeoutId);

      if (response.ok) {
        const text = await response.text();

        if (proxyUrl.includes('allorigins')) {
          try {
            const wrapped = JSON.parse(text);
            if (wrapped.contents) return JSON.parse(wrapped.contents);
          } catch {}
        } else {
          try {
            return JSON.parse(text);
          } catch {
            return text;
          }
        }
      }
    } catch {
      continue;
    }
  }

  throw new Error('Të gjitha proxy-t dështuan');
};

/**
 * LOGIN
 */
export const xtreamLogin = async (server, user, pass) => {
  try {
    const url = buildUrl(server, user, pass);
    const data = await fetchWithCORSProxy(url);

    if (data && data.user_info && (data.user_info.auth === 1 || data.user_info.status === 'Active')) {
      return { success: true, data: data.user_info, server };
    }

    return { success: false, error: 'Kredencialet e gabuara' };
  } catch {
    return { success: false, error: 'Nuk u lidh me serverin' };
  }
};

/**
 * Group content by category
 */
export const groupByCategory = (items, defaultCategory = 'Të tjera') => {
  const grouped = {};
  items.forEach((item) => {
    let category = item.category_name || item.category || defaultCategory;
    category = category.toString().trim() || defaultCategory;
    if (!grouped[category]) grouped[category] = [];
    grouped[category].push(item);
  });

  return Object.keys(grouped).map((name) => ({
    name,
    items: grouped[name],
  }));
};

/**
 * LIVE STREAMS
 */
export const getLiveStreams = async (server, user, pass) => {
  const actions = ['get_live_streams', 'get_live_channels', 'get_channels'];

  for (const action of actions) {
    try {
      const url = buildUrl(server, user, pass, action);
      const data = await fetchWithCORSProxy(url);
      if (!data) continue;

      let streams = [];
      if (Array.isArray(data)) streams = data;
      else if (data.live) streams = data.live;
      else if (data.channels) streams = data.channels;
      else if (data.data) streams = data.data;

      if (streams.length > 0) {
        return streams.map((channel) => {
          let category = channel.category_name || channel.category || 'Të tjera';
          if (typeof category === 'number') category = `Kategoria ${category}`;
          category = category.toString().trim() || 'Të tjera';

          return {
            id: channel.stream_id || channel.id,
            stream_id: channel.stream_id || channel.id,
            name: channel.name || 'Pa emër',
            title: channel.name || 'Pa emër',
            logo: channel.stream_icon || channel.logo || null,
            poster: channel.stream_icon || channel.logo || null,
            cover: channel.stream_icon || channel.logo || null,
            category_id: channel.category_id,
            category,
            category_name: category,
            epg_channel_id: channel.epg_channel_id,
            type: 'live',
            stream_url: null,
          };
        });
      }
    } catch {
      continue;
    }
  }

  return [];
};

/**
 * LIVE CATEGORIES
 */
export const getLiveCategories = async (server, user, pass) => {
  const actions = ['get_live_categories', 'get_categories'];

  for (const action of actions) {
    try {
      const url = buildUrl(server, user, pass, action);
      const data = await fetchWithCORSProxy(url);
      if (!data) continue;

      let categories = Array.isArray(data) ? data : data.categories || [];
      if (categories.length > 0) {
        return categories.map((cat) => ({
          id: cat.category_id || cat.id,
          category_id: cat.category_id || cat.id,
          name: cat.category_name || cat.name || 'Të tjera',
          title: cat.category_name || cat.name || 'Të tjera',
          type: 'live',
        }));
      }
    } catch {}
  }

  return [];
};

/**
