      const data = await fetchWithCORSProxy(url);
      if (!data) continue;

      let movies = Array.isArray(data)
        ? data
        : data.vod || data.movies || data.data || [];

      if (movies.length > 0) {
        return movies.map((movie) => {
          let category = movie.category_name || movie.genre || movie.category || 'Filma';
          if (typeof category === 'number') category = `Kategoria ${category}`;
          category = category.toString().trim() || 'Filma';

          return {
            id: movie.stream_id || movie.id,
            stream_id: movie.stream_id || movie.id,
