// src/App.jsx - Versioni i optimizuar pÃ«r performancÃ«
import React, { useState, useEffect, useMemo, useCallback } from "react";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import XtreamLogin from "./components/XtreamLogin";
import M3UUploader from "./components/M3UUploader";
import SearchModal from "./components/SearchModal";
import WelcomeScreen from "./components/WelcomeScreen";
import ChannelListWithPagination from "./components/ChannelListWithPagination"; // âœ… Versioni me pagination
import VirtualChannelList from "./components/VirtualChannelList"; // âœ… PÃ«r 10,000+ kanale
import Toast from "./components/Toast";
import LoadingSpinner from "./components/LoadingSpinner";
import { useIPTVData } from "./hooks/useIPTVData";
import { useTheme } from "./hooks/useTheme";
import { useErrorHandler } from "./hooks/useErrorHandler";
import { useLocalStorage } from "./hooks/useLocalStorage";
import { useChannelCache } from "./hooks/useChannelCache"; // âœ… Hook i ri pÃ«r cache
import { 
  xtreamLogin, 
  getCategoriesOnly, // âœ… VetÃ«m kategoritÃ« (shpejt)
  getChannelsByCategory, // âœ… Kanalet sipas kategorisÃ«
  getAllContent, 
  getAllCategories, 
  buildStreamUrl,
  getEPG,
  fetchM3UFromUrl,
  parseM3U
} from './services/xtream';
import { 
  getItemCategory, 
  getItemName, 
  getItemId, 
  getItemLogo, 
  getStreamType,
  searchItems
} from './utils/streamHelpers';
import { TABS, SOURCE_TYPES, STREAM_TYPES, APP_CONSTANTS } from './constants/iptv';
import "./App.css";

const INITIAL_CREDENTIALS = {
  server: "",
  username: "",
  password: "",
  isLoggedIn: false,
  expires: null,
  activeConnections: null,
  maxConnections: null,
  auth: null
};

const INITIAL_LOADING_STATE = {
  categories: false,
  channels: false,
  movies: false,
  series: false,
  general: false
};

function App() {
  const { theme, toggleTheme } = useTheme();
  const { error, success, handleError, handleSuccess, clearMessages } = useErrorHandler();
  const [favorites, setFavorites] = useLocalStorage('iptv-favorites', []);
  const [recentItems, setRecentItems] = useLocalStorage('iptv-recent', []);
  
  // âœ… PÃ«rdor cache pÃ«r kanale
  const { getCachedChannels, setCachedChannels, prefetchCategory } = useChannelCache();
  
  const {
    channels, setChannels,
    movies, setMovies,
    series, setSeries,
    categories, setCategories,
    safeChannels, safeMovies, safeSeries,
    clearData
  } = useIPTVData();

  const [xtreamCredentials, setXtreamCredentials] = useState(INITIAL_CREDENTIALS);
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);
  const [epgData, setEpgData] = useState([]);
  const [currentEpg, setCurrentEpg] = useState(null);
  const [activeTab, setActiveTab] = useState(TABS.LIVE);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showXtreamLogin, setShowXtreamLogin] = useState(false);
  const [showM3UModal, setShowM3UModal] = useState(true);
  const [sourceType, setSourceType] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(APP_CONSTANTS.DEFAULT_CATEGORY);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [isLoading, setIsLoading] = useState(INITIAL_LOADING_STATE);
  
  // âœ… State pÃ«r pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(50); // âœ… VetÃ«m 50 kanale pÃ«r faqe!

  // ================ EFFECTS ================
  useEffect(() => {
    setShowM3UModal(!sourceType);
  }, [sourceType]);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.ctrlKey && e.key === 'k') {
        e.preventDefault();
        setShowSearchModal(true);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  // ================ COMPUTED PROPERTIES ================
  
  // âœ… 1. KATEGORITÃ‹ - Ngarkohen shpejt sepse janÃ« tÃ« vogla
  const currentCategories = useMemo(() => {
    const categoriesSet = new Set();
    categoriesSet.add(APP_CONSTANTS.DEFAULT_CATEGORY);
    
    // PÃ«rdor kategoritÃ« nga API ose cache
    if (categories && categories.length > 0) {
      categories.forEach(cat => {
        if (cat.category_name || cat.name) {
          categoriesSet.add(cat.category_name || cat.name);
        }
      });
    }
    
    return Array.from(categoriesSet);
  }, [categories]);

  // âœ… 2. FILTERED ITEMS - VetÃ«m pÃ«r kategorinÃ« aktive
  const filteredItems = useMemo(() => {
    let items = [];
    
    // Merr vetÃ«m pÃ«r tab-in aktiv
    if (activeTab === TABS.LIVE) {
      // Kontrollo cache para se tÃ« pÃ«rdorÃ«sh state
      items = getCachedChannels(activeTab, selectedCategory) || safeChannels;
    }
    else if (activeTab === TABS.MOVIES) items = safeMovies;
    else if (activeTab === TABS.SERIES) items = safeSeries;
    
    if (!items.length) return [];
    
    if (selectedCategory === APP_CONSTANTS.DEFAULT_CATEGORY) {
      return items;
    }
    
    // Filtro sipas kategorisÃ«
    return items.filter(item => {
      const itemCategory = 
        item.groupTitle || 
        item.category_name || 
        item.category || 
        item.tvg_group || 
        item.group || 
        'Pa Kategori';
      return itemCategory === selectedCategory;
    });
  }, [activeTab, selectedCategory, safeChannels, safeMovies, safeSeries, getCachedChannels]);

  // âœ… 3. PAGINATION - VetÃ«m 50 kanale pÃ«r faqe!
  const paginatedItems = useMemo(() => {
    const start = 0;
    const end = currentPage * itemsPerPage;
    return filteredItems.slice(start, end);
  }, [filteredItems, currentPage, itemsPerPage]);

  const searchedItems = useMemo(() => {
    if (!searchQuery) return paginatedItems;
    return searchItems(paginatedItems, searchQuery);
  }, [paginatedItems, searchQuery]);

  const hasLiveContent = safeChannels.length > 0;
  const hasMoviesContent = safeMovies.length > 0;
  const hasSeriesContent = safeSeries.length > 0;

  // ================ HANDLERS ================
  const setLoading = useCallback((key, value) => {
    setIsLoading(prev => ({ ...prev, [key]: value }));
  }, []);

  // âœ… HANDLER I OPTIMIZUAR PÃ‹R XTREAM
  const handleXtreamLogin = useCallback(async (credentials) => {
    setLoading('general', true);
    setLoading('categories', true);
    clearMessages();
    
    try {
      // âœ… 1. VERIFIKO KREDENCIALET (shpejt)
      const login = await xtreamLogin(credentials.server, credentials.username, credentials.password);
      
      if (!login.success) {
        throw new Error(login.error || 'Kredencialet e gabuara');
      }

      // âœ… 2. NGARKO VETÃ‹M KATEGORITÃ‹ FILLIMISHT (shpejt - 1 sekondÃ«)
      const allCategories = await getAllCategories(
        credentials.server, 
        credentials.username, 
        credentials.password
      );
      
      setCategories(allCategories);
      
      setXtreamCredentials({ 
        ...credentials, 
        isLoggedIn: true,
        auth: login.data
      });
      
      setSourceType(SOURCE_TYPES.XTREAM);
      setShowXtreamLogin(false);
      setActiveTab(TABS.LIVE);
      setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
      
      handleSuccess(`âœ… U lidhÃ«t! Duke ngarkuar kategoritÃ«...`);
      
      setLoading('categories', false);
      
      // âœ… 3. NGARKO KANALET LIVE NÃ‹ BACKGROUND (pasi shfaqet UI)
      setTimeout(() => {
        loadLiveChannelsBackground(credentials);
      }, 500);
      
    } catch (err) {
      console.error('âŒ Login error:', err);
      handleError(err, 'Nuk u lidh me serverin: ' + err.message);
      setLoading('categories', false);
      setLoading('general', false);
    }
  }, [setCategories, handleSuccess, handleError, clearMessages, setLoading]);

  // âœ… FUNKSION PÃ‹R NGARKIMIN NÃ‹ BACKGROUND TÃ‹ KANALEVE LIVE
  const loadLiveChannelsBackground = useCallback(async (credentials) => {
    setLoading('channels', true);
    
    try {
      console.log('â³ Duke ngarkuar kanalet live nÃ« background...');
      
      // Merr tÃ« gjitha kategoritÃ« live
      const liveCategories = categories.filter(c => c.category_type === 'live');
      
      let allChannels = [];
      
      // âœ… Ngarko vetÃ«m 3 kategori tÃ« para menjÃ«herÃ«
      const firstCategories = liveCategories.slice(0, 3);
      const restCategories = liveCategories.slice(3);
      
      for (const cat of firstCategories) {
        const channels = await getChannelsByCategory(
          credentials.server,
          credentials.username,
          credentials.password,
          'live',
          cat.category_id
        );
        
        const channelsWithUrls = channels.map(ch => ({
          ...ch,
          stream_url: buildStreamUrl(
            STREAM_TYPES.LIVE,
            ch.stream_id,
            credentials.server,
            credentials.username,
            credentials.password
          ),
          type: STREAM_TYPES.LIVE
        }));
        
        allChannels = [...allChannels, ...channelsWithUrls];
        
        // âœ… Ruaj nÃ« cache
        setCachedChannels('live', cat.category_name, channelsWithUrls);
      }
      
      // âœ… Vendos kanalet e para
      setChannels(allChannels);
      
      // âœ… Ngarko pjesÃ«n tjetÃ«r nÃ« background
      if (restCategories.length > 0) {
        setTimeout(() => {
          loadRemainingChannels(credentials, restCategories, allChannels);
        }, 1000);
      }
      
      setLoading('channels', false);
      setLoading('general', false);
      
      handleSuccess(`âœ… ${allChannels.length} kanale live u ngarkuan`);
      
    } catch (err) {
      console.error('âŒ Gabim gjatÃ« ngarkimit tÃ« kanaleve:', err);
      setLoading('channels', false);
      setLoading('general', false);
    }
  }, [categories, setChannels, setCachedChannels, handleSuccess]);

  // âœ… NGARKIMI GRADUAL I PJESÃ‹S SÃ‹ MBETUR
  const loadRemainingChannels = useCallback(async (credentials, categories, existingChannels) => {
    let allChannels = [...existingChannels];
    
    for (let i = 0; i < categories.length; i++) {
      const cat = categories[i];
      
      try {
        const channels = await getChannelsByCategory(
          credentials.server,
          credentials.username,
          credentials.password,
          'live',
          cat.category_id
        );
        
        const channelsWithUrls = channels.map(ch => ({
          ...ch,
          stream_url: buildStreamUrl(
            STREAM_TYPES.LIVE,
            ch.stream_id,
            credentials.server,
            credentials.username,
            credentials.password
          ),
          type: STREAM_TYPES.LIVE
        }));
        
        allChannels = [...allChannels, ...channelsWithUrls];
        
        // âœ… PÃ«rditÃ«so gradualisht
        if (i % 5 === 4) { // Ã‡do 5 kategori
          setChannels([...allChannels]);
          setCachedChannels('live', 'all', allChannels);
        }
        
        // âœ… Prefetch kategorinÃ« tjetÃ«r
        if (i < categories.length - 1) {
          prefetchCategory(
            credentials,
            'live',
            categories[i + 1].category_id
          );
        }
        
      } catch (err) {
        console.error(`âŒ Gabim pÃ«r kategorinÃ« ${cat.category_name}:`, err);
      }
    }
    
    // âœ… PÃ«rditÃ«so final
    setChannels(allChannels);
    setCachedChannels('live', 'all', allChannels);
    
    console.log(`âœ… U ngarkuan gjithsej ${allChannels.length} kanale live`);
  }, [setChannels, setCachedChannels, prefetchCategory]);

  // âœ… HANDLER I OPTIMIZUAR PÃ‹R M3U
  const handleM3ULoad = useCallback(async (source) => {
    setLoading('general', true);
    setLoading('channels', true);
    clearMessages();
    
    try {
      let channels = [];
      
      if (source.channels) {
        channels = source.channels;
      } else {
        const m3uText = source.type === 'file' 
          ? source.content 
          : await fetchM3UFromUrl(source.url);
        
        channels = parseM3U(m3uText);
      }

      if (channels.length === 0) {
        throw new Error('Nuk u gjet asnjÃ« kanal nÃ« playlist');
      }

      console.log(`âœ… U gjetÃ«n ${channels.length} kanale nga M3U`);
      
      // âœ… Ngarko vetÃ«m 100 kanale tÃ« para menjÃ«herÃ«
      const firstChannels = channels.slice(0, 100);
      setChannels(firstChannels);
      
      setSourceType(SOURCE_TYPES.M3U);
      setShowM3UModal(false);
      setActiveTab(TABS.LIVE);
      setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
      
      handleSuccess(`âœ… ${firstChannels.length} kanale u ngarkuan nga M3U`);
      
      // âœ… Ngarko pjesÃ«n tjetÃ«r nÃ« background
      if (channels.length > 100) {
        setTimeout(() => {
          setChannels(channels);
          handleSuccess(`âœ… Gjithsej ${channels.length} kanale u ngarkuan`);
        }, 1000);
      }
      
    } catch (err) {
      console.error('âŒ M3U error:', err);
      handleError(err, 'M3U gabim: ' + err.message);
    } finally {
      setLoading('general', false);
      setLoading('channels', false);
    }
  }, [setChannels, handleSuccess, handleError, clearMessages, setLoading]);

  // âœ… LOAD MORE PÃ‹R PAGINATION
  const loadMoreItems = useCallback(() => {
    if (currentPage * itemsPerPage < filteredItems.length) {
      setCurrentPage(prev => prev + 1);
    }
  }, [currentPage, itemsPerPage, filteredItems.length]);

  const buildStreamUrlFromCredentials = useCallback((item) => {
    if (sourceType !== SOURCE_TYPES.XTREAM || !xtreamCredentials.isLoggedIn) {
      return item.stream_url || item.url;
    }

    const { server, username, password } = xtreamCredentials;
    const streamType = getStreamType(item, activeTab);
    const streamId = item.stream_id || item.series_id || item.id;
    const extension = item.container_extension || 'm3u8';

    return buildStreamUrl(streamType, streamId, server, username, password, extension);
  }, [sourceType, xtreamCredentials, activeTab]);

  const fetchEPGData = useCallback(async (streamId) => {
    if (sourceType !== SOURCE_TYPES.XTREAM || !xtreamCredentials.isLoggedIn) {
      return;
    }

    try {
      const epg = await getEPG(
        xtreamCredentials.server,
        xtreamCredentials.username,
        xtreamCredentials.password,
        streamId
      );
      
      setEpgData(epg || []);
      
      const now = Math.floor(Date.now() / 1000);
      const current = (epg || []).find(p => 
        p.start_timestamp <= now && p.end_timestamp >= now
      );
      setCurrentEpg(current || null);
    } catch (err) {
      console.error('âŒ EPG error:', err);
      setEpgData([]);
      setCurrentEpg(null);
    }
  }, [sourceType, xtreamCredentials]);

  const handlePlay = useCallback(async (item) => {
    if (!item) return;

    try {
      const streamUrl = buildStreamUrlFromCredentials(item);
      
      if (!streamUrl) {
        throw new Error('Stream URL nuk eshte i vlefshem');
      }

      const itemId = getItemId(item);
      const itemName = getItemName(item);
      const itemLogo = getItemLogo(item);
      const itemCategory = getItemCategory(item, activeTab);
      const streamType = getStreamType(item, activeTab);
      
      setCurrentStream(streamUrl);
      setCurrentStreamInfo({
        id: itemId,
        name: itemName,
        logo: itemLogo,
        category: itemCategory,
        type: streamType
      });
      setSelectedItem(item);
      setIsPlaying(true);
      setIsPlayerVisible(true);
      
      setRecentItems(prev => {
        const filtered = prev.filter(i => getItemId(i) !== itemId);
        return [item, ...filtered].slice(0, APP_CONSTANTS.MAX_RECENT_ITEMS);
      });

      if (streamType === STREAM_TYPES.LIVE) {
        await fetchEPGData(itemId);
      }
      
    } catch (err) {
      console.error('âŒ Play error:', err);
      handleError(err, 'Gabim gjatÃ« luajtjes: ' + err.message);
    }
  }, [buildStreamUrlFromCredentials, activeTab, fetchEPGData, handleError, setRecentItems]);

  const toggleFavorite = useCallback((id) => {
    setFavorites(prev => 
      prev.includes(id) ? prev.filter(fid => fid !== id) : [...prev, id]
    );
  }, [setFavorites]);

  const handleLogout = useCallback(() => {
    clearData();
    setXtreamCredentials(INITIAL_CREDENTIALS);
    setSourceType(null);
    setCurrentStream(null);
    setIsPlayerVisible(false);
    setIsPlaying(false);
    setActiveTab(TABS.LIVE);
    setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
    setCurrentPage(1);
    setEpgData([]);
    setCurrentEpg(null);
    setCurrentStreamInfo(null);
    setSelectedItem(null);
    setSearchQuery("");
    clearMessages();
  }, [clearData, clearMessages]);

  const toggleSidebar = useCallback(() => {
    setIsSidebarOpen(prev => !prev);
  }, []);

  const handleEpgUpdate = useCallback((epg) => {
    setEpgData(epg || []);
    const now = Math.floor(Date.now() / 1000);
    const current = (epg || []).find(p => 
      p.start_timestamp <= now && p.end_timestamp >= now
    );
    setCurrentEpg(current || null);
  }, []);

  const handleTabChange = useCallback((tab) => {
    setActiveTab(tab);
    setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
    setCurrentPage(1); // Reset pagination
  }, []);

  const handleSearchClose = useCallback(() => {
    setShowSearchModal(false);
    setSearchQuery("");
  }, []);

  const handleCategoryChange = useCallback((category) => {
    setSelectedCategory(category);
    setCurrentPage(1); // Reset pagination
    
    // âœ… Prefetch kanalet pÃ«r kategorinÃ« e re
    if (sourceType === SOURCE_TYPES.XTREAM && xtreamCredentials.isLoggedIn) {
      prefetchCategory(
        xtreamCredentials,
        activeTab,
        category
      );
    }
  }, [sourceType, xtreamCredentials, activeTab, prefetchCategory]);

  const getCategoryCount = useCallback((category) => {
    let items = [];
    if (activeTab === TABS.LIVE) items = safeChannels;
    else if (activeTab === TABS.MOVIES) items = safeMovies;
    else if (activeTab === TABS.SERIES) items = safeSeries;
    
    if (!items.length) return 0;
    
    if (category === APP_CONSTANTS.DEFAULT_CATEGORY) {
      return items.length;
    }
    
    return items.filter(item => {
      const itemCategory = 
        item.groupTitle || 
        item.category_name || 
        item.category || 
        item.tvg_group || 
        item.group || 
        'Pa Kategori';
      return itemCategory === category;
    }).length;
  }, [activeTab, safeChannels, safeMovies, safeSeries]);

  // ================ RENDER ================
  if (!sourceType) {
    return (
      <ErrorBoundary>
        <WelcomeScreen
          theme={theme}
          showXtreamLogin={showXtreamLogin}
          showM3UModal={showM3UModal}
          onXtreamClick={() => {
            setShowXtreamLogin(true);
            setShowM3UModal(false);
          }}
          onM3UClick={() => {
            setShowM3UModal(true);
            setShowXtreamLogin(false);
          }}
          onXtreamLogin={handleXtreamLogin}
          onM3ULoad={handleM3ULoad}
          onCloseXtream={() => setShowXtreamLogin(false)}
          onCloseM3U={() => setShowM3UModal(false)}
          isLoading={isLoading.general || isLoading.categories}
        />
      </ErrorBoundary>
    );
  }

  return (
    <ErrorBoundary>
      <div className={`app theme-${theme}`} data-theme={theme}>
        {/* Modals */}
        {showXtreamLogin && (
          <XtreamLogin
            onLogin={handleXtreamLogin}
            onClose={() => setShowXtreamLogin(false)}
            isLoading={isLoading.general}
            theme={theme}
          />
        )}

        {showM3UModal && (
          <M3UUploader
            onLoad={handleM3ULoad}
            onClose={() => setShowM3UModal(false)}
            isLoading={isLoading.general}
            theme={theme}
          />
        )}

        <SearchModal
          isOpen={showSearchModal}
          query={searchQuery}
          setQuery={setSearchQuery}
          results={searchedItems.slice(0, APP_CONSTANTS.MAX_SEARCH_RESULTS)}
          onClose={handleSearchClose}
          onSelectStream={handlePlay}
          onToggleFavorite={toggleFavorite}
          favorites={favorites}
          theme={theme}
          activeTab={activeTab}
        />

        <Toast 
          error={error} 
          success={success} 
          onClose={clearMessages} 
          theme={theme}
        />

        {/* Header */}
        <header className="smart-header">
          <div className="smart-header-left">
            <button 
              className="smart-menu-btn" 
              onClick={toggleSidebar}
              aria-label={isSidebarOpen ? 'Mbyll menunÃ«' : 'Hap menunÃ«'}
            >
              {isSidebarOpen ? 'â—€' : 'â–¶'}
            </button>
            <h1 className="smart-logo">IPTV Player</h1>
            {sourceType && (
              <div className="smart-source">
                {sourceType === SOURCE_TYPES.XTREAM && (
                  <span className="smart-badge xtream">
                    <span className="badge-dot"></span>
                    {xtreamCredentials.username}
                  </span>
                )}
                {sourceType === SOURCE_TYPES.M3U && (
                  <span className="smart-badge m3u">
                    <span className="badge-dot"></span>
                    M3U â€¢ {safeChannels.length}
                  </span>
                )}
              </div>
            )}
          </div>

          <div className="smart-header-center">
            <button 
              className="smart-search" 
              onClick={() => setShowSearchModal(true)}
              aria-label="KÃ«rko"
            >
              <span className="smart-search-icon">ðŸ”</span>
              <span className="smart-search-text">
                KÃ«rko {activeTab === TABS.LIVE ? 'kanale' : 
                       activeTab === TABS.MOVIES ? 'filma' : 'seriale'}...
              </span>
              <span className="smart-search-hint">Ctrl+K</span>
            </button>
          </div>

          <div className="smart-header-right">
            <button 
              className="smart-icon-btn theme-btn" 
              onClick={toggleTheme} 
              title={theme === 'dark' ? 'Light Mode' : 'Dark Mode'}
              aria-label="Ndrysho temÃ«n"
            >
              {theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}
            </button>
            <button 
              className="smart-icon-btn logout-btn" 
              onClick={handleLogout} 
              title="Ã‡kyÃ§u"
              aria-label="Ã‡kyÃ§u"
            >
              <span className="icon">ðŸšª</span>
            </button>
          </div>
        </header>

        {/* Tabs */}
        {(hasLiveContent || hasMoviesContent || hasSeriesContent) && (
          <div className="smart-tabs">
            {hasLiveContent && (
              <button 
                className={`smart-tab ${activeTab === TABS.LIVE ? 'active' : ''}`}
                onClick={() => handleTabChange(TABS.LIVE)}
                aria-label="Live TV"
              >
                <span className="tab-icon">ðŸ“º</span>
                <span className="tab-text">Live TV</span>
                <span className="tab-count">{safeChannels.length}</span>
              </button>
            )}
            {hasMoviesContent && (
              <button 
                className={`smart-tab ${activeTab === TABS.MOVIES ? 'active' : ''}`}
                onClick={() => handleTabChange(TABS.MOVIES)}
                aria-label="Filma"
              >
                <span className="tab-icon">ðŸŽ¬</span>
                <span className="tab-text">Filma</span>
                <span className="tab-count">{safeMovies.length}</span>
              </button>
            )}
            {hasSeriesContent && (
              <button 
                className={`smart-tab ${activeTab === TABS.SERIES ? 'active' : ''}`}
                onClick={() => handleTabChange(TABS.SERIES)}
                aria-label="Seriale"
              >
                <span className="tab-icon">ðŸ“º</span>
                <span className="tab-text">Seriale</span>
                <span className="tab-count">{safeSeries.length}</span>
              </button>
            )}
          </div>
        )}

        {/* Player Section */}
        {isPlayerVisible && currentStream && (
          <section className="smart-player-section">
            <div className="smart-player-container">
              <div className="smart-player-header">
                <div className="smart-now-playing">
                  <img 
                    src={currentStreamInfo?.logo || `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`}
                    alt={currentStreamInfo?.name}
                    className="smart-now-logo"
                    loading="lazy"
                    onError={(e) => {
                      e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`;
                    }}
                  />
                  <div className="smart-now-info">
                    <span className="smart-now-label">
                      {currentStreamInfo?.type === STREAM_TYPES.LIVE ? 'LIVE' : 
                       currentStreamInfo?.type === STREAM_TYPES.MOVIE ? 'FILM' : 
                       currentStreamInfo?.type === STREAM_TYPES.SERIES ? 'SERIAL' : 'DUKE U LUATUR'}
                    </span>
                    <span className="smart-now-name">{currentStreamInfo?.name}</span>
                    {currentEpg && (
                      <span className="smart-now-epg">{currentEpg.title}</span>
                    )}
                  </div>
                </div>
                <button 
                  className="smart-close-btn" 
                  onClick={() => setIsPlayerVisible(false)}
                >
                  âœ•
                </button>
              </div>
              
              <HlsPlayer
                src={currentStream}
                isPlaying={isPlaying}
                onPlayPause={() => setIsPlaying(!isPlaying)}
                onClose={() => setIsPlayerVisible(false)}
                onError={(err) => {
                  console.error('ðŸŽ¬ Player error:', err);
                  handleError(err, 'Gabim nÃ« player: ' + err.message);
                }}
                theme={theme}
                currentStreamInfo={currentStreamInfo}
                currentEpg={currentEpg}
                epgData={epgData}
                onEpgUpdate={handleEpgUpdate}
              />
            </div>
          </section>
        )}

        {/* Main Layout */}
        <div className="smart-layout">
          {/* Sidebar - KategoritÃ« */}
          <aside className={`smart-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
            <div className="smart-categories">
              <div className="smart-categories-header">
                <h3>KATEGORITÃ‹</h3>
                <span className="smart-category-count">
                  {currentCategories.length - 1}
                </span>
              </div>
              
              {currentCategories.length > 1 ? (
                <ul className="smart-category-list">
                  {currentCategories.map((category) => {
                    const count = getCategoryCount(category);
                    if (count === 0 && category !== APP_CONSTANTS.DEFAULT_CATEGORY) return null;
                    
                    return (
                      <li 
                        key={category}
                        className={`smart-category-item ${selectedCategory === category ? 'active' : ''}`}
                        onClick={() => handleCategoryChange(category)}
                        role="button"
                        tabIndex={0}
                        onKeyDown={(e) => e.key === 'Enter' && handleCategoryChange(category)}
                      >
                        <span className="smart-category-icon">
                          {category === APP_CONSTANTS.DEFAULT_CATEGORY ? 'ðŸ“‹' : 
                           activeTab === TABS.LIVE ? 'ðŸ“º' : 
                           activeTab === TABS.MOVIES ? 'ðŸŽ¬' : 'ðŸ“º'}
                        </span>
                        <span className="smart-category-name">
                          {category === APP_CONSTANTS.DEFAULT_CATEGORY 
                            ? activeTab === TABS.LIVE ? 'TÃ« gjitha kanalet' :
                              activeTab === TABS.MOVIES ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                            : category}
                        </span>
                        <span className="smart-category-badge">{count}</span>
                      </li>
                    );
                  })}
                </ul>
              ) : (
                <div className="smart-no-categories">
                  {isLoading.channels ? 'Duke ngarkuar kategoritÃ«...' : 'Nuk ka kategori'}
                </div>
              )}
            </div>
          </aside>

          {/* Main Content */}
          <main className="smart-content">
            <div className="smart-content-header">
              <div className="smart-content-title">
                <span className="smart-content-icon">
                  {activeTab === TABS.LIVE ? 'ðŸ“º' : activeTab === TABS.MOVIES ? 'ðŸŽ¬' : 'ðŸ“º'}
                </span>
                <h2>
                  {selectedCategory === APP_CONSTANTS.DEFAULT_CATEGORY 
                    ? activeTab === TABS.LIVE ? 'TÃ« gjitha kanalet' :
                      activeTab === TABS.MOVIES ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                    : selectedCategory}
                </h2>
                <span className="smart-content-count">{filteredItems.length}</span>
              </div>
              {isLoading.channels && (
                <div className="loading-status">
                  <div className="loading-spinner"></div>
                  <span>Duke ngarkuar kanalet {safeChannels.length}...</span>
                </div>
              )}
            </div>
            
            {/* âœ… ZGJEDH VIEW SIPAS NUMRIT TÃ‹ KANALEVE */}
            {filteredItems.length > 1000 ? (
              <VirtualChannelList
                items={paginatedItems}
                onPlay={handlePlay}
                onToggleFavorite={toggleFavorite}
                favorites={favorites}
                selectedItem={selectedItem}
                theme={theme}
                activeTab={activeTab}
              />
            ) : (
              <ChannelListWithPagination
                items={paginatedItems}
                onPlay={handlePlay}
                onToggleFavorite={toggleFavorite}
                favorites={favorites}
                selectedItem={selectedItem}
                theme={theme}
                activeTab={activeTab}
                onLoadMore={loadMoreItems}
                hasMore={currentPage * itemsPerPage < filteredItems.length}
                isLoading={isLoading.channels}
              />
            )}
          </main>
        </div>

        {isLoading.general && <LoadingSpinner theme={theme} />}
      </div>
    </ErrorBoundary>
  );
}

export default App;
