// src/components/ChannelListWithPagination.jsx
import React, { useState, useEffect, useCallback, useRef, useMemo } from 'react';
import { getItemName, getItemLogo, getStreamType } from '../utils/streamHelpers';
import './ChannelList.css';

const ChannelListWithPagination = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem, 
  theme,
  activeTab,
  onLoadMore,
  hasMore,
  isLoading 
}) => {
  const [displayedItems, setDisplayedItems] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const lastItemRef = useRef();
  
  const ITEMS_PER_PAGE = 50;

  // Reset kur ndryshojnÃ« items
  useEffect(() => {
    setDisplayedItems(items.slice(0, ITEMS_PER_PAGE));
    setPage(1);
  }, [items]);

  // Grupo items sipas kategorive
  const groupedItems = useMemo(() => {
    return displayedItems.reduce((groups, item) => {
      let category = item.groupTitle || 
                    item.category_name || 
                    item.category || 
                    item.tvg_group || 
                    item.group || 
                    'Pa Kategori';
      category = category.trim();
      
      if (!groups[category]) {
        groups[category] = [];
      }
      groups[category].push(item);
      return groups;
    }, {});
  }, [displayedItems]);

  // Intersection Observer pÃ«r infinite scroll
  useEffect(() => {
    if (loading || !hasMore) return;
    
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting && hasMore) {
          loadMore();
        }
      },
      { threshold: 0.5, rootMargin: '100px' }
    );
    
    if (lastItemRef.current) {
      observer.observe(lastItemRef.current);
    }
    
    return () => {
      if (lastItemRef.current) {
        observer.unobserve(lastItemRef.current);
      }
    };
  }, [loading, hasMore]);

  const loadMore = useCallback(() => {
    if (loading || !hasMore) return;
    
    setLoading(true);
    
    setTimeout(() => {
      const start = page * ITEMS_PER_PAGE;
      const end = start + ITEMS_PER_PAGE;
      const newItems = items.slice(start, end);
      
      if (newItems.length > 0) {
        setDisplayedItems(prev => [...prev, ...newItems]);
        setPage(prev => prev + 1);
      }
      
      setLoading(false);
      if (onLoadMore) onLoadMore();
    }, 300);
  }, [items, page, loading, hasMore, onLoadMore]);

  const handlePlayClick = useCallback((item) => {
    if (isPlaying) return;
    
    setIsPlaying(true);
    onPlay(item);
    
    setTimeout(() => setIsPlaying(false), 1000);
  }, [onPlay, isPlaying]);

  if (!items || items.length === 0) {
    return (
      <div className={`channel-list-empty theme-${theme}`}>
        <div className="empty-icon">ðŸ“º</div>
        <h3>Nuk ka kanale</h3>
        <p>Nuk u gjet asnjÃ« kanal nÃ« kÃ«tÃ« kategori</p>
      </div>
    );
  }

  return (
    <div className={`channel-list theme-${theme}`}>
      {Object.entries(groupedItems).map(([category, categoryItems]) => (
        <div key={category} className="channel-category-section">
          <div className="channel-category-header">
            <h3 className="channel-category-title">
              <span className="category-icon">
                {activeTab === 'live' ? 'ðŸ“º' : activeTab === 'movies' ? 'ðŸŽ¬' : 'ðŸ“º'}
              </span>
              {category}
              <span className="category-count">{categoryItems.length}</span>
            </h3>
          </div>
          
          <div className="channel-grid">
            {categoryItems.map((item, index) => {
              const itemId = item.stream_id || item.id || item.series_id || `item-${index}`;
              const isLastItem = index === categoryItems.length - 1;
              
              return (
                <ChannelCard
                  key={itemId}
                  item={item}
                  isLastItem={isLastItem}
                  lastItemRef={lastItemRef}
                  onPlay={handlePlayClick}
                  onToggleFavorite={onToggleFavorite}
                  isFavorite={favorites?.includes(itemId) || false}
                  isSelected={selectedItem?.stream_id === itemId || selectedItem?.id === itemId}
                  theme={theme}
                  activeTab={activeTab}
                  category={category}
                />
              );
            })}
          </div>
        </div>
      ))}
      
      {loading && (
        <div className="loading-more">
          <div className="loading-spinner"></div>
          <span>Duke ngarkuar mÃ« shumÃ« kanale...</span>
        </div>
      )}
      
      {!hasMore && displayedItems.length < items.length && (
        <button 
          className="load-more-btn"
          onClick={loadMore}
          disabled={loading}
        >
          Ngarko mÃ« shumÃ« ({items.length - displayedItems.length} kanale)
        </button>
      )}
      
      {isLoading && (
        <div className="loading-skeleton">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="channel-skeleton"></div>
          ))}
        </div>
      )}
    </div>
  );
};

// ChannelCard komponent
const ChannelCard = React.memo(({ 
  item, 
  isLastItem, 
  lastItemRef, 
  onPlay, 
  onToggleFavorite, 
  isFavorite, 
  isSelected, 
  theme, 
  activeTab, 
  category 
}) => {
  const itemId = item.stream_id || item.id || item.series_id;
  const itemName = getItemName(item);
  const itemLogo = getItemLogo(item);
  const streamType = getStreamType(item, activeTab);

  return (
    <div
      ref={isLastItem ? lastItemRef : null}
      className={`channel-card ${isSelected ? 'selected' : ''}`}
      onClick={() => onPlay(item)}
      role="button"
      tabIndex={0}
      onKeyDown={(e) => {
        if (e.key === 'Enter' || e.key === ' ') {
          e.preventDefault();
          onPlay(item);
        }
      }}
    >
      <div className="channel-card-image">
        <img
          src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`}
          alt={itemName}
          loading="lazy"
          onError={(e) => {
            e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`;
          }}
        />
        <div className="channel-card-badge">
          {streamType === 'live' ? 'LIVE' : 
           streamType === 'movie' ? 'FILM' : 
           streamType === 'series' ? 'SERIAL' : 'HD'}
        </div>
      </div>
      
      <div className="channel-card-content">
        <h4 className="channel-card-title">{itemName}</h4>
        <div className="channel-card-footer">
          <span className="channel-card-category">{category}</span>
          <button
            className={`channel-card-favorite ${isFavorite ? 'active' : ''}`}
            onClick={(e) => {
              e.stopPropagation();
              onToggleFavorite(itemId);
            }}
            aria-label={isFavorite ? 'Hiq nga favoritet' : 'Shto nÃ« favoritet'}
          >
            {isFavorite ? 'â˜…' : 'â˜†'}
          </button>
        </div>
      </div>
    </div>
  );
});

export default ChannelListWithPagination;
