// src/App.js
import { useState, useEffect } from "react";
import {
  Tv, Film, Layers, Heart, Folder, Settings, Search,
  Play, Pause, Volume2, Maximize, Clock, Home, 
  ChevronRight, ChevronLeft, X, Plus, Trash2, 
  Edit, Globe, Server, Star, ListVideo
} from "lucide-react";
import IPTVPlayer from "./components/IPTVPlayer";
import { fetchChannelsFromM3U } from "./services/iptvService";

export default function App() {
  const [section, setSection] = useState("live");
  const [isPlaying, setIsPlaying] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState(() => 
    JSON.parse(localStorage.getItem("iptvFavorites") || "[]")
  );
  const [playlists, setPlaylists] = useState(() => 
    JSON.parse(localStorage.getItem("iptvPlaylists") || "[]")
  );
  const [activePlaylistId, setActivePlaylistId] = useState(() => 
    localStorage.getItem("activePlaylistId") || null
  );
  const [channels, setChannels] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currentStream, setCurrentStream] = useState(null);
  const [currentChannel, setCurrentChannel] = useState(null);
  const [showPlaylistModal, setShowPlaylistModal] = useState(false);
  const [modalMode, setModalMode] = useState("add");
  const [currentPlaylist, setCurrentPlaylist] = useState({
    id: null, name: "", type: "m3u", url: "", 
    username: "", password: "", serverUrl: ""
  });

  // Toggle favorite
  const toggleFavorite = (channelId) => {
    const newFavorites = favorites.includes(channelId)
      ? favorites.filter(id => id !== channelId)
      : [...favorites, channelId];
    setFavorites(newFavorites);
    localStorage.setItem("iptvFavorites", JSON.stringify(newFavorites));
  };

  // Load channels from playlist
  const loadChannels = async (playlistId) => {
    const playlist = playlists.find(p => p.id === playlistId);
    if (!playlist) return;
    
    setIsLoading(true);
    setCurrentStream(null);
    setCurrentChannel(null);

    try {
      let m3uUrl = playlist.url;
      if (playlist.type === "xtream") {
        const cleanServer = playlist.serverUrl.replace(/\/$/, '');
        m3uUrl = `${cleanServer}/get.php?username=${playlist.username}&password=${playlist.password}&type=m3u_plus`;
      }

      const fetchedChannels = await fetchChannelsFromM3U(m3uUrl);
      const validChannels = fetchedChannels.filter(ch => ch.streamUrl?.trim() !== '');
      setChannels(validChannels);
      setActivePlaylistId(playlistId);
      localStorage.setItem("activePlaylistId", playlistId);

      if (validChannels.length > 0) {
        setCurrentStream(validChannels[0].streamUrl);
        setCurrentChannel(validChannels[0]);
        setIsPlaying(true);
      }
    } catch (error) {
      console.error("Gabim gjatë ngarkimit:", error);
      alert(`Gabim: ${error.message}\nKontrollo URL-në/llogarinë dhe internetin`);
    } finally {
      setIsLoading(false);
    }
  };

  // Change channel
  const changeChannel = (channel) => {
    setCurrentStream(channel.streamUrl);
    setCurrentChannel(channel);
    setIsPlaying(true);
  };

  // Save playlist
  const savePlaylist = () => {
    if (!currentPlaylist.name.trim()) return alert("Vendos emër për listën!");
    if (currentPlaylist.type === "m3u" && !currentPlaylist.url.trim()) 
      return alert("Vendos URL për listën M3U!");
    if (currentPlaylist.type === "xtream" && 
        (!currentPlaylist.serverUrl || !currentPlaylist.username || !currentPlaylist.password)) 
      return alert("Plotëso të gjitha fushat për Xtream!");

    let newPlaylists = [...playlists];
    if (modalMode === "add") {
      const newId = Date.now().toString();
      newPlaylists.push({ 
        ...currentPlaylist, 
        id: newId, 
        createdAt: new Date().toISOString() 
      });
    } else {
      newPlaylists = newPlaylists.map(p => 
        p.id === currentPlaylist.id ? { ...currentPlaylist } : p
      );
    }

    setPlaylists(newPlaylists);
    localStorage.setItem("iptvPlaylists", JSON.stringify(newPlaylists));

    if (modalMode === "add" && newPlaylists.length === 1) {
      loadChannels(newPlaylists[0].id);
    }

    setShowPlaylistModal(false);
    setCurrentPlaylist({ 
      id: null, name: "", type: "m3u", url: "", 
      username: "", password: "", serverUrl: "" 
    });
  };

  // Delete playlist
  const deletePlaylist = (playlistId, e) => {
    e?.stopPropagation();
    if (!confirm("Je i sigurtë që do ta fshish këtë listë?")) return;
    
    const newPlaylists = playlists.filter(p => p.id !== playlistId);
    setPlaylists(newPlaylists);
    localStorage.setItem("iptvPlaylists", JSON.stringify(newPlaylists));

    if (activePlaylistId === playlistId) {
      setActivePlaylistId(null);
      setChannels([]);
      setCurrentStream(null);
      setCurrentChannel(null);
      localStorage.removeItem("activePlaylistId");
    }
  };

  // Edit playlist
  const editPlaylist = (playlist, e) => {
    e?.stopPropagation();
    setCurrentPlaylist(playlist);
    setModalMode("edit");
    setShowPlaylistModal(true);
  };

  // Add new playlist
  const addNewPlaylist = () => {
    setCurrentPlaylist({ 
      id: null, name: "", type: "m3u", url: "", 
      username: "", password: "", serverUrl: "" 
    });
    setModalMode("add");
    setShowPlaylistModal(true);
  };

  // Load active playlist on mount
  useEffect(() => {
    if (activePlaylistId) {
      loadChannels(activePlaylistId);
    }
  }, []);

  const activePlaylist = playlists.find(p => p.id === activePlaylistId);

  // Filter channels based on section and search
  const filteredChannels = channels.filter(channel => {
    const matchesSearch = channel.name?.toLowerCase()
      .includes(searchQuery.toLowerCase());
    
    if (section === "fav") {
      return matchesSearch && favorites.includes(channel.streamUrl);
    }
    
    return matchesSearch;
  });

  return (
    <div className="flex h-screen bg-gradient-to-br from-[#0b0f14] to-[#1a1f2e] text-white">
      {/* Sidebar */}
      <aside className="w-64 bg-gradient-to-b from-[#111827] to-[#1f2937] flex flex-col p-4 gap-2 border-r border-gray-800">
        <div className="flex items-center gap-3 mb-6 p-2">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-600 to-purple-600 rounded-xl flex items-center justify-center">
            <Tv size={24} />
          </div>
          <h1 className="text-xl font-bold">IPTV Player</h1>
        </div>
        
        <NavItem 
          icon={<Tv />} 
          label="Live TV" 
          active={section === "live"} 
          onClick={() => setSection("live")} 
        />
        <NavItem 
          icon={<Film />} 
          label="Filma" 
          active={section === "movies"} 
          onClick={() => setSection("movies")} 
        />
        <NavItem 
          icon={<Layers />} 
          label="Seriale" 
          active={section === "series"} 
          onClick={() => setSection("series")} 
        />
        <NavItem 
          icon={<Heart />} 
          label="Të preferuarat" 
          active={section === "fav"} 
          onClick={() => setSection("fav")} 
          badge={favorites.length} 
        />
        <NavItem 
          icon={<Folder />} 
          label="Listat" 
          active={section === "lists"} 
          onClick={() => setSection("lists")} 
          badge={playlists.length} 
        />
        
        <div className="mt-auto pt-4 border-t border-gray-800">
          <NavItem 
            icon={<Settings />} 
            label="Cilësimet" 
            active={section === "settings"} 
            onClick={() => setSection("settings")} 
          />
        </div>
      </aside>

      {/* Main content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        <header className="p-4 border-b border-gray-800 bg-gradient-to-r from-[#111827]/80 to-[#1f2937]/80 backdrop-blur-sm">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="relative w-96">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Kërko kanale..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 bg-gray-900/50 border border-gray-700 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>
            <div className="flex items-center gap-4">
              {activePlaylist && (
                <div className="flex items-center gap-2 bg-gray-800/50 px-3 py-1.5 rounded-lg">
                  <div className={`p-1.5 rounded-full ${activePlaylist.type === 'm3u' ? 'bg-blue-500/20' : 'bg-green-500/20'}`}>
                    {activePlaylist.type === 'm3u' ? <Globe size={14} /> : <Server size={14} />}
                  </div>
                  <span className="text-green-400 font-medium">{activePlaylist.name}</span>
                  <span className="text-gray-400 text-sm">({channels.length} kanale)</span>
                </div>
              )}
            </div>
          </div>
        </header>

        <main className="flex-1 p-6 overflow-y-auto">
          {section === "live" && (
            <LiveTVContent
              channels={filteredChannels}
              favorites={favorites}
              toggleFavorite={toggleFavorite}
              currentStream={currentStream}
              changeChannel={changeChannel}
              isLoading={isLoading}
              isPlaying={isPlaying}
              setIsPlaying={setIsPlaying}
              loadChannels={loadChannels}
              playlists={playlists}
              activePlaylist={activePlaylist}
              setShowPlaylistModal={setShowPlaylistModal}
              setModalMode={setModalMode}
              addNewPlaylist={addNewPlaylist}
            />
          )}
          
          {section === "movies" && (
            <MoviesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
            />
          )}
          
          {section === "series" && (
            <SeriesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
            />
          )}
          
          {section === "fav" && (
            <FavoritesContent 
              favorites={favorites} 
              toggleFavorite={toggleFavorite} 
              channels={channels}
              changeChannel={changeChannel}
            />
          )}
          
          {section === "lists" && (
            <PlaylistsContent 
              playlists={playlists} 
              activePlaylistId={activePlaylistId} 
              loadChannels={loadChannels} 
              deletePlaylist={deletePlaylist} 
              editPlaylist={editPlaylist}
              addNewPlaylist={addNewPlaylist}
            />
          )}
          
          {section === "settings" && (
            <SettingsContent 
              playlists={playlists} 
              activePlaylistId={activePlaylistId} 
              loadChannels={loadChannels} 
              favorites={favorites}
              setFavorites={setFavorites}
            />
          )}
        </main>

        {/* Player section */}
        <div className="border-t border-gray-800 bg-[#111827]">
          {currentStream ? (
            <div className="h-64">
              <IPTVPlayer
                streamUrl={currentStream}
                isPlaying={isPlaying}
                onPlay={() => setIsPlaying(true)}
                onPause={() => setIsPlaying(false)}
              />
            </div>
          ) : (
            <div className="p-6 text-center text-gray-400">
              <Tv size={48} className="mx-auto mb-3 opacity-50" />
              <p>Zgjidh një kanal për të filluar shikimin</p>
            </div>
          )}
          
          {/* Player controls */}
          <div className="p-4 bg-[#0d1117]">
            <div className="flex items-center justify-between max-w-4xl mx-auto">
              <div className="flex items-center gap-4">
                {currentChannel && (
                  <>
                    <div className="w-12 h-12 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                      {currentChannel.logo ? (
                        <img src={currentChannel.logo} alt="Logo" className="w-10 h-10 rounded" />
                      ) : (
                        <Tv size={20} />
                      )}
                    </div>
                    <div>
                      <div className="font-medium">{currentChannel.name}</div>
                      <div className="text-sm text-gray-400">Live TV</div>
                    </div>
                  </>
                )}
              </div>
              
              <div className="flex items-center gap-4">
                <button 
                  onClick={() => setIsPlaying(!isPlaying)} 
                  className="p-3 bg-blue-600 hover:bg-blue-700 rounded-xl transition"
                >
                  {isPlaying ? <Pause size={20} /> : <Play size={20} />}
                </button>
                
                {currentChannel && (
                  <button 
                    onClick={() => toggleFavorite(currentChannel.streamUrl)}
                    className={`p-2 rounded-lg transition ${favorites.includes(currentChannel.streamUrl) ? 'text-red-500 bg-red-500/10' : 'text-gray-400 hover:bg-gray-800'}`}
                  >
                    <Heart size={20} fill={favorites.includes(currentChannel.streamUrl) ? 'currentColor' : 'none'} />
                  </button>
                )}
              </div>
              
              <div className="flex items-center gap-4">
                <button className="p-2 hover:bg-gray-800 rounded-lg transition">
                  <Volume2 size={20} />
                </button>
                <button className="p-2 hover:bg-gray-800 rounded-lg transition">
                  <Maximize size={20} />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Playlist Modal */}
      {showPlaylistModal && (
        <PlaylistModal
          currentPlaylist={currentPlaylist}
          setCurrentPlaylist={setCurrentPlaylist}
          modalMode={modalMode}
          savePlaylist={savePlaylist}
          closeModal={() => {
            setShowPlaylistModal(false);
            setCurrentPlaylist({ 
              id: null, name: "", type: "m3u", url: "", 
              username: "", password: "", serverUrl: "" 
            });
          }}
        />
      )}
    </div>
  );
}

// ---------- Helper Components ----------

function NavItem({ icon, label, active, onClick, badge }) {
  return (
    <button
      className={`flex items-center gap-3 p-3 w-full text-left rounded-lg transition ${active ? "bg-blue-600/30 text-blue-400" : "hover:bg-gray-800 text-gray-300"}`}
      onClick={onClick}
    >
      <div className={active ? "text-blue-400" : "text-gray-400"}>
        {icon}
      </div>
      <span className="flex-1 font-medium">{label}</span>
      {badge > 0 && (
        <span className="text-xs bg-red-600 rounded-full px-2 py-0.5">
          {badge}
        </span>
      )}
    </button>
  );
}

// ---------- Live TV Content Component ----------
function LiveTVContent({ 
  channels, 
  favorites, 
  toggleFavorite, 
  changeChannel, 
  isLoading, 
  playlists,
  activePlaylist,
  addNewPlaylist 
}) {
  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-400">Duke ngarkuar kanalet...</p>
        </div>
      </div>
    );
  }

  if (playlists.length === 0) {
    return (
      <div className="text-center py-12">
        <Folder size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Nuk ka lista të shtuara</h3>
        <p className="text-gray-400 mb-6">Shto një listë IPTV për të filluar</p>
        <button
          onClick={addNewPlaylist}
          className="px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-xl font-medium flex items-center gap-2 mx-auto"
        >
          <Plus size={20} />
          Shto Listë të Re
        </button>
      </div>
    );
  }

  if (!activePlaylist) {
    return (
      <div className="text-center py-12">
        <ListVideo size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Zgjidh një listë</h3>
        <p className="text-gray-400">Shko te seksioni "Listat" për të zgjedhur një listë aktive</p>
      </div>
    );
  }

  if (channels.length === 0) {
    return (
      <div className="text-center py-12">
        <Tv size={64} className="mx-auto mb-4 text-gray-600" />
        <h3 className="text-xl font-bold mb-2">Nuk u gjetën kanale</h3>
        <p className="text-gray-400">Lista nuk përmban kanale ose ka ndodhur një gabim</p>
      </div>
    );
  }

  return (
    <div>
      <div className="mb-6">
        <h2 className="text-2xl font-bold mb-2">Kanale Live</h2>
        <p className="text-gray-400">{channels.length} kanale të disponueshme</p>
      </div>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
        {channels.map((channel) => (
          <div
            key={channel.streamUrl}
            className="bg-gray-800/50 rounded-xl overflow-hidden border border-gray-700 hover:border-blue-500/50 hover:bg-gray-800 transition-all cursor-pointer group"
            onClick={() => changeChannel(channel)}
          >
            <div className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-500/20 to-purple-600/20 rounded-lg flex items-center justify-center">
                  {channel.logo ? (
                    <img src={channel.logo} alt={channel.name} className="w-10 h-10 rounded" />
                  ) : (
                    <Tv size={20} className="text-blue-400" />
                  )}
                </div>
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    toggleFavorite(channel.streamUrl);
                  }}
                  className="p-1.5 rounded-full hover:bg-gray-700/50 transition"
                >
                  <Heart
                    size={18}
                    className={`${favorites.includes(channel.streamUrl) ? 'text-red-500 fill-red-500' : 'text-gray-400'}`}
                  />
                </button>
              </div>
              
              <h3 className="font-medium truncate">{channel.name}</h3>
              {channel.group && (
                <p className="text-sm text-gray-400 mt-1">{channel.group}</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

// ---------- Other Content Components (placeholder) ----------
function MoviesContent() { 
  return (
    <div className="text-center py-12">
      <Film size={64} className="mx-auto mb-4 text-gray-600" />
      <h2 className="text-2xl font-bold mb-2">Filma</h2>
      <p className="text-gray-400">Seksioni i filmave do të jetë në dispozicion së shpejti</p>
    </div>
  );
}

function SeriesContent() { 
  return (
    <div className="text-center py-12">
      <Layers size={64} className="mx-auto mb-4 text-gray-600" />
      <h2 className="text-2xl font-bold mb-2">Seriale</h2>
      <p className="text-gray-400">Seksioni i serialeve do të jetë në dispozicion së shpejti</p>
    </div>
  );
}

function FavoritesContent({ favorites, channels, changeChannel }) {
  const favoriteChannels = channels.filter(ch => favorites.includes(ch.streamUrl));
  
  if (favorites.length === 0) {
    return (
      <div className="text-center py-12">
        <Heart size={64} className="mx-auto mb-4 text-gray-600" />
        <h2 className="text-2xl font-bold mb-2">Nuk ka të preferuara</h2>
        <p className="text-gray-400">Shto kanale në të preferuarat duke klikuar ikonën e zemrës</p>
      </div>
    );
  }
  
  if (favoriteChannels.length === 0) {
    return (
      <div className="text-center py-12">
        <Heart size={64} className="mx-auto mb-4 text-gray-600" />
        <h2 className="text-2xl font-bold mb-2">Të preferuarat nuk janë në listën aktive</h2>
        <p className="text-gray-400">Ngarko listën që përmban kanalet e tua të preferuara</p>
      </div>
    );
  }
  
  return (
    <div>
      <div className="mb-6">
        <h2 className="text-2xl font-bold mb-2">Kanale të Preferuara</h2>
        <p className="text-gray-400">{favoriteChannels.length} kanale të preferuara</p>
      </div>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-5 gap-4">
        {favoriteChannels.map((channel) => (
          <div
            key={channel.streamUrl}
            className="bg-gray-800/50 rounded-xl overflow-hidden border-2 border-red-500/20 hover:border-red-500/40 hover:bg-gray-800 transition-all cursor-pointer group"
            onClick={() => changeChannel(channel)}
          >
            <div className="p-4">
              <div className="flex items-start justify-between mb-3">
                <div className="w-12 h-12 bg-gradient-to-br from-red-500/20 to-pink-600/20 rounded-lg flex items-center justify-center">
                  {channel.logo ? (
                    <img src={channel.logo} alt={channel.name} className="w-10 h-10 rounded" />
                  ) : (
                    <Tv size={20} className="text-red-400" />
                  )}
                </div>
                <div className="p-1.5 rounded-full bg-red-500/20">
                  <Heart size={18} className="text-red-500 fill-red-500" />
                </div>
              </div>
              
              <h3 className="font-medium truncate">{channel.name}</h3>
              {channel.group && (
                <p className="text-sm text-gray-400 mt-1">{channel.group}</p>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

function PlaylistsContent({ 
  playlists, 
  activePlaylistId, 
  loadChannels, 
  deletePlaylist, 
  editPlaylist,
  addNewPlaylist 
}) {
  return (
    <div>
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold mb-2">Listat IPTV</h2>
          <p className="text-gray-400">Menaxho listat tuaja të kanaleve</p>
        </div>
        <button
          onClick={addNewPlaylist}
          className="px-5 py-2.5 bg-blue-600 hover:bg-blue-700 rounded-xl font-medium flex items-center gap-2"
        >
          <Plus size={20} />
          Shto Listë të Re
        </button>
      </div>
      
      {playlists.length === 0 ? (
        <div className="text-center py-12">
          <Folder size={64} className="mx-auto mb-4 text-gray-600" />
          <h3 className="text-xl font-bold mb-2">Nuk ka lista të shtuara</h3>
          <p className="text-gray-400 mb-6">Shto listën tuaj të parë IPTV</p>
          <button
            onClick={addNewPlaylist}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 rounded-xl font-medium flex items-center gap-2 mx-auto"
          >
            <Plus size={20} />
            Shto Listë të Re
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {playlists.map((playlist) => (
            <div
              key={playlist.id}
              className={`bg-gray-800/50 rounded-xl p-5 border-2 transition-all ${activePlaylistId === playlist.id ? 'border-blue-500 bg-blue-500/10' : 'border-gray-700 hover:border-gray-600 hover:bg-gray-800/70'}`}
            >
              <div className="flex items-start justify-between mb-4">
                <div className={`p-3 rounded-lg ${playlist.type === 'm3u' ? 'bg-blue-500/20' : 'bg-green-500/20'}`}>
                  {playlist.type === 'm3u' ? <Globe size={24} /> : <Server size={24} />}
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={(e) => editPlaylist(playlist, e)}
                    className="p-2 hover:bg-gray-700 rounded-lg transition"
                    title="Edito"
                  >
                    <Edit size={16} />
                  </button>
                  <button
                    onClick={(e) => deletePlaylist(playlist.id, e)}
                    className="p-2 hover:bg-red-500/20 hover:text-red-400 rounded-lg transition"
                    title="Fshi"
                  >
                    <Trash2 size={16} />
                  </button>
                </div>
              </div>
              
              <h3 className="font-bold text-lg mb-1">{playlist.name}</h3>
              <p className="text-sm text-gray-400 mb-2">
                {playlist.type === 'm3u' ? 'M3U Playlist' : 'Xtream Codes'}
              </p>
              
              {playlist.type === 'm3u' ? (
                <p className="text-sm text-gray-300 truncate mb-4" title={playlist.url}>
                  {playlist.url.substring(0, 40)}...
                </p>
              ) : (
                <div className="text-sm text-gray-300 mb-4">
                  <p>Server: {playlist.serverUrl}</p>
                  <p>User: {playlist.username}</p>
                </div>
              )}
              
              <div className="flex items-center justify-between">
                <span className="text-xs text-gray-400">
                  Shtuar: {new Date(playlist.createdAt).toLocaleDateString()}
                </span>
                <button
                  onClick={() => loadChannels(playlist.id)}
                  className={`px-4 py-1.5 rounded-lg font-medium ${activePlaylistId === playlist.id ? 'bg-blue-600' : 'bg-gray-700 hover:bg-gray-600'}`}
                >
                  {activePlaylistId === playlist.id ? 'Aktive' : 'Aktivizo'}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

function SettingsContent({ favorites, setFavorites }) {
  const clearFavorites = () => {
    if (confirm("Je i sigurtë që do t'i fshish të gjitha të preferuarat?")) {
      setFavorites([]);
      localStorage.removeItem("iptvFavorites");
    }
  };
  
  const clearAllData = () => {
    if (confirm("Kjo do të fshijë të gjitha të dhënat (listat dhe të preferuarat). Je i sigurtë?")) {
      localStorage.clear();
      window.location.reload();
    }
  };
  
  return (
    <div className="max-w-3xl">
      <h2 className="text-2xl font-bold mb-6">Cilësime</h2>
      
      <div className="space-y-6">
        <div className="bg-gray-800/50 rounded-xl p-5">
          <h3 className="font-bold text-lg mb-4">Të Dhënat</h3>
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="font-medium">Të preferuarat</p>
                <p className="text-sm text-gray-400">{favorites.length} kanale</p>
              </div>
              <button
                onClick={clearFavorites}
                className="px-4 py-2 bg-red-500/20 text-red-400 hover:bg-red-500/30 rounded-lg font-medium"
                disabled={favorites.length === 0}
              >
                Fshi të gjitha
              </button>
            </div>
          </div>
        </div>
        
        <div className="bg-gray-800/50 rounded-xl p-5">
          <h3 className="font-bold text-lg mb-4">Rreth aplikacionit</h3>
          <div className="space-y-3 text-gray-300">
            <p><strong>IPTV Player</strong> - Version 1.0.0</p>
            <p>Një aplikacion për shikimin e kanaleve IPTV</p>
            <p className="text-sm text-gray-400">Zhvilluar me React dhe Tailwind CSS</p>
          </div>
        </div>
        
        <div className="bg-gray-800/50 rounded-xl p-5 border-2 border-red-500/20">
          <h3 className="font-bold text-lg mb-4 text-red-400">Zona e rrezikshme</h3>
          <p className="text-gray-300 mb-4">Këto veprime nuk mund të zhbëhen</p>
          <button
            onClick={clearAllData}
            className="px-4 py-2.5 bg-red-600 hover:bg-red-700 rounded-lg font-medium w-full"
          >
            Fshi të gjitha të dhënat
          </button>
        </div>
      </div>
    </div>
  );
}

function PlaylistModal({ currentPlaylist, setCurrentPlaylist, modalMode, savePlaylist, closeModal }) {
  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50 p-4">
      <div className="bg-gradient-to-b from-gray-900 to-gray-950 p-6 rounded-xl w-full max-w-md border border-gray-800">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-xl font-bold">
            {modalMode === "add" ? "Shto Listë të Re" : "Edito Listë"}
          </h2>
          <button
            onClick={closeModal}
            className="p-1.5 hover:bg-gray-800 rounded-lg transition"
          >
            <X size={20} />
          </button>
        </div>
        
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Emri i Listës *</label>
            <input
              type="text"
              placeholder="P.sh. Lista ime IPTV"
              className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-blue-500 focus:outline-none"
              value={currentPlaylist.name}
              onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, name: e.target.value })}
            />
          </div>
          
          <div>
            <label className="block text-sm font-medium mb-2">Lloji i Listës</label>
            <div className="flex gap-4">
              <button
                type="button"
                onClick={() => setCurrentPlaylist({ ...currentPlaylist, type: "m3u" })}
                className={`flex-1 p-3 rounded-lg border ${currentPlaylist.type === "m3u" ? 'border-blue-500 bg-blue-500/10' : 'border-gray-700 bg-gray-800 hover:border-gray-600'}`}
              >
                <div className="flex items-center gap-2 justify-center">
                  <Globe size={18} />
                  <span>M3U URL</span>
                </div>
              </button>
              <button
                type="button"
                onClick={() => setCurrentPlaylist({ ...currentPlaylist, type: "xtream" })}
                className={`flex-1 p-3 rounded-lg border ${currentPlaylist.type === "xtream" ? 'border-green-500 bg-green-500/10' : 'border-gray-700 bg-gray-800 hover:border-gray-600'}`}
              >
                <div className="flex items-center gap-2 justify-center">
                  <Server size={18} />
                  <span>Xtream</span>
                </div>
              </button>
            </div>
          </div>
          
          {currentPlaylist.type === "m3u" ? (
            <div>
              <label className="block text-sm font-medium mb-2">URL M3U *</label>
              <input
                type="text"
                placeholder="https://example.com/playlist.m3u"
                className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-blue-500 focus:outline-none"
                value={currentPlaylist.url}
                onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, url: e.target.value })}
              />
            </div>
          ) : (
            <>
              <div>
                <label className="block text-sm font-medium mb-2">URL Serveri *</label>
                <input
                  type="text"
                  placeholder="http://server.example.com:8080"
                  className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none"
                  value={currentPlaylist.serverUrl}
                  onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, serverUrl: e.target.value })}
                />
              </div>
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">Username *</label>
                  <input
                    type="text"
                    placeholder="përdoruesi"
                    className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none"
                    value={currentPlaylist.username}
                    onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, username: e.target.value })}
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium mb-2">Password *</label>
                  <input
                    type="password"
                    placeholder="••••••••"
                    className="w-full p-3 rounded-lg bg-gray-800 border border-gray-700 focus:border-green-500 focus:outline-none"
                    value={currentPlaylist.password}
                    onChange={(e) => setCurrentPlaylist({ ...currentPlaylist, password: e.target.value })}
                  />
                </div>
              </div>
            </>
          )}
        </div>
        
        <div className="flex justify-end gap-3 mt-8 pt-6 border-t border-gray-800">
          <button
            onClick={closeModal}
            className="px-5 py-2.5 bg-gray-800 hover:bg-gray-700 rounded-lg font-medium"
          >
            Anulo
          </button>
          <button
            onClick={savePlaylist}
            className={`px-5 py-2.5 rounded-lg font-medium ${modalMode === "add" ? 'bg-blue-600 hover:bg-blue-700' : 'bg-green-600 hover:bg-green-700'}`}
          >
            {modalMode === "add" ? "Shto Listë" : "Ruaj Ndryshimet"}
          </button>
        </div>
      </div>
    </div>
  );
}
