import React from "react";

function LiveTVContent({
  channels = [],            // ✅ default array
  selectedCategory = "Të gjitha",
  searchQuery = "",
  onSelect,
  favorites = []
}) {
  // ✅ siguri absolute
  const safeChannels = Array.isArray(channels) ? channels : [];
  const safeSearch = (searchQuery || "").toLowerCase();
  const safeCategory = selectedCategory || "Të gjitha";

  const filteredChannels = safeChannels.filter((ch) => {
    const name = (ch?.name || "").toLowerCase();
    const category = (ch?.category || "").toLowerCase();

    const matchCategory =
      safeCategory === "Të gjitha" ||
      category === safeCategory.toLowerCase();

    const matchSearch =
      !safeSearch ||
      name.includes(safeSearch) ||
      category.includes(safeSearch);

    return matchCategory && matchSearch;
  });

  if (filteredChannels.length === 0) {
    return (
      <div className="text-gray-400 text-center py-10">
        Nuk u gjetën kanale
      </div>
    );
  }

  return (
    <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4">
      {filteredChannels.map((ch, index) => (
        <div
          key={ch.stream_id || index}
          onClick={() => onSelect && onSelect(ch)}
          className="bg-gray-800 rounded p-2 cursor-pointer hover:bg-gray-700"
        >
          <img
            src={ch.logo}
            alt={ch.name}
            className="w-full h-28 object-cover rounded"
            onError={(e) => {
              e.target.src =
                "https://ui-avatars.com/api/?name=TV&size=128";
            }}
          />
          <div className="mt-2 text-sm font-semibold truncate">
            {ch.name || "Pa emër"}
          </div>

          {favorites.includes(ch.stream_id) && (
            <div className="text-yellow-400 text-xs">★ Favorite</div>
          )}
        </div>
      ))}
    </div>
  );
}

export default LiveTVContent;
