import React, { useState, useEffect, useMemo, useCallback } from "react";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import XtreamLogin from "./components/XtreamLogin";
import M3UUploader from "./components/M3UUploader";
import SearchModal from "./components/SearchModal";
import EPGInfo from "./components/EPGInfo";
import WelcomeScreen from "./components/WelcomeScreen";
import ChannelList from "./components/ChannelList";
import { useIPTVData } from "./hooks/useIPTVData";
import { useTheme } from "./hooks/useTheme";
import { useErrorHandler } from "./hooks/useErrorHandler";
import { 
  xtreamLogin, 
  getAllContent, 
  getAllCategories, 
  buildStreamUrl,
  getEPG,
  fetchM3UFromUrl,
  parseM3U
} from './services/xtream';
import { TABS, SOURCE_TYPES, STREAM_TYPES, APP_CONSTANTS } from './constants/iptv';
import "./App.css";

function App() {
  const { theme, toggleTheme } = useTheme();
  const { error, success, handleError, handleSuccess, clearMessages } = useErrorHandler();
  
  const {
    channels, setChannels,
    movies, setMovies,
    series, setSeries,
    categories, setCategories,
    safeChannels, safeMovies, safeSeries,
    clearData
  } = useIPTVData();

  const [xtreamCredentials, setXtreamCredentials] = useState({
    server: "",
    username: "",
    password: "",
    isLoggedIn: false,
    expires: null,
    activeConnections: null,
    maxConnections: null,
    auth: null
  });

  const [m3uUrl, setM3uUrl] = useState("");
  const [m3uActive, setM3uActive] = useState(false);
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playerError, setPlayerError] = useState(null);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);
  const [epgData, setEpgData] = useState([]);
  const [currentEpg, setCurrentEpg] = useState(null);
  const [activeTab, setActiveTab] = useState(TABS.LIVE);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showXtreamLogin, setShowXtreamLogin] = useState(false);
  const [showM3UModal, setShowM3UModal] = useState(true);
  const [sourceType, setSourceType] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState(APP_CONSTANTS.DEFAULT_CATEGORY);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState([]);
  const [recentItems, setRecentItems] = useState([]);
  const [isLoading, setIsLoading] = useState({
    channels: false,
    movies: false,
    series: false,
    general: false
  });

  useEffect(() => {
    setShowM3UModal(true);
  }, []);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (e.ctrlKey && e.key === 'k') {
        e.preventDefault();
        setShowSearchModal(true);
      }
    };
    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, []);

  const currentCategories = useMemo(() => {
    const categoriesSet = new Set();
    categoriesSet.add(APP_CONSTANTS.DEFAULT_CATEGORY);
    
    let items = [];
    if (activeTab === TABS.LIVE) items = safeChannels;
    else if (activeTab === TABS.MOVIES) items = safeMovies;
    else if (activeTab === TABS.SERIES) items = safeSeries;
    
    items.forEach(item => {
      const catName = item.category || item.category_name || item.genre || 
        (activeTab === TABS.LIVE ? 'TÃ« tjera' : activeTab === TABS.MOVIES ? 'Filma' : 'Seriale');
      if (catName && catName.trim() !== '') {
        categoriesSet.add(catName);
      }
    });
    
    return Array.from(categoriesSet);
  }, [activeTab, safeChannels, safeMovies, safeSeries]);

  const filteredItems = useMemo(() => {
    let items = [];
    if (activeTab === TABS.LIVE) items = safeChannels;
    else if (activeTab === TABS.MOVIES) items = safeMovies;
    else if (activeTab === TABS.SERIES) items = safeSeries;
    
    if (!items.length) return [];
    if (selectedCategory === APP_CONSTANTS.DEFAULT_CATEGORY) return items;
    
    return items.filter(item => {
      const itemCategory = item.category || item.category_name || item.genre || 
        (activeTab === TABS.LIVE ? 'TÃ« tjera' : activeTab === TABS.MOVIES ? 'Filma' : 'Seriale');
      return itemCategory.trim() === selectedCategory;
    });
  }, [activeTab, selectedCategory, safeChannels, safeMovies, safeSeries]);

  const searchedItems = useMemo(() => {
    if (!searchQuery) return filteredItems;
    const q = searchQuery.toLowerCase();
    return filteredItems.filter(item => 
      item.name?.toLowerCase().includes(q) ||
      item.title?.toLowerCase().includes(q)
    );
  }, [filteredItems, searchQuery]);

  const handleXtreamLogin = useCallback(async (credentials) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    clearMessages();
    
    try {
      const login = await xtreamLogin(credentials.server, credentials.username, credentials.password);
      
      if (!login.success) {
        throw new Error(login.error || 'Kredencialet e gabuara');
      }

      const [content, allCategories] = await Promise.all([
        getAllContent(credentials.server, credentials.username, credentials.password),
        getAllCategories(credentials.server, credentials.username, credentials.password)
      ]);

      const liveWithUrls = content.live.map(ch => ({
        ...ch,
        stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, ch.stream_id, STREAM_TYPES.LIVE),
        type: STREAM_TYPES.LIVE
      }));

      const moviesWithUrls = content.movies.map(m => ({
        ...m,
        stream_url: buildStreamUrl(credentials.server, credentials.username, credentials.password, m.stream_id, STREAM_TYPES.MOVIE, m.container_extension),
        type: STREAM_TYPES.MOVIE
      }));

      setChannels(liveWithUrls);
      setMovies(moviesWithUrls);
      setSeries(content.series);
      setCategories(allCategories);
      
      setXtreamCredentials({ 
        ...credentials, 
        isLoggedIn: true,
        auth: login.data
      });
      
      setSourceType(SOURCE_TYPES.XTREAM);
      setShowXtreamLogin(false);
      setShowM3UModal(false);
      setActiveTab(TABS.LIVE);
      setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
      
      handleSuccess(`âœ… U lidhÃ«t! ðŸ“º ${liveWithUrls.length} kanale, ðŸŽ¬ ${moviesWithUrls.length} filma, ðŸ“º ${content.series.length} seriale`);
      
    } catch (err) {
      handleError(err, 'Nuk u lidh me serverin');
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, [setChannels, setMovies, setSeries, setCategories, handleSuccess, handleError, clearMessages]);

  const handleM3ULoad = useCallback(async (source) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    clearMessages();
    
    try {
      let channels = [];
      
      if (source.channels) {
        channels = source.channels;
      } else {
        let m3uText = '';
        
        if (source.type === 'file') {
          m3uText = source.content;
        } else {
          m3uText = await fetchM3UFromUrl(source.url);
        }
        
        channels = parseM3U(m3uText);
      }

      if (channels.length === 0) {
        throw new Error('Nuk u gjet asnjÃ« kanal nÃ« playlist');
      }

      setChannels(channels);
      setMovies([]);
      setSeries([]);
      setSourceType(SOURCE_TYPES.M3U);
      setShowM3UModal(false);
      setActiveTab(TABS.LIVE);
      setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
      handleSuccess(`âœ… ${channels.length} kanale u ngarkuan nga M3U`);
      
    } catch (err) {
      handleError(err, 'M3U gabim: ' + err.message);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, [setChannels, setMovies, setSeries, handleSuccess, handleError, clearMessages]);

  const handlePlay = useCallback(async (item) => {
    if (!item) return;

    try {
      let streamUrl = item.stream_url;
      
      if (sourceType === SOURCE_TYPES.XTREAM && xtreamCredentials.isLoggedIn) {
        const { server, username, password } = xtreamCredentials;
        
        if (item.type === STREAM_TYPES.LIVE || activeTab === TABS.LIVE) {
          streamUrl = buildStreamUrl(server, username, password, item.stream_id, STREAM_TYPES.LIVE);
        } else if (item.type === STREAM_TYPES.MOVIE || activeTab === TABS.MOVIES) {
          streamUrl = buildStreamUrl(server, username, password, item.stream_id, STREAM_TYPES.MOVIE, item.container_extension || 'mp4');
        } else if (item.type === STREAM_TYPES.SERIES || activeTab === TABS.SERIES) {
          streamUrl = buildStreamUrl(server, username, password, item.series_id || item.stream_id, STREAM_TYPES.SERIES);
        }
      }

      if (!streamUrl) {
        throw new Error('Stream URL nuk eshte i vlefshem');
      }

      setCurrentStream(streamUrl);
      setCurrentStreamInfo({
        id: item.stream_id || item.series_id,
        name: item.name || item.title,
        logo: item.logo || item.poster || item.cover,
        category: item.category || item.category_name || item.genre,
        type: item.type || activeTab
      });
      setSelectedItem(item);
      setIsPlaying(true);
      setIsPlayerVisible(true);
      
      setRecentItems(prev => {
        const filtered = prev.filter(i => 
          (i.stream_id || i.series_id) !== (item.stream_id || item.series_id)
        );
        return [item, ...filtered].slice(0, APP_CONSTANTS.MAX_RECENT_ITEMS);
      });

      if ((item.type === STREAM_TYPES.LIVE || activeTab === TABS.LIVE) && sourceType === SOURCE_TYPES.XTREAM) {
        try {
          const epg = await getEPG(
            xtreamCredentials.server,
            xtreamCredentials.username,
            xtreamCredentials.password,
            item.stream_id
          );
          setEpgData(epg);
          
          const now = Math.floor(Date.now() / 1000);
          const current = epg.find(p => 
            p.start_timestamp <= now && p.end_timestamp >= now
          );
          setCurrentEpg(current);
        } catch (err) {
          console.error('EPG error:', err);
        }
      }
      
    } catch (err) {
      setPlayerError(err.message);
      handleError(err, 'Gabim gjatÃ« luajtjes');
    }
  }, [sourceType, xtreamCredentials, activeTab, handleError]);

  const toggleFavorite = useCallback((id) => {
    setFavorites(prev => 
      prev.includes(id) 
        ? prev.filter(fid => fid !== id)
        : [...prev, id]
    );
  }, []);

  const handleLogout = useCallback(() => {
    clearData();
    setXtreamCredentials({ server: "", username: "", password: "", isLoggedIn: false });
    setM3uActive(false);
    setSourceType(null);
    setCurrentStream(null);
    setIsPlayerVisible(false);
    setShowM3UModal(true);
    setActiveTab(TABS.LIVE);
    setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
    setRecentItems([]);
    setEpgData([]);
    setCurrentEpg(null);
    setFavorites([]);
  }, [clearData]);

  const toggleSidebar = useCallback(() => {
    setIsSidebarOpen(prev => !prev);
  }, []);

  const handleEpgUpdate = useCallback((epg) => {
    setEpgData(epg);
    const now = Math.floor(Date.now() / 1000);
    const current = epg.find(p => 
      p.start_timestamp <= now && p.end_timestamp >= now
    );
    setCurrentEpg(current);
  }, []);

  if (!sourceType) {
    return (
      <ErrorBoundary>
        <WelcomeScreen
          theme={theme}
          showXtreamLogin={showXtreamLogin}
          showM3UModal={showM3UModal}
          onXtreamClick={() => {
            setShowXtreamLogin(true);
            setShowM3UModal(false);
          }}
          onM3UClick={() => {
            setShowM3UModal(true);
            setShowXtreamLogin(false);
          }}
          onXtreamLogin={handleXtreamLogin}
          onM3ULoad={handleM3ULoad}
          onCloseXtream={() => setShowXtreamLogin(false)}
          onCloseM3U={() => setShowM3UModal(false)}
          isLoading={isLoading.general}
        />
      </ErrorBoundary>
    );
  }

  return (
    <ErrorBoundary>
      <div className={`app theme-${theme}`} data-theme={theme}>
        {/* MODALS */}
        {showXtreamLogin && (
          <XtreamLogin
            onLogin={handleXtreamLogin}
            onClose={() => setShowXtreamLogin(false)}
            isLoading={isLoading.general}
            theme={theme}
          />
        )}

        {showM3UModal && (
          <M3UUploader
            onLoad={handleM3ULoad}
            onClose={() => setShowM3UModal(false)}
            isLoading={isLoading.general}
            theme={theme}
          />
        )}

        <SearchModal
          isOpen={showSearchModal}
          query={searchQuery}
          setQuery={setSearchQuery}
          results={searchedItems.slice(0, APP_CONSTANTS.MAX_SEARCH_RESULTS)}
          onClose={() => {
            setShowSearchModal(false);
            setSearchQuery("");
          }}
          onSelectStream={handlePlay}
          onToggleFavorite={toggleFavorite}
          favorites={favorites}
          theme={theme}
        />

        {/* TOAST MESSAGES */}
        {error && (
          <div className="error-toast">
            <span>âš ï¸ {error}</span>
            <button onClick={clearMessages}>Ã—</button>
          </div>
        )}

        {success && (
          <div className="success-toast">
            <span>âœ… {success}</span>
            <button onClick={clearMessages}>Ã—</button>
          </div>
        )}

        {/* HEADER */}
        <header className="smart-header">
          <div className="smart-header-left">
            <button className="smart-menu-btn" onClick={toggleSidebar}>
              {isSidebarOpen ? 'â—€' : 'â–¶'}
            </button>
            <h1 className="smart-logo">IPTV</h1>
            <div className="smart-source">
              {sourceType === SOURCE_TYPES.XTREAM && (
                <span className="smart-badge xtream">
                  <span className="badge-dot"></span>
                  {xtreamCredentials.username}
                </span>
              )}
              {sourceType === SOURCE_TYPES.M3U && (
                <span className="smart-badge m3u">
                  <span className="badge-dot"></span>
                  M3U â€¢ {safeChannels.length}
                </span>
              )}
            </div>
          </div>

          <div className="smart-header-center">
            <div className="smart-search" onClick={() => setShowSearchModal(true)}>
              <span className="smart-search-icon">ðŸ”</span>
              <span className="smart-search-text">
                KÃ«rko {activeTab === TABS.LIVE ? 'kanale' : activeTab === TABS.MOVIES ? 'filma' : 'seriale'}...
              </span>
              <span className="smart-search-hint">Ctrl+K</span>
            </div>
          </div>

          <div className="smart-header-right">
            <button className="smart-icon-btn theme-btn" onClick={toggleTheme} title={theme === 'dark' ? 'Light Mode' : 'Dark Mode'}>
              {theme === 'dark' ? 'â˜€ï¸' : 'ðŸŒ™'}
            </button>
            <button className="smart-icon-btn logout-btn" onClick={handleLogout} title="Ã‡kyÃ§u">
              <span className="icon">ðŸšª</span>
            </button>
          </div>
        </header>

        {/* TABS */}
        <div className="smart-tabs">
          {safeChannels.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === TABS.LIVE ? 'active' : ''}`}
              onClick={() => {
                setActiveTab(TABS.LIVE);
                setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
              }}
            >
              <span className="tab-icon">ðŸ“º</span>
              <span className="tab-text">Live TV</span>
              <span className="tab-count">{safeChannels.length}</span>
            </button>
          )}
          {safeMovies.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === TABS.MOVIES ? 'active' : ''}`}
              onClick={() => {
                setActiveTab(TABS.MOVIES);
                setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
              }}
            >
              <span className="tab-icon">ðŸŽ¬</span>
              <span className="tab-text">Filma</span>
              <span className="tab-count">{safeMovies.length}</span>
            </button>
          )}
          {safeSeries.length > 0 && (
            <button 
              className={`smart-tab ${activeTab === TABS.SERIES ? 'active' : ''}`}
              onClick={() => {
                setActiveTab(TABS.SERIES);
                setSelectedCategory(APP_CONSTANTS.DEFAULT_CATEGORY);
              }}
            >
              <span className="tab-icon">ðŸ“º</span>
              <span className="tab-text">Seriale</span>
              <span className="tab-count">{safeSeries.length}</span>
            </button>
          )}
        </div>

        {/* PLAYER SECTION */}
        {isPlayerVisible && currentStream && (
          <section className="smart-player-section">
            <div className="smart-player-container">
              <div className="smart-player-header">
                <div className="smart-now-playing">
                  <img 
                    src={currentStreamInfo?.logo || `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`}
                    alt={currentStreamInfo?.name}
                    className="smart-now-logo"
                    onError={(e) => {
                      e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(currentStreamInfo?.name || 'TV')}&size=48&background=${theme === 'dark' ? '1e293b' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '0f172a'}`;
                    }}
                  />
                  <div className="smart-now-info">
                    <span className="smart-now-label">
                      {currentStreamInfo?.type === STREAM_TYPES.LIVE ? 'LIVE' : 
                       currentStreamInfo?.type === STREAM_TYPES.MOVIE ? 'FILM' : 
                       currentStreamInfo?.type === STREAM_TYPES.SERIES ? 'SERIAL' : 'DUKE U LUATUR'}
                    </span>
                    <span className="smart-now-name">{currentStreamInfo?.name}</span>
                  </div>
                </div>
                <button className="smart-close-btn" onClick={() => setIsPlayerVisible(false)}>
                  âœ•
                </button>
              </div>
              
              <HlsPlayer
                src={currentStream}
                isPlaying={isPlaying}
                onPlayPause={() => setIsPlaying(!isPlaying)}
                onClose={() => setIsPlayerVisible(false)}
                onError={(err) => {
                  setPlayerError(err);
                  handleError(err, 'Gabim nÃ« player');
                }}
                theme={theme}
                currentStreamInfo={currentStreamInfo}
                currentEpg={currentEpg}
                epgData={epgData}
                onEpgUpdate={handleEpgUpdate}
              />
            </div>
          </section>
        )}

        {/* MAIN LAYOUT */}
        <div className="smart-layout">
          {/* SIDEBAR */}
          <aside className={`smart-sidebar ${isSidebarOpen ? 'open' : 'closed'}`}>
            <div className="smart-categories">
              <div className="smart-categories-header">
                <h3>KATEGORITÃ‹</h3>
                <span className="smart-category-count">
                  {currentCategories.length - 1}
                </span>
              </div>
              
              {currentCategories.length > 1 ? (
                <ul className="smart-category-list">
                  {currentCategories.map((category) => {
                    let count = 0;
                    if (activeTab === TABS.LIVE) {
                      count = category === APP_CONSTANTS.DEFAULT_CATEGORY 
                        ? safeChannels.length 
                        : safeChannels.filter(ch => 
                            (ch.category || ch.category_name || 'TÃ« tjera') === category
                          ).length;
                    } else if (activeTab === TABS.MOVIES) {
                      count = category === APP_CONSTANTS.DEFAULT_CATEGORY 
                        ? safeMovies.length 
                        : safeMovies.filter(m => 
                            (m.category || m.category_name || m.genre || 'Filma') === category
                          ).length;
                    } else if (activeTab === TABS.SERIES) {
                      count = category === APP_CONSTANTS.DEFAULT_CATEGORY 
                        ? safeSeries.length 
                        : safeSeries.filter(s => 
                            (s.category || s.category_name || s.genre || 'Seriale') === category
                          ).length;
                    }
                    
                    if (count === 0 && category !== APP_CONSTANTS.DEFAULT_CATEGORY) return null;
                    
                    return (
                      <li 
                        key={category}
                        className={`smart-category-item ${selectedCategory === category ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(category)}
                      >
                        <span className="smart-category-icon">
                          {category === APP_CONSTANTS.DEFAULT_CATEGORY ? 'ðŸ“‹' : 
                           activeTab === TABS.LIVE ? 'ðŸ“º' : 
                           activeTab === TABS.MOVIES ? 'ðŸŽ¬' : 'ðŸ“º'}
                        </span>
                        <span className="smart-category-name">
                          {category === APP_CONSTANTS.DEFAULT_CATEGORY 
                            ? activeTab === TABS.LIVE ? 'TÃ« gjitha kanalet' :
                              activeTab === TABS.MOVIES ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                            : category}
                        </span>
                        <span className="smart-category-badge">{count}</span>
                      </li>
                    );
                  })}
                </ul>
              ) : (
                <div className="smart-no-categories">
                  Nuk ka kategori
                </div>
              )}
            </div>

            <div className="smart-channels">
              <div className="smart-channels-header">
                <div className="smart-channels-title">
                  <span className="smart-channel-icon">
                    {activeTab === TABS.LIVE ? 'ðŸ“º' : activeTab === TABS.MOVIES ? 'ðŸŽ¬' : 'ðŸ“º'}
                  </span>
                  <span>
                    {selectedCategory === APP_CONSTANTS.DEFAULT_CATEGORY 
                      ? activeTab === TABS.LIVE ? 'TÃ« gjitha kanalet' :
                        activeTab === TABS.MOVIES ? 'TÃ« gjithÃ« filmat' : 'TÃ« gjitha serialet'
                      : selectedCategory}
                  </span>
                </div>
                <span className="smart-channel-count">{filteredItems.length}</span>
              </div>
              
              <ChannelList
                items={filteredItems}
                onPlay={handlePlay}
                onToggleFavorite={toggleFavorite}
                favorites={favorites}
                selectedItem={selectedItem}
                theme={theme}
                activeTab={activeTab}
              />
            </div>
            
           
            
          </aside>

          {/* CONTENT AREA */}
          <main className="smart-content"></main>
        </div>

        {/* LOADING */}
        {isLoading.general && (
          <div className="smart-loading">
            <div className="smart-spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
