// components/XtreamLogin.jsx
import React, { useState } from 'react';

function XtreamLogin({ onLogin, onClose, isLoading }) {
  const [credentials, setCredentials] = useState({
    server: '',
    username: '',
    password: ''
  });
  const [errors, setErrors] = useState({});

  const validateForm = () => {
    const newErrors = {};
    
    if (!credentials.server.trim()) {
      newErrors.server = 'Server URL është i detyrueshëm';
    } else if (!credentials.server.match(/^https?:\/\//)) {
      newErrors.server = 'URL duhet të fillojë me http:// ose https://';
    }
    
    if (!credentials.username.trim()) {
      newErrors.username = 'Username është i detyrueshëm';
    }
    
    if (!credentials.password.trim()) {
      newErrors.password = 'Password është i detyrueshëm';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      onLogin(credentials); // ✅ Thërret handleXtreamLogin nga App.js
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prev => ({
      ...prev,
      [name]: value
    }));
    // Fshij error-in kur fillon të shkruash
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>Xtream Codes Login</h2>
          <button className="modal-close-btn" onClick={onClose}>×</button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="server">Server URL</label>
            <input
              type="url"
              id="server"
              name="server"
              placeholder="https://example.com:8080"
              value={credentials.server}
              onChange={handleChange}
              className={errors.server ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.server && <span className="error-message">{errors.server}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              name="username"
              placeholder="Username"
              value={credentials.username}
              onChange={handleChange}
              className={errors.username ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.username && <span className="error-message">{errors.username}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Password"
              value={credentials.password}
              onChange={handleChange}
              className={errors.password ? 'error' : ''}
              disabled={isLoading}
              autoComplete="off"
            />
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>

          <div className="info-box">
            <span className="info-icon">ℹ️</span>
            <span className="info-text">
              Futni URL-në e plotë të serverit (p.sh.: https://example.com:8080)
            </span>
          </div>

          <div className="modal-actions">
            <button 
              type="submit" 
              className="btn-primary"
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="spinner-small"></span>
                  Duke u lidhur...
                </>
              ) : (
                'Lidhu'
              )}
            </button>
            <button 
              type="button" 
              className="btn-secondary"
              onClick={onClose}
              disabled={isLoading}
            >
              Anulo
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default XtreamLogin;
