import React, { useEffect, useRef, useState } from 'react';
import Hls from 'hls.js';
import EPGInfo from './EPGInfo';

const HlsPlayer = ({ 
  src, 
  isPlaying, 
  onPlayPause, 
  onClose, 
  onError, 
  theme,
  currentStreamInfo,
  currentEpg,
  epgData,
  onEpgUpdate
}) => {
  const videoRef = useRef(null);
  const hlsRef = useRef(null);
  const [isReady, setIsReady] = useState(false);
  const [error, setError] = useState(null);
  const [showEpg, setShowEpg] = useState(true);

  useEffect(() => {
    const video = videoRef.current;
    if (!video || !src) return;

    const loadVideo = () => {
      if (Hls.isSupported()) {
        if (hlsRef.current) {
          hlsRef.current.destroy();
        }

        const hls = new Hls({
          enableWorker: true,
          lowLatencyMode: true,
          backBufferLength: 90,
        });

        hlsRef.current = hls;
        hls.loadSource(src);
        hls.attachMedia(video);

        hls.on(Hls.Events.MANIFEST_PARSED, () => {
          setIsReady(true);
          if (isPlaying) {
            video.play().catch(err => {
              console.error('Playback error:', err);
              onError?.(err);
            });
          }
        });

        hls.on(Hls.Events.ERROR, (event, data) => {
          if (data.fatal) {
            console.error('HLS fatal error:', data);
            setError('Gabim n√´ transmetim');
            onError?.(new Error('HLS fatal error'));
          }
        });

      } else if (video.canPlayType('application/vnd.apple.mpegurl')) {
        video.src = src;
        video.addEventListener('loadedmetadata', () => {
          setIsReady(true);
          if (isPlaying) {
            video.play().catch(err => {
              console.error('Playback error:', err);
              onError?.(err);
            });
          }
        });
      } else {
        setError('HLS nuk mb√´shtetet n√´ k√´t√´ shfletues');
        onError?.(new Error('HLS not supported'));
      }
    };

    loadVideo();

    return () => {
      if (hlsRef.current) {
        hlsRef.current.destroy();
        hlsRef.current = null;
      }
      video.src = '';
    };
  }, [src, isPlaying, onError]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video || !isReady) return;

    if (isPlaying) {
      video.play().catch(err => {
        console.error('Playback error:', err);
        onError?.(err);
      });
    } else {
      video.pause();
    }
  }, [isPlaying, isReady, onError]);

  if (error) {
    return (
      <div className="hls-player-error">
        <div className="error-content">
          <span className="error-icon">‚ö†Ô∏è</span>
          <p>{error}</p>
          <button className="error-retry" onClick={onClose}>
            Mbyll
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="hls-player-wrapper">
      <div className="hls-player-container">
        <video
          ref={videoRef}
          className="hls-player-video"
          controls
          playsInline
          poster={null}
        />
        
        <div className="hls-player-controls-overlay">
          <button 
            className="hls-control-btn play-pause"
            onClick={onPlayPause}
            aria-label={isPlaying ? 'Pause' : 'Play'}
          >
            {isPlaying ? '‚è∏Ô∏è' : '‚ñ∂Ô∏è'}
          </button>
          
          <button 
            className="hls-control-btn epg-toggle"
            onClick={() => setShowEpg(!showEpg)}
            aria-label="Toggle EPG"
          >
            üìã
          </button>
          
          <button 
            className="hls-control-btn close"
            onClick={onClose}
            aria-label="Close"
          >
            ‚úï
          </button>
        </div>

        {!isReady && (
          <div className="hls-player-loading">
            <div className="spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>

      {/* EPG Section - Posht√´ Playerit */}
      {showEpg && currentStreamInfo && (
        <div className="player-epg-section">
          <EPGInfo
            channel={currentStreamInfo}
            currentProgram={currentEpg}
            nextProgram={epgData?.find(p => p.start_timestamp > (currentEpg?.end_timestamp || 0))}
            epgData={epgData}
            theme={theme}
          />
        </div>
      )}
    </div>
  );
};

export default HlsPlayer;
