import React, { useState, useEffect } from "react";
import HlsPlayer from "./components/HlsPlayer";
import SearchModal from "./components/SearchModal";
import SettingsModal from "./components/SettingsModal";
import { getLive, getMovies, getSeries, getCategories, buildStream, fetchM3U } from "./services/iptvService";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  // Credentials
  const [server, setServer] = useState(localStorage.getItem("server") || "");
  const [user, setUser] = useState(localStorage.getItem("user") || "");
  const [pass, setPass] = useState(localStorage.getItem("pass") || "");
  const [m3uUrl, setM3uUrl] = useState(localStorage.getItem("m3uUrl") || "");

  const [settings, setSettings] = useState({ theme: "light", quality: "auto" });

  // Data
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [categories, setCategories] = useState([]);
  const [currentStream, setCurrentStream] = useState(null);
  const [loading, setLoading] = useState(false);

  // UI State
  const [activeTab, setActiveTab] = useState("live");
  const [showSettingsModal, setShowSettingsModal] = useState(false);
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [favorites, setFavorites] = useState(() => {
    try { return JSON.parse(localStorage.getItem("favorites")) || []; } catch { return []; }
  });

  // Save credentials/favorites/settings
  useEffect(() => {
    localStorage.setItem("server", server);
    localStorage.setItem("user", user);
    localStorage.setItem("pass", pass);
    localStorage.setItem("m3uUrl", m3uUrl);
    localStorage.setItem("favorites", JSON.stringify(favorites));
    localStorage.setItem("settings", JSON.stringify(settings));
  }, [server, user, pass, m3uUrl, favorites, settings]);

  // Safe API wrapper
  const safeApiCall = async (fn, name) => {
    try {
      const result = await fn();
      if (!result || typeof result !== "object") throw new Error(`Invalid JSON for ${name}`);
      return result;
    } catch (err) {
      toast.error(`Gabim te ${name}: ${err.message}`);
      console.error(`Error in ${name}:`, err);
      return [];
    }
  };

  const fetchData = async () => {
    if (!server || !user || !pass) return;
    setLoading(true);

    const liveData = await safeApiCall(() => getLive(server, user, pass), "Live Streams");
    const movieData = await safeApiCall(() => getMovies(server, user, pass), "Movies");
    const seriesData = await safeApiCall(() => getSeries(server, user, pass), "Series");
    const catData = await safeApiCall(() => getCategories(server, user, pass), "Categories");

    setChannels(liveData);
    setMovies(movieData);
    setSeries(seriesData);
    setCategories(catData);
    setLoading(false);
  };

  useEffect(() => { fetchData(); }, [server, user, pass]);

  // Play stream
  const playStream = async (type, id) => {
    const url = buildStream(server, user, pass, type, id);
    const m3u = await fetchM3U(url);
    if (m3u) setCurrentStream(url);
    else toast.error("Failed to load stream");
  };

  // Search
  const performSearch = (query) => {
    const all = [...channels, ...movies, ...series];
    const results = all.filter(item =>
      item.name?.toLowerCase().includes(query.toLowerCase())
    );
    setSearchResults(results);
  };

  // Favorites
  const toggleFavorite = (item) => {
    let updated = [...favorites];
    if (favorites.find(f => f.id === item.id)) {
      updated = favorites.filter(f => f.id !== item.id);
    } else {
      updated.push(item);
    }
    setFavorites(updated);
  };

  // Render card list
  const renderCards = (items, type) => (
    <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-4 p-4">
      {items.map(item => (
        <div key={item.id} className="bg-white rounded shadow hover:shadow-lg p-2 cursor-pointer">
          <img src={item.stream_icon || "https://via.placeholder.com/150"} alt={item.name} className="rounded w-full h-24 object-cover mb-2" />
          <h3 className="text-sm font-semibold">{item.name}</h3>
          <div className="flex justify-between mt-1">
            <button className="text-blue-600 text-xs" onClick={() => playStream(type, item.stream_id || item.id)}>Play</button>
            <button className="text-red-500 text-xs" onClick={() => toggleFavorite(item)}>
              {favorites.find(f => f.id === item.id) ? "★" : "☆"}
            </button>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <div className="App min-h-screen bg-gray-50 text-black">
      {/* Tabs */}
      <div className="flex space-x-2 p-2 bg-white shadow">
        <button className={`px-4 py-2 rounded ${activeTab==="live"?"bg-blue-100":"bg-gray-200"}`} onClick={() => setActiveTab("live")}>Live TV</button>
        <button className={`px-4 py-2 rounded ${activeTab==="movies"?"bg-blue-100":"bg-gray-200"}`} onClick={() => setActiveTab("movies")}>Movies</button>
        <button className={`px-4 py-2 rounded ${activeTab==="series"?"bg-blue-100":"bg-gray-200"}`} onClick={() => setActiveTab("series")}>Series</button>
        <button className="px-4 py-2 rounded bg-gray-200" onClick={() => setShowSearchModal(true)}>Search</button>
        <button className="px-4 py-2 rounded bg-gray-200" onClick={() => setShowSettingsModal(true)}>Settings</button>
      </div>

      {/* Content */}
      {activeTab === "live" && renderCards(channels, "live")}
      {activeTab === "movies" && renderCards(movies, "movie")}
      {activeTab === "series" && renderCards(series, "series")}

      {/* HLS Player */}
      {currentStream && <HlsPlayer url={currentStream} />}

      {/* Modals */}
      {showSettingsModal && (
        <SettingsModal
          server={server} user={user} pass={pass} m3uUrl={m3uUrl} settings={settings}
          onServerChange={setServer} onUserChange={setUser} onPassChange={setPass} onM3uUrlChange={setM3uUrl}
          onSettingsChange={setSettings} onFetchData={fetchData}
          onClose={() => setShowSettingsModal(false)}
        />
      )}
      {showSearchModal && (
        <SearchModal
          searchQuery={searchQuery} searchResults={searchResults}
          onSearchChange={(val) => { setSearchQuery(val); performSearch(val); }}
          onPlay={playStream}
          onClose={() => { setShowSearchModal(false); setSearchQuery(""); setSearchResults([]); }}
          favorites={favorites} toggleFavorite={toggleFavorite}
        />
      )}

      <ToastContainer position="bottom-right" autoClose={5000} theme="light" />
    </div>
  );
}

export default App;
