// src/components/VirtualChannelList.jsx
import React, { useMemo } from 'react';
import { FixedSizeGrid } from 'react-window'; // âœ… Import direkt pa 'as Grid'
import AutoSizer from 'react-virtualized-auto-sizer'; // âœ… Import default
import { getItemName, getItemLogo, getStreamType } from '../utils/streamHelpers';
import './ChannelList.css';

const VirtualChannelList = ({ 
  items, 
  onPlay, 
  onToggleFavorite, 
  favorites, 
  selectedItem, 
  theme,
  activeTab 
}) => {
  // Konstantet pÃ«r grid
  const COLUMN_COUNT = 5;
  const ROW_HEIGHT = 180;
  const COLUMN_WIDTH = 200;

  // NÃ«se nuk ka items, shfaq empty state
  if (!items || items.length === 0) {
    return (
      <div className={`channel-list-empty theme-${theme}`}>
        <div className="empty-icon">ðŸ“º</div>
        <h3>Nuk ka kanale</h3>
        <p>Nuk u gjet asnjÃ« kanal nÃ« kÃ«tÃ« kategori</p>
      </div>
    );
  }

  // Cell component pÃ«r grid
  const Cell = ({ columnIndex, rowIndex, style }) => {
    const index = rowIndex * COLUMN_COUNT + columnIndex;
    const item = items[index];
    
    if (!item) return null;
    
    const itemId = item.stream_id || item.id || item.series_id;
    const itemName = getItemName(item);
    const itemLogo = getItemLogo(item);
    const streamType = getStreamType(item, activeTab);
    const isFavorite = favorites.includes(itemId);
    const isSelected = selectedItem?.stream_id === itemId || selectedItem?.id === itemId;
    
    return (
      <div style={style}>
        <div 
          className={`virtual-channel-card ${isSelected ? 'selected' : ''} theme-${theme}`}
          onClick={() => onPlay(item)}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              onPlay(item);
            }
          }}
        >
          <div className="virtual-card-image">
            <img 
              src={itemLogo || `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`}
              alt={itemName}
              loading="lazy"
              onError={(e) => {
                e.target.src = `https://ui-avatars.com/api/?name=${encodeURIComponent(itemName)}&size=80&background=${theme === 'dark' ? '2d3748' : 'e2e8f0'}&color=${theme === 'dark' ? 'fff' : '1a202c'}`;
              }}
            />
            <div className="virtual-card-badge">
              {streamType === 'live' ? 'LIVE' : 
               streamType === 'movie' ? 'FILM' : 
               streamType === 'series' ? 'SERIAL' : 'HD'}
            </div>
          </div>
          <div className="virtual-card-content">
            <h4 className="virtual-card-title">{itemName}</h4>
            <button
              className={`virtual-card-favorite ${isFavorite ? 'active' : ''}`}
              onClick={(e) => {
                e.stopPropagation();
                onToggleFavorite(itemId);
              }}
              aria-label={isFavorite ? 'Hiq nga favoritet' : 'Shto nÃ« favoritet'}
            >
              {isFavorite ? 'â˜…' : 'â˜†'}
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className={`virtual-channel-list theme-${theme}`}>
      <AutoSizer>
        {({ height, width }) => {
          // Llogarit column count bazuar nÃ« width
          const columnCount = Math.max(1, Math.floor(width / COLUMN_WIDTH));
          const rowCount = Math.ceil(items.length / columnCount);
          
          return (
            <FixedSizeGrid
              columnCount={columnCount}
              columnWidth={COLUMN_WIDTH}
              height={height}
              rowCount={rowCount}
              rowHeight={ROW_HEIGHT}
              width={width}
              className="virtual-grid"
            >
              {Cell}
            </FixedSizeGrid>
          );
        }}
      </AutoSizer>
    </div>
  );
};

export default VirtualChannelList;
