import React, { useState, useEffect, useMemo, useCallback } from "react";
import LiveTVContent from "./components/LiveTVContent";
import MoviesContent from "./components/MoviesContent";
import SeriesContent from "./components/SeriesContent";
import SearchModal from "./components/SearchModal";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import XtreamLogin from "./components/XtreamLogin";
import M3UUploader from "./components/M3UUploader";
import "./App.css";

function App() {
  /* =====================
     DATA STATE
  ===================== */
  const [channels, setChannels] = useState([]);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  const [isLoading, setIsLoading] = useState({
    channels: false,
    movies: false,
    series: false,
    general: false
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  /* =====================
     XTREAM CREDENTIALS
  ===================== */
  const [xtreamCredentials, setXtreamCredentials] = useState({
    server: "",
    username: "",
    password: "",
    isLoggedIn: false,
    expires: null,
    activeConnections: null,
    maxConnections: null
  });

  /* =====================
     M3U STATE
  ===================== */
  const [m3uUrl, setM3uUrl] = useState("");
  const [m3uData, setM3uData] = useState(null);
  const [m3uActive, setM3uActive] = useState(false);

  /* =====================
     PLAYER STATE
  ===================== */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playerError, setPlayerError] = useState(null);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);

  /* =====================
     UI STATE
  ===================== */
  const [activeTab, setActiveTab] = useState("live");
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showXtreamLogin, setShowXtreamLogin] = useState(false);
  const [showM3UModal, setShowM3UModal] = useState(true); // Trego modalin M3U nÃ« fillim
  const [sourceType, setSourceType] = useState(null); // null, xtream, m3u

  /* =====================
     SEARCH STATE
  ===================== */
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [favorites, setFavorites] = useState([]);

  /* =====================
     SAFE DATA
  ===================== */
  const safeChannels = useMemo(() => {
    if (!Array.isArray(channels)) return [];
    return channels.filter(c => c && typeof c === 'object');
  }, [channels]);

  const safeMovies = useMemo(() => {
    if (!Array.isArray(movies)) return [];
    return movies.filter(m => m && typeof m === 'object');
  }, [movies]);

  const safeSeries = useMemo(() => {
    if (!Array.isArray(series)) return [];
    return series.filter(s => s && typeof s === 'object');
  }, [series]);

  /* =====================
     SEARCH LOGIC
  ===================== */
  useEffect(() => {
    if (!searchQuery || searchQuery.trim() === "" || !sourceType) {
      setSearchResults([]);
      return;
    }

    const q = searchQuery.toLowerCase();
    const results = [];

    safeChannels.forEach((c) => {
      if (c?.name && typeof c.name === 'string' && c.name.toLowerCase().includes(q)) {
        results.push({ ...c, type: "Live", source: sourceType });
      }
    });

    safeMovies.forEach((m) => {
      if (m?.name && typeof m.name === 'string' && m.name.toLowerCase().includes(q)) {
        results.push({ ...m, type: "Movie", source: sourceType });
      }
    });

    safeSeries.forEach((s) => {
      if (s?.name && typeof s.name === 'string' && s.name.toLowerCase().includes(q)) {
        results.push({ ...s, type: "Series", source: sourceType });
      }
    });

    setSearchResults(results);
  }, [searchQuery, safeChannels, safeMovies, safeSeries, sourceType]);

  /* =====================
     XTREAM API FUNCTIONS
  ===================== */
  const handleXtreamLogin = useCallback(async (credentials) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      const { server, username, password } = credentials;
      
      // Pastro URL-nÃ« e serverit
      let baseUrl = server.trim();
      if (baseUrl.endsWith('/')) {
        baseUrl = baseUrl.slice(0, -1);
      }
      
      // Shto https:// nÃ«se nuk ka protocol
      if (!baseUrl.startsWith('http://') && !baseUrl.startsWith('https://')) {
        baseUrl = 'https://' + baseUrl;
      }

      console.log('Connecting to:', baseUrl);

      // 1. Testo login me timeout
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 sekonda timeout

      const loginUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}`;
      const loginResponse = await fetch(loginUrl, { 
        signal: controller.signal,
        mode: 'cors',
        headers: {
          'Accept': 'application/json'
        }
      });
      
      clearTimeout(timeoutId);
      
      if (!loginResponse.ok) {
        throw new Error(`HTTP error! status: ${loginResponse.status}`);
      }
      
      const loginData = await loginResponse.json();
      
      if (!loginData || loginData.user_info?.auth === 0 || loginData.user_info?.status === "Disabled") {
        throw new Error("Kredencialet janÃ« tÃ« gabuara ose llogaria Ã«shtÃ« e Ã§aktivizuar");
      }

      console.log('Login successful:', loginData.user_info);

      // 2. Merr live streams
      const liveUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}&action=get_live_streams`;
      const liveResponse = await fetch(liveUrl);
      let liveData = await liveResponse.json();
      
      liveData = Array.isArray(liveData) ? liveData.map(ch => ({
        ...ch,
        stream_url: `${baseUrl}/live/${username}/${password}/${ch.stream_id}.m3u8`,
        stream_id: ch.stream_id,
        name: ch.name,
        logo: ch.stream_icon || null,
        category: ch.category_name || 'Uncategorized',
        epg_channel_id: ch.epg_channel_id,
        tv_archive: ch.tv_archive,
        tv_archive_duration: ch.tv_archive_duration
      })) : [];

      // 3. Merr movies
      const moviesUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}&action=get_vod_streams`;
      const moviesResponse = await fetch(moviesUrl);
      let moviesData = await moviesResponse.json();
      
      moviesData = Array.isArray(moviesData) ? moviesData.map(m => ({
        ...m,
        stream_url: `${baseUrl}/movie/${username}/${password}/${m.stream_id}.${m.container_extension || 'mp4'}`,
        stream_id: m.stream_id,
        name: m.name,
        poster: m.stream_icon,
        year: m.year,
        rating: m.rating,
        genre: m.category_name,
        description: m.description,
        duration: m.duration
      })) : [];

      // 4. Merr series
      const seriesUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}&action=get_series`;
      const seriesResponse = await fetch(seriesUrl);
      let seriesData = await seriesResponse.json();
      
      seriesData = Array.isArray(seriesData) ? seriesData.map(s => ({
        ...s,
        series_id: s.series_id,
        name: s.name,
        cover: s.cover,
        backdrop: s.backdrop_path,
        seasons: s.seasons?.length || 0,
        rating: s.rating,
        genre: s.category_name,
        description: s.description
      })) : [];

      // Update state
      setChannels(liveData);
      setMovies(moviesData);
      setSeries(seriesData);
      setXtreamCredentials({ 
        ...credentials, 
        isLoggedIn: true,
        expires: loginData.user_info?.exp_date,
        activeConnections: loginData.user_info?.active_cons,
        maxConnections: loginData.user_info?.max_connections
      });
      setSourceType("xtream");
      setM3uActive(false);
      setM3uUrl("");
      setShowXtreamLogin(false);
      setShowM3UModal(false);
      
      setSuccess(
        `âœ… U lidhÃ«t me sukses! 
        ðŸ“º ${liveData.length} kanale, 
        ðŸŽ¬ ${moviesData.length} filma, 
        ðŸ“º ${seriesData.length} seriale
        ${loginData.user_info?.exp_date ? `\nðŸ“… Skadon: ${new Date(loginData.user_info.exp_date * 1000).toLocaleDateString()}` : ''}`
      );
      
    } catch (err) {
      if (err.name === 'AbortError') {
        setError("Lidhja mori shumÃ« kohÃ«. Kontrollo serverin.");
      } else {
        setError(`Xtream login gabim: ${err.message}`);
      }
      console.error('Xtream login error:', err);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     M3U FUNCTIONS - E RREGULLUAR
  ===================== */
  const handleM3ULoad = useCallback(async (url) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      // Validim URL
      if (!url || url.trim() === "") {
        throw new Error("URL nuk mund tÃ« jetÃ« bosh");
      }

      let m3uUrl = url.trim();
      
      // Shto https:// nÃ«se nuk ka protocol
      if (!m3uUrl.startsWith('http://') && !m3uUrl.startsWith('https://')) {
        m3uUrl = 'https://' + m3uUrl;
      }

      console.log('Fetching M3U from:', m3uUrl);

      // Fetch M3U file me timeout dhe CORS
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 15000); // 15 sekonda timeout

      const response = await fetch(m3uUrl, {
        signal: controller.signal,
        mode: 'cors',
        headers: {
          'Accept': '*/*',
          'Cache-Control': 'no-cache',
          'Pragma': 'no-cache'
        }
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        throw new Error(`HTTP gabim! Statusi: ${response.status} ${response.statusText}`);
      }

      const m3uText = await response.text();
      
      if (!m3uText || m3uText.length === 0) {
        throw new Error("M3U skedari Ã«shtÃ« bosh");
      }

      console.log('M3U file size:', m3uText.length, 'bytes');

      // Parse M3U
      const lines = m3uText.split('\n');
      const channels = [];
      let currentChannel = null;
      let channelCount = 0;

      lines.forEach((line, index) => {
        line = line.trim();
        
        if (line.startsWith('#EXTINF:')) {
          try {
            currentChannel = {};
            
            // Extract duration
            const durationMatch = line.match(/#EXTINF:(-?\d+)/);
            if (durationMatch) currentChannel.duration = parseInt(durationMatch[1]);
            
            // Extract tvg-id
            const tvgIdMatch = line.match(/tvg-id="([^"]*)"/);
            if (tvgIdMatch) currentChannel.tvg_id = tvgIdMatch[1];
            
            // Extract tvg-name
            const tvgNameMatch = line.match(/tvg-name="([^"]*)"/);
            if (tvgNameMatch) currentChannel.tvg_name = tvgNameMatch[1];
            
            // Extract tvg-logo
            const tvgLogoMatch = line.match(/tvg-logo="([^"]*)"/);
            if (tvgLogoMatch) currentChannel.logo = tvgLogoMatch[1];
            
            // Extract group-title
            const groupMatch = line.match(/group-title="([^"]*)"/);
            if (groupMatch) currentChannel.category = groupMatch[1];
            
            // Extract name
            const nameMatch = line.split(',').pop();
            if (nameMatch) currentChannel.name = nameMatch.trim();
            
          } catch (parseErr) {
            console.error('Error parsing EXTINF line:', line, parseErr);
          }
          
        } else if (line && !line.startsWith('#') && currentChannel) {
          // This is the URL
          try {
            currentChannel.stream_url = line;
            currentChannel.stream_id = `m3u_${channelCount + 1}`;
            
            // Sigurohu qÃ« ka emÃ«r
            if (!currentChannel.name) {
              currentChannel.name = `Kanali ${channelCount + 1}`;
            }
            
            // Sigurohu qÃ« ka kategori
            if (!currentChannel.category) {
              currentChannel.category = 'Uncategorized';
            }
            
            channels.push({ ...currentChannel });
            channelCount++;
            currentChannel = null;
          } catch (urlErr) {
            console.error('Error adding channel URL:', line, urlErr);
          }
        }
      });

      if (channels.length === 0) {
        throw new Error("Nuk u gjetÃ«n kanale nÃ« M3U skedar");
      }

      console.log(`âœ… U gjetÃ«n ${channels.length} kanale`);

      setChannels(channels);
      setMovies([]);
      setSeries([]);
      setM3uUrl(m3uUrl);
      setM3uData(channels);
      setM3uActive(true);
      setSourceType("m3u");
      setXtreamCredentials(prev => ({ ...prev, isLoggedIn: false }));
      setShowM3UModal(false);
      setSuccess(`âœ… ${channels.length} kanale u ngarkuan nga M3U playlist`);
      
    } catch (err) {
      if (err.name === 'AbortError') {
        setError("Lidhja mori shumÃ« kohÃ«. Kontrollo URL-nÃ« ose provo pÃ«rsÃ«ri.");
      } else if (err.message.includes('CORS') || err.message.includes('cors')) {
        setError("Problem me CORS. URL-ja nuk lejon lidhje direkte. Provo njÃ« URL tjetÃ«r.");
      } else {
        setError(`M3U gabim: ${err.message}`);
      }
      console.error('M3U loading error:', err);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     PLAYER HANDLERS
  ===================== */
  const handlePlay = useCallback((stream) => {
    try {
      if (!stream) {
        setPlayerError("Stream nuk Ã«shtÃ« valid");
        return;
      }

      let streamUrl = stream.stream_url;
      
      // PÃ«r Xtream, sigurohu qÃ« URL Ã«shtÃ« e plotÃ«
      if (sourceType === "xtream" && xtreamCredentials.isLoggedIn) {
        const { server, username, password } = xtreamCredentials;
        if (stream.stream_id) {
          if (stream.container_extension) {
            streamUrl = `${server}/movie/${username}/${password}/${stream.stream_id}.${stream.container_extension}`;
          } else {
            streamUrl = `${server}/live/${username}/${password}/${stream.stream_id}.m3u8`;
          }
        }
      }

      if (!streamUrl) {
        setPlayerError("URL e stream-it nuk Ã«shtÃ« e vlefshme");
        return;
      }

      setCurrentStream(streamUrl);
      setCurrentStreamInfo({
        name: stream.name,
        logo: stream.logo || stream.poster || stream.cover,
        type: stream.type || (sourceType === 'm3u' ? 'Live' : 'Stream')
      });
      setIsPlaying(true);
      setPlayerError(null);
    } catch (err) {
      setPlayerError("Gabim gjatÃ« fillimit tÃ« playback-ut");
      console.error(err);
    }
  }, [sourceType, xtreamCredentials]);

  const handleClosePlayer = useCallback(() => {
    setCurrentStream(null);
    setCurrentStreamInfo(null);
    setIsPlaying(false);
    setPlayerError(null);
  }, []);

  /* =====================
     FAVORITES
  ===================== */
  const toggleFavorite = useCallback((id) => {
    setFavorites(prev => {
      if (prev.includes(id)) {
        return prev.filter(fid => fid !== id);
      } else {
        return [...prev, id];
      }
    });
  }, []);

  /* =====================
     LOGOUT
  ===================== */
  const handleLogout = useCallback(() => {
    setChannels([]);
    setMovies([]);
    setSeries([]);
    setXtreamCredentials({ server: "", username: "", password: "", isLoggedIn: false });
    setM3uActive(false);
    setM3uUrl("");
    setM3uData(null);
    setSourceType(null);
    setCurrentStream(null);
    setCurrentStreamInfo(null);
    setIsPlaying(false);
    setShowM3UModal(true); // Trego modalin M3U pÃ«rsÃ«ri
    setSuccess("U Ã§kyÃ§Ã«t me sukses");
  }, []);

  /* =====================
     NUK KA DEMO! 
     FillojmÃ« direkt me M3U modal
  ===================== */
  useEffect(() => {
    // Trego modalin M3U nÃ« fillim
    setShowM3UModal(true);
  }, []);

  /* =====================
     RENDER
  ===================== */
  if (!sourceType) {
    return (
      <ErrorBoundary>
        <div className="app welcome-screen">
          <div className="welcome-container">
            <h1 className="welcome-title">IPTV Player</h1>
            <p className="welcome-subtitle">Zgjidh burimin e tÃ« dhÃ«nave</p>
            
            <div className="welcome-buttons">
              <button 
                className="welcome-btn xtream"
                onClick={() => {
                  setShowXtreamLogin(true);
                  setShowM3UModal(false);
                }}
              >
                <span className="welcome-icon">ðŸ“¡</span>
                <span className="welcome-btn-title">Xtream Codes</span>
                <span className="welcome-btn-desc">Lidhu me server Xtream</span>
              </button>
              
              <button 
                className="welcome-btn m3u"
                onClick={() => {
                  setShowM3UModal(true);
                  setShowXtreamLogin(false);
                }}
              >
                <span className="welcome-icon">ðŸ“‹</span>
                <span className="welcome-btn-title">M3U Playlist</span>
                <span className="welcome-btn-desc">Vendos URL tÃ« playlist-it</span>
              </button>
            </div>

            {/* Modals */}
            {showXtreamLogin && (
              <XtreamLogin
                onLogin={handleXtreamLogin}
                onClose={() => setShowXtreamLogin(false)}
                isLoading={isLoading.general}
              />
            )}

            {showM3UModal && (
              <M3UUploader
                onLoad={handleM3ULoad}
                onClose={() => setShowM3UModal(false)}
                isLoading={isLoading.general}
              />
            )}
          </div>
        </div>
      </ErrorBoundary>
    );
  }

  // Main App (kur ka source)
  return (
    <ErrorBoundary>
      <div className="app">
        {/* Modals */}
        {showXtreamLogin && (
          <XtreamLogin
            onLogin={handleXtreamLogin}
            onClose={() => setShowXtreamLogin(false)}
            isLoading={isLoading.general}
          />
        )}

        {showM3UModal && (
          <M3UUploader
            onLoad={handleM3ULoad}
            onClose={() => setShowM3UModal(false)}
            isLoading={isLoading.general}
          />
        )}

        <SearchModal
          isOpen={showSearchModal}
          query={searchQuery}
          setQuery={setSearchQuery}
          results={searchResults}
          onClose={() => {
            setShowSearchModal(false);
            setSearchQuery("");
          }}
          onSelectStream={handlePlay}
          onToggleFavorite={toggleFavorite}
          favorites={favorites}
        />

        {/* Error/Success Messages */}
        {error && (
          <div className="error-banner">
            <span className="error-icon">âš ï¸</span>
            <span className="error-message">{error}</span>
            <button className="error-close" onClick={() => setError(null)}>Ã—</button>
          </div>
        )}

        {success && (
          <div className="success-banner">
            <span className="success-icon">âœ…</span>
            <span className="success-message">{success}</span>
            <button className="success-close" onClick={() => setSuccess(null)}>Ã—</button>
          </div>
        )}

        {playerError && (
          <div className="error-banner player-error">
            <span className="error-icon">ðŸŽ¬</span>
            <span className="error-message">{playerError}</span>
            <button className="error-close" onClick={() => setPlayerError(null)}>Ã—</button>
          </div>
        )}

        {/* Header */}
        <header className="app-header">
          <div className="header-left">
            <h1 className="app-title">IPTV Player</h1>
            
            <div className="source-badge">
              {sourceType === "xtream" && (
                <span className="badge xtream">
                  ðŸ“¡ Xtream {xtreamCredentials.username}
                </span>
              )}
              {sourceType === "m3u" && (
                <span className="badge m3u">
                  ðŸ“‹ M3U ({safeChannels.length} kanale)
                </span>
              )}
            </div>

            <button className="search-btn" onClick={() => setShowSearchModal(true)}>
              ðŸ” KÃ«rko
              <span className="search-shortcut">Ctrl+K</span>
            </button>
          </div>

          <nav className="main-nav">
            {safeChannels.length > 0 && (
              <button 
                className={`nav-btn ${activeTab === "live" ? "active" : ""}`}
                onClick={() => setActiveTab("live")}
              >
                ðŸ“º Live ({safeChannels.length})
              </button>
            )}
            {safeMovies.length > 0 && (
              <button 
                className={`nav-btn ${activeTab === "movies" ? "active" : ""}`}
                onClick={() => setActiveTab("movies")}
              >
                ðŸŽ¬ Filma ({safeMovies.length})
              </button>
            )}
            {safeSeries.length > 0 && (
              <button 
                className={`nav-btn ${activeTab === "series" ? "active" : ""}`}
                onClick={() => setActiveTab("series")}
              >
                ðŸ“º Seriale ({safeSeries.length})
              </button>
            )}
          </nav>

          <div className="header-right">
            <button 
              className="source-btn"
              onClick={() => {
                handleLogout();
              }}
            >
              ðŸ”„ Ndrysho Burimin
            </button>
            
            <button className="logout-btn" onClick={handleLogout}>
              ðŸšª Ã‡kyÃ§u
            </button>
          </div>
        </header>

        {/* Connection Info */}
        {sourceType === "xtream" && xtreamCredentials.isLoggedIn && (
          <div className="connection-info">
            <span>ðŸ“¡ Server: {xtreamCredentials.server}</span>
            <span>ðŸ‘¤ User: {xtreamCredentials.username}</span>
            <span>ðŸ“º {safeChannels.length} kanale</span>
            <span>ðŸŽ¬ {safeMovies.length} filma</span>
            <span>ðŸ“º {safeSeries.length} seriale</span>
            {xtreamCredentials.expires && (
              <span>ðŸ“… Skadon: {new Date(xtreamCredentials.expires * 1000).toLocaleDateString()}</span>
            )}
          </div>
        )}

        {sourceType === "m3u" && m3uActive && (
          <div className="connection-info">
            <span>ðŸ“‹ M3U Playlist</span>
            <span>ðŸ“º {safeChannels.length} kanale</span>
            <span className="m3u-url" title={m3uUrl}>
              ðŸ”— {m3uUrl.substring(0, 50)}...
            </span>
          </div>
        )}

        {/* Main Content */}
        <main className="main-content">
          {activeTab === "live" && safeChannels.length > 0 && (
            <LiveTVContent
              channels={safeChannels}
              onPlay={handlePlay}
              onToggleFavorite={toggleFavorite}
              favorites={favorites}
              isLoading={isLoading.channels}
            />
          )}

          {activeTab === "movies" && safeMovies.length > 0 && (
            <MoviesContent 
              movies={safeMovies} 
              isLoading={isLoading.movies}
              onPlay={handlePlay}
              onToggleFavorite={toggleFavorite}
              favorites={favorites}
            />
          )}

          {activeTab === "series" && safeSeries.length > 0 && (
            <SeriesContent 
              series={safeSeries} 
              isLoading={isLoading.series}
              onPlay={handlePlay}
              onToggleFavorite={toggleFavorite}
              favorites={favorites}
            />
          )}

          {activeTab === "live" && safeChannels.length === 0 && sourceType === "m3u" && (
            <div className="no-content">
              <p>Nuk ka kanale nÃ« kÃ«tÃ« playlist M3U</p>
            </div>
          )}
        </main>

        {/* HLS Player */}
        {currentStream && (
          <div className="player-container">
            {currentStreamInfo && (
              <div className="now-playing">
                <img 
                  src={currentStreamInfo.logo || `https://ui-avatars.com/api/?name=${currentStreamInfo.name}&size=32`} 
                  alt={currentStreamInfo.name}
                  className="now-playing-logo"
                />
                <span className="now-playing-name">{currentStreamInfo.name}</span>
              </div>
            )}
            <HlsPlayer
              src={currentStream}
              isPlaying={isPlaying}
              onPlayPause={() => setIsPlaying(!isPlaying)}
              onClose={handleClosePlayer}
              onError={(err) => setPlayerError(err)}
            />
          </div>
        )}

        {/* Loading Overlay */}
        {isLoading.general && (
          <div className="loading-overlay">
            <div className="spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
