import React from "react";

function SearchModal({
  isOpen,
  onClose,
  results = [],   // ✅ DEFAULT ARRAY
  onSelect
}) {
  if (!isOpen) return null;

  // ✅ SIGURI ABSOLUTE
  const safeResults = Array.isArray(results) ? results : [];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-60 z-50 flex items-center justify-center">
      <div className="bg-gray-900 text-white w-full max-w-2xl rounded-lg p-4">
        
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-bold">Kërko</h2>
          <button
            onClick={onClose}
            className="text-red-400 hover:text-red-600"
          >
            ✕
          </button>
        </div>

        {safeResults.length === 0 ? (
          <p className="text-gray-400 text-center py-6">
            Nuk u gjetën rezultate
          </p>
        ) : (
          <ul className="max-h-96 overflow-y-auto space-y-2">
            {safeResults.map((item, index) => (
              <li
                key={item.stream_id || index}
                onClick={() => {
                  if (onSelect) onSelect(item);
                  onClose();
                }}
                className="p-3 bg-gray-800 rounded cursor-pointer hover:bg-gray-700"
              >
                <div className="font-semibold">
                  {item.name || "Pa emër"}
                </div>
                {item.category && (
                  <div className="text-sm text-gray-400">
                    {item.category}
                  </div>
                )}
              </li>
            ))}
          </ul>
        )}

      </div>
    </div>
  );
}

export default SearchModal;
