import React from 'react';

const EPGInfo = ({ channel, currentProgram, nextProgram, theme }) => {
  if (!currentProgram) return null;

  const formatTime = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleTimeString('sq-AL', { hour: '2-digit', minute: '2-digit' });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleDateString('sq-AL', { day: 'numeric', month: 'short' });
  };

  return (
    <div className="epg-info">
      <div className="epg-header">
        <span className="epg-icon">ðŸ“º</span>
        <span className="epg-title">Programacioni</span>
      </div>

      <div className="epg-grid">
        <div className="epg-item">
          <span className="epg-label">Kanali</span>
          <span className="epg-value">{channel?.name}</span>
        </div>
        <div className="epg-item">
          <span className="epg-label">Data</span>
          <span className="epg-value">{formatDate(currentProgram.start_timestamp)}</span>
        </div>
      </div>

      {/* Current Program */}
      <div className="epg-program current">
        <div className="epg-program-title">Tani nÃ« transmetim</div>
        <div className="epg-program-name">{currentProgram.title}</div>
        <div className="epg-program-time">
          {formatTime(currentProgram.start_timestamp)} - {formatTime(currentProgram.end_timestamp)}
        </div>
        {currentProgram.description && (
          <div className="epg-program-description">{currentProgram.description}</div>
        )}
      </div>

      {/* Next Program */}
      {nextProgram && (
        <div className="epg-program next">
          <div className="epg-program-title">MÃ« pas</div>
          <div className="epg-program-name">{nextProgram.title}</div>
          <div className="epg-program-time">
            {formatTime(nextProgram.start_timestamp)} - {formatTime(nextProgram.end_timestamp)}
          </div>
        </div>
      )}
    </div>
  );
};

export default EPGInfo;
