import React, { useState, useEffect, useMemo, useCallback } from "react";
import ErrorBoundary from "./components/ErrorBoundary";
import HlsPlayer from "./components/HlsPlayer";
import XtreamLogin from "./components/XtreamLogin";
import M3UUploader from "./components/M3UUploader";
import SearchModal from "./components/SearchModal";
import EPGInfo from "./components/EPGInfo";
import "./App.css";

function App() {
  /* =====================
     DATA STATE
  ===================== */
  const [channels, setChannels] = useState([]);
  const [isLoading, setIsLoading] = useState({
    channels: false,
    general: false
  });
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  /* =====================
     XTREAM CREDENTIALS
  ===================== */
  const [xtreamCredentials, setXtreamCredentials] = useState({
    server: "",
    username: "",
    password: "",
    isLoggedIn: false,
    expires: null,
    activeConnections: null,
    maxConnections: null
  });

  /* =====================
     M3U STATE
  ===================== */
  const [m3uUrl, setM3uUrl] = useState("");
  const [m3uActive, setM3uActive] = useState(false);

  /* =====================
     PLAYER STATE
  ===================== */
  const [currentStream, setCurrentStream] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [playerError, setPlayerError] = useState(null);
  const [currentStreamInfo, setCurrentStreamInfo] = useState(null);
  const [isPlayerVisible, setIsPlayerVisible] = useState(false);

  /* =====================
     UI STATE
  ===================== */
  const [showSearchModal, setShowSearchModal] = useState(false);
  const [showXtreamLogin, setShowXtreamLogin] = useState(false);
  const [showM3UModal, setShowM3UModal] = useState(true);
  const [sourceType, setSourceType] = useState(null);
  
  /* =====================
     SIDEBAR STATE - SMART PLAYER STYLE
  ===================== */
  const [selectedCategory, setSelectedCategory] = useState("TÃ« gjitha");
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [categories, setCategories] = useState([]);
  const [selectedChannel, setSelectedChannel] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [favorites, setFavorites] = useState([]);
  const [recentChannels, setRecentChannels] = useState([]);

  /* =====================
     EPG STATE
  ===================== */
  const [epgData, setEpgData] = useState({
    current: null,
    next: null
  });

  /* =====================
     SAFE DATA
  ===================== */
  const safeChannels = useMemo(() => {
    if (!Array.isArray(channels)) return [];
    return channels.filter(c => c && typeof c === 'object');
  }, [channels]);

  /* =====================
     GJENERO KATEGORIT NGA KANALET
  ===================== */
  useEffect(() => {
    if (safeChannels.length > 0) {
      const cats = ["TÃ« gjitha", ...new Set(
        safeChannels
          .map(c => c.category || c.category_name || 'TÃ« tjera')
          .filter(Boolean)
          .sort()
      )];
      setCategories(cats);
    }
  }, [safeChannels]);

  /* =====================
     FILTRO KANALET SIPAS KATEGORISE
  ===================== */
  const filteredChannels = useMemo(() => {
    if (!safeChannels.length) return [];
    if (selectedCategory === "TÃ« gjitha") return safeChannels;
    return safeChannels.filter(ch => 
      (ch.category || ch.category_name || 'TÃ« tjera') === selectedCategory
    );
  }, [safeChannels, selectedCategory]);

  /* =====================
     KERKIMI
  ===================== */
  const searchedChannels = useMemo(() => {
    if (!searchQuery) return filteredChannels;
    const q = searchQuery.toLowerCase();
    return filteredChannels.filter(ch => 
      ch.name?.toLowerCase().includes(q) ||
      ch.category?.toLowerCase().includes(q)
    );
  }, [filteredChannels, searchQuery]);

  /* =====================
     XTREAM LOGIN
  ===================== */
  const handleXtreamLogin = useCallback(async (credentials) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      const { server, username, password } = credentials;
      
      let baseUrl = server.trim();
      if (baseUrl.endsWith('/')) baseUrl = baseUrl.slice(0, -1);
      if (!baseUrl.startsWith('http')) baseUrl = 'https://' + baseUrl;

      const loginUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}`;
      const loginResponse = await fetch(loginUrl);
      
      if (!loginResponse.ok) throw new Error("Serveri nuk u gjet");
      
      const loginData = await loginResponse.json();
      if (!loginData?.user_info?.auth) throw new Error("Kredencialet e gabuara");

      const liveUrl = `${baseUrl}/player_api.php?username=${username}&password=${password}&action=get_live_streams`;
      const liveResponse = await fetch(liveUrl);
      let liveData = await liveResponse.json();
      
      liveData = Array.isArray(liveData) ? liveData.map(ch => ({
        ...ch,
        stream_url: `${baseUrl}/live/${username}/${password}/${ch.stream_id}.m3u8`,
        stream_id: ch.stream_id,
        name: ch.name,
        logo: ch.stream_icon || null,
        category: ch.category_name || 'TÃ« tjera',
        epg_channel_id: ch.epg_channel_id
      })) : [];

      setChannels(liveData);
      setXtreamCredentials({ ...credentials, isLoggedIn: true });
      setSourceType("xtream");
      setShowXtreamLogin(false);
      setShowM3UModal(false);
      setSelectedCategory("TÃ« gjitha");
      setSuccess(`âœ… U lidhÃ«t! ${liveData.length} kanale`);
      
    } catch (err) {
      setError(err.message);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     M3U LOAD
  ===================== */
  const handleM3ULoad = useCallback(async (source) => {
    setIsLoading(prev => ({ ...prev, general: true }));
    setError(null);
    
    try {
      let m3uText = '';

      if (source.type === 'file') {
        m3uText = source.content;
      } else {
        let url = source.url.trim();
        if (!url.startsWith('http')) url = 'https://' + url;
        const proxyUrl = `https://api.allorigins.win/raw?url=${encodeURIComponent(url)}`;
        const response = await fetch(proxyUrl);
        if (!response.ok) throw new Error('Nuk u ngarkua');
        m3uText = await response.text();
      }

      const lines = m3uText.split('\n');
      const channels = [];
      let currentChannel = null;
      let id = 1;

      lines.forEach(line => {
        line = line.trim();
        if (line.startsWith('#EXTINF:')) {
          currentChannel = {};
          const nameMatch = line.split(',').pop();
          if (nameMatch) currentChannel.name = nameMatch.trim();
          const logoMatch = line.match(/tvg-logo="([^"]*)"/);
          if (logoMatch) currentChannel.logo = logoMatch[1];
          const groupMatch = line.match(/group-title="([^"]*)"/);
          if (groupMatch) currentChannel.category = groupMatch[1];
        } else if (line && !line.startsWith('#') && currentChannel) {
          currentChannel.stream_url = line;
          currentChannel.stream_id = `m3u_${id++}`;
          currentChannel.name = currentChannel.name || `Kanali ${id}`;
          currentChannel.category = currentChannel.category || 'TÃ« tjera';
          channels.push({ ...currentChannel });
          currentChannel = null;
        }
      });

      setChannels(channels);
      setSourceType("m3u");
      setShowM3UModal(false);
      setSelectedCategory("TÃ« gjitha");
      setSuccess(`âœ… ${channels.length} kanale u ngarkuan`);
      
    } catch (err) {
      setError(`M3U gabim: ${err.message}`);
    } finally {
      setIsLoading(prev => ({ ...prev, general: false }));
    }
  }, []);

  /* =====================
     PLAY CHANNEL
  ===================== */
  const handlePlay = useCallback((channel) => {
    if (!channel) return;

    let streamUrl = channel.stream_url;
    
    if (sourceType === "xtream" && xtreamCredentials.isLoggedIn) {
      const { server, username, password } = xtreamCredentials;
      streamUrl = `${server}/live/${username}/${password}/${channel.stream_id}.m3u8`;
    }

    setCurrentStream(streamUrl);
    setCurrentStreamInfo({
      id: channel.stream_id,
      name: channel.name,
      logo: channel.logo,
      category: channel.category
    });
    setSelectedChannel(channel);
    setIsPlaying(true);
    setIsPlayerVisible(true);
    
    // Shto nÃ« tÃ« fundit
    setRecentChannels(prev => {
      const filtered = prev.filter(c => c.stream_id !== channel.stream_id);
      return [channel, ...filtered].slice(0, 10);
    });

    // Simulo EPG
    setEpgData({
      current: {
        title: "Programi nÃ« transmetim",
        start: "20:00",
        end: "21:00",
        description: "Duke u transmetuar..."
      },
      next: {
        title: "Programi i radhÃ«s",
        start: "21:00",
        end: "22:00"
      }
    });
  }, [sourceType, xtreamCredentials]);

  /* =====================
     FAVORITES
  ===================== */
  const toggleFavorite = useCallback((id) => {
    setFavorites(prev => 
      prev.includes(id) 
