    setSourceType(null);
    setCurrentStream(null);
    setIsPlayerVisible(false);
    setShowM3UModal(true);
    setSelectedCategory("TÃ« gjitha");
    setCategories([]);
    setRecentChannels([]);
  }, []);

  /* =====================
