            <div className="smart-search" onClick={() => setShowSearchModal(true)}>
              <span className="smart-search-icon">ðŸ”</span>
              <span className="smart-search-text">KÃ«rko kanale...</span>
              <span className="smart-search-hint">Ctrl+K</span>
            </div>
          </div>

          <div className="smart-header-right">
            <button className="smart-icon-btn" onClick={handleLogout}>
              <span className="icon">ðŸšª</span>
            </button>
          </div>
        </header>

        {/* PLAYER SECTION */}
        {isPlayerVisible && currentStream && (
          <section className="smart-player-section">
            <div className="smart-player-container">
              <div className="smart-player-header">
                <div className="smart-now-playing">
                  <img 
                    src={currentStreamInfo?.logo || `https://ui-avatars.com/api/?name=${currentStreamInfo?.name}&size=32`}
