            </div>

            {/* KANALET E KATEGORISE - SMART STYLE */}
            <div className="smart-channels">
              <div className="smart-channels-header">
                <div className="smart-channels-title">
                  <span className="smart-channel-icon">ðŸ“º</span>
                  <span>{selectedCategory === 'TÃ« gjitha' ? 'TÃ« gjitha kanalet' : selectedCategory}</span>
                </div>
                <span className="smart-channel-count">{filteredChannels.length}</span>
              </div>
              
              <div className="smart-channels-list">
                {filteredChannels.map((channel) => (
                  <div 
                    key={channel.stream_id}
                    className={`smart-channel-item ${selectedChannel?.stream_id === channel.stream_id ? 'playing' : ''}`}
                    onClick={() => handlePlay(channel)}
                  >
                    <img 
                      src={channel.logo || `https://ui-avatars.com/api/?name=${channel.name}&size=32&background=f1f5f9&color=334155`}
                      alt={channel.name}
                      className="smart-channel-logo"
                      loading="lazy"
                      onError={(e) => {
                        e.target.src = `https://ui-avatars.com/api/?name=${channel.name}&size=32&background=f1f5f9&color=334155`;
                      }}
                    />
                    
                    <div className="smart-channel-info">
                      <div className="smart-channel-name">{channel.name}</div>
                      <div className="smart-channel-meta">
                        <span className="smart-channel-category">
                          {channel.category || 'Live'}
                        </span>
                        {selectedChannel?.stream_id === channel.stream_id && (
                          <span className="smart-now-indicator">
                            <span className="dot"></span>
                            Live
                          </span>
                        )}
                      </div>
                    </div>

                    <button 
                      className={`smart-fav-btn ${favorites.includes(channel.stream_id) ? 'active' : ''}`}
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleFavorite(channel.stream_id);
                      }}
                    >
                      {favorites.includes(channel.stream_id) ? 'â˜…' : 'â˜†'}
                    </button>
                  </div>
                ))}

                {filteredChannels.length === 0 && (
                  <div className="smart-no-channels">
                    Nuk ka kanale nÃ« kÃ«tÃ« kategori
                  </div>
                )}
              </div>
            </div>

            {/* RECENT CHANNELS - SMART STYLE */}
            {recentChannels.length > 0 && (
              <div className="smart-recent">
                <div className="smart-recent-header">
                  <h4>TÃ‹ FUNDIT</h4>
                  <span className="smart-recent-count">{recentChannels.length}</span>
                </div>
                <div className="smart-recent-list">
                  {recentChannels.slice(0, 5).map((channel) => (
                    <div 
                      key={channel.stream_id}
                      className="smart-recent-item"
                      onClick={() => handlePlay(channel)}
                    >
                      <img 
                        src={channel.logo || `https://ui-avatars.com/api/?name=${channel.name}&size=24`}
                        alt={channel.name}
                        className="smart-recent-logo"
                      />
                      <span className="smart-recent-name">{channel.name}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* SIDEBAR FOOTER */}
            <div className="smart-sidebar-footer">
              <div className="smart-stats">
                <div className="smart-stat-item">
                  <span className="stat-icon">ðŸ“º</span>
                  <span className="stat-label">Total</span>
                  <span className="stat-value">{safeChannels.length}</span>
                </div>
                <div className="smart-stat-item">
                  <span className="stat-icon">ðŸ“Œ</span>
                  <span className="stat-label">Kjo kategori</span>
                  <span className="stat-value">{filteredChannels.length}</span>
                </div>
                {favorites.length > 0 && (
                  <div className="smart-stat-item">
                    <span className="stat-icon">â­</span>
                    <span className="stat-label">Favorite</span>
                    <span className="stat-value">{favorites.length}</span>
                  </div>
                )}
              </div>
            </div>
          </aside>

          {/* CONTENT AREA - BOSH */}
          <main className="smart-content"></main>
        </div>

        {/* LOADING */}
        {isLoading.general && (
          <div className="smart-loading">
            <div className="smart-spinner"></div>
            <p>Duke ngarkuar...</p>
          </div>
        )}
      </div>
    </ErrorBoundary>
  );
}

export default App;
