import React from 'react';

class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      hasError: false, 
      error: null, 
      errorInfo: null 
    };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true };
  }

  componentDidCatch(error, errorInfo) {
    this.setState({
      error: error,
      errorInfo: errorInfo
    });
    
    // Log error to console
    console.error('ErrorBoundary caught an error:', error, errorInfo);
    
    // You can also log to an error reporting service here
    // logErrorToService(error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="min-h-screen flex items-center justify-center bg-gray-900 p-4">
          <div className="bg-gray-800 p-8 rounded-xl max-w-2xl w-full border border-red-700">
            <div className="text-center mb-6">
              <div className="text-6xl mb-4">⚠️</div>
              <h1 className="text-2xl font-bold text-red-400 mb-2">
                Diçka shkoi keq!
              </h1>
              <p className="text-gray-400 mb-6">
                Një gabim ka ndodhur në aplikacion. Ju lutem provoni përsëri.
              </p>
            </div>
            
            <div className="bg-gray-900 p-4 rounded-lg mb-6 overflow-auto">
              <h2 className="text-lg font-semibold text-red-300 mb-2">
                Detaje të Gabimit:
              </h2>
              <pre className="text-sm text-red-200 whitespace-pre-wrap">
                {this.state.error && this.state.error.toString()}
              </pre>
              {this.state.errorInfo && (
                <>
                  <h3 className="text-md font-semibold text-red-300 mt-4 mb-2">
                    Stack Trace:
                  </h3>
                  <pre className="text-xs text-gray-300 whitespace-pre-wrap overflow-auto max-h-60">
                    {this.state.errorInfo.componentStack}
                  </pre>
                </>
              )}
            </div>
            
            <div className="flex flex-col sm:flex-row gap-3">
              <button
                onClick={() => window.location.reload()}
                className="flex-1 bg-blue-600 hover:bg-blue-500 text-white font-bold py-3 px-4 rounded-lg transition-colors"
              >
                Rifresko Faqen
              </button>
              
              <button
                onClick={() => {
                  localStorage.clear();
                  sessionStorage.clear();
                  window.location.reload();
                }}
                className="flex-1 bg-red-600 hover:bg-red-500 text-white font-bold py-3 px-4 rounded-lg transition-colors"
              >
                Pastro Cache & Rifresko
              </button>
              
              <button
                onClick={() => {
                  this.setState({ hasError: false, error: null, errorInfo: null });
                }}
                className="flex-1 bg-gray-700 hover:bg-gray-600 text-white font-bold py-3 px-4 rounded-lg transition-colors"
              >
                Provo Përsëri
              </button>
            </div>
            
            <div className="mt-6 pt-6 border-t border-gray-700">
              <p className="text-sm text-gray-500 text-center">
                Nëse problemi vazhdon, kontrolloni konfigurimin tuaj IPTV ose kontaktoni mbështetjen.
              </p>
            </div>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

export default ErrorBoundary;
