// components/M3UUploader.jsx
import React, { useState, useCallback } from 'react';

function M3UUploader({ onLoad, onClose, isLoading }) {
  const [url, setUrl] = useState('');
  const [file, setFile] = useState(null);
  const [activeTab, setActiveTab] = useState('url');
  const [error, setError] = useState('');

  const handleFileChange = useCallback((e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.name.endsWith('.m3u') || selectedFile.name.endsWith('.m3u8')) {
        setFile(selectedFile);
        setError('');
      } else {
        setError('Zgjidh nj√´ skedar .m3u ose .m3u8');
        setFile(null);
      }
    }
  }, []);

  const handleFileUpload = useCallback(async () => {
    if (!file) {
      setError('Zgjidh nj√´ skedar');
      return;
    }
    
    try {
      const text = await file.text();
      onLoad({ 
        type: 'file', 
        content: text, 
        name: file.name 
      });
    } catch (err) {
      setError('Gabim gjat√´ leximit t√´ skedarit');
    }
  }, [file, onLoad]);

  const handleUrlSubmit = useCallback(() => {
    if (!url.trim()) {
      setError('URL nuk mund t√´ jet√´ bosh');
      return;
    }
    onLoad({ type: 'url', url: url.trim() });
  }, [url, onLoad]);

  const handleSubmit = useCallback((e) => {
    e.preventDefault();
    setError('');
    
    if (activeTab === 'url') {
      handleUrlSubmit();
    } else {
      handleFileUpload();
    }
  }, [activeTab, handleUrlSubmit, handleFileUpload]);

  const handleClose = useCallback(() => {
    onClose();
  }, [onClose]);

  const handleTabChange = useCallback((tab) => {
    setActiveTab(tab);
    setError('');
  }, []);

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <div className="modal-header">
          <h2>M3U Playlist</h2>
          <button 
            className="modal-close-btn" 
            onClick={handleClose}
            disabled={isLoading}
          >
            √ó
          </button>
        </div>
        
        <div className="tab-buttons">
          <button 
            className={`tab-btn ${activeTab === 'url' ? 'active' : ''}`}
            onClick={() => handleTabChange('url')}
            disabled={isLoading}
            type="button"
          >
            URL
          </button>
          <button 
            className={`tab-btn ${activeTab === 'file' ? 'active' : ''}`}
            onClick={() => handleTabChange('file')}
            disabled={isLoading}
            type="button"
          >
            Upload Skedar
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          {activeTab === 'url' ? (
            <div className="form-group">
              <label htmlFor="m3u-url">M3U URL</label>
              <input
                type="url"
                id="m3u-url"
                placeholder="https://example.com/playlist.m3u"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                disabled={isLoading}
                autoComplete="off"
                autoFocus
              />
              <p className="help-text">
                Vendos URL-n√´ e playlist-it M3U
              </p>
            </div>
          ) : (
            <div className="form-group">
              <label htmlFor="m3u-file">Skedari M3U</label>
              <div className="file-upload-area">
                <input
                  type="file"
                  id="m3u-file"
                  accept=".m3u,.m3u8,text/plain"
                  onChange={handleFileChange}
                  disabled={isLoading}
                  style={{ display: 'none' }}
                />
                <label htmlFor="m3u-file" className="file-label">
                  <span className="file-icon">üìÅ</span>
                  <span className="file-name">
                    {file ? file.name : 'Zgjidh skedar M3U'}
                  </span>
                  <span className="file-hint">
                    Kliko ose drag & drop
                  </span>
                </label>
              </div>
              <p className="help-text">
                Formate t√´ lejuara: .m3u, .m3u8
              </p>
            </div>
          )}

          {error && (
            <div className="error-message" style={{ marginBottom: '16px' }}>
              ‚ö†Ô∏è {error}
            </div>
          )}

          <div className="modal-actions">
            <button 
              type="submit" 
              className="btn-primary"
              disabled={isLoading || (activeTab === 'file' && !file)}
            >
              {isLoading ? (
                <>
                  <span className="spinner-small"></span>
                  Duke ngarkuar...
                </>
              ) : (
                'Ngarko'
              )}
            </button>
            <button 
              type="button" 
              className="btn-secondary"
              onClick={handleClose}
              disabled={isLoading}
            >
              Anulo
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default M3UUploader;
