/**
 * BUILD STREAM URL
 */
export const buildStreamUrl = (server, user, pass, streamId, type = 'live', extension = 'm3u8') => {
  let baseUrl = server.trim();
  
  if (baseUrl.endsWith('/')) {
    baseUrl = baseUrl.slice(0, -1);
  }
  
  if (!baseUrl.startsWith('http://') && !baseUrl.startsWith('https://')) {
    baseUrl = 'http://' + baseUrl;
  }

  switch (type) {
    case 'live':
      return `${baseUrl}/live/${user}/${pass}/${streamId}.m3u8`;
    case 'movie':
      return `${baseUrl}/movie/${user}/${pass}/${streamId}.${extension}`;
    case 'series':
      return `${baseUrl}/series/${user}/${pass}/${streamId}.mp4`;
    default:
      return `${baseUrl}/live/${user}/${pass}/${streamId}.m3u8`;
  }
};

