import React, { useEffect, useRef } from 'react';

function SearchModal({ 
  searchQuery, 
  searchResults, 
  onSearchChange, 
  onClose, 
  onPlay, 
  favorites, 
  toggleFavorite,
  theme 
}) {
  const inputRef = useRef(null);

  useEffect(() => {
    // Focus input when modal opens
    if (inputRef.current) {
      inputRef.current.focus();
    }
    
    // Prevent body scroll
    document.body.style.overflow = 'hidden';
    
    return () => {
      document.body.style.overflow = 'auto';
    };
  }, []);

  const handleKeyDown = (e) => {
    if (e.key === 'Escape') {
      onClose();
    }
    if (e.key === 'Enter' && searchResults.length > 0) {
      onPlay(searchResults[0]);
    }
  };

  const getTypeIcon = (type) => {
    switch (type) {
      case 'channel': return 'üì∫';
      case 'movie': return 'üé¨';
      case 'series': return 'üì∫';
      default: return 'üìÑ';
    }
  };

  const getTypeColor = (type) => {
    switch (type) {
      case 'channel': return 'bg-blue-600';
      case 'movie': return 'bg-purple-600';
      case 'series': return 'bg-red-600';
      default: return 'bg-gray-600';
    }
  };

  return (
    <div 
      className="fixed inset-0 bg-black bg-opacity-70 flex items-start justify-center z-50 p-4 overflow-y-auto"
      onClick={onClose}
    >
      <div 
        className={`mt-20 w-full max-w-3xl rounded-xl overflow-hidden ${theme === 'dark' ? 'bg-gray-800' : 'bg-white'}`}
        onClick={(e) => e.stopPropagation()}
      >
        {/* Header */}
        <div className={`p-4 border-b ${theme === 'dark' ? 'border-gray-700' : 'border-gray-200'}`}>
          <div className="flex items-center justify-between mb-3">
            <h2 className="text-xl font-bold flex items-center gap-2">
              üîç K√´rko
              <span className="text-sm font-normal text-gray-400">
                ({searchResults.length} rezultate)
              </span>
            </h2>
            <button
              onClick={onClose}
              className="p-2 rounded-lg hover:bg-gray-700"
            >
              ‚úï
            </button>
          </div>
          
          <div className="relative">
            <input
              ref={inputRef}
              type="text"
              value={searchQuery}
              onChange={(e) => onSearchChange(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Shkruani p√´r t√´ k√´rkuar kanale, filma, seriale..."
              className={`w-full p-3 pl-12 rounded-lg ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}
            />
            <span className="absolute left-4 top-1/2 transform -translate-y-1/2 text-xl">
              üîç
            </span>
            {searchQuery && (
              <button
                onClick={() => onSearchChange('')}
                className="absolute right-4 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white"
              >
                ‚úï
              </button>
            )}
          </div>
          
          <div className="mt-3 text-sm text-gray-400">
            Shtypni <kbd className="px-2 py-1 bg-gray-700 rounded">Esc</kbd> p√´r t√´ mbyllur
            {searchResults.length > 0 && (
              <span className="ml-4">
                <kbd className="px-2 py-1 bg-gray-700 rounded">Enter</kbd> p√´r t√´ luajtur t√´ parin
              </span>
            )}
          </div>
        </div>
        
        {/* Results */}
        <div className="max-h-[60vh] overflow-y-auto">
          {searchResults.length > 0 ? (
            <div className="divide-y divide-gray-700">
              {searchResults.map((item, index) => (
                <div
                  key={`${item.stream_id}_${index}`}
                  className={`p-4 hover:bg-gray-700 hover:bg-opacity-30 cursor-pointer transition-colors ${
                    theme === 'dark' ? 'hover:bg-gray-700' : 'hover:bg-gray-100'
                  }`}
                  onClick={() => onPlay(item)}
                >
                  <div className="flex items-start gap-4">
                    <div className="flex-shrink-0">
                      <div className="w-16 h-16 rounded-lg overflow-hidden bg-gray-700">
                        {item.logo ? (
                          <img 
                            src={item.logo} 
                            alt={item.name}
                            className="w-full h-full object-cover"
                            loading="lazy"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center text-2xl">
                            {getTypeIcon(item.type)}
                          </div>
                        )}
                      </div>
                    </div>
                    
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-2 mb-1">
                        <span className={`px-2 py-0.5 rounded text-xs font-medium text-white ${getTypeColor(item.type)}`}>
                          {item.type === 'channel' ? 'Live TV' : 
                           item.type === 'movie' ? 'Film' : 'Seriale'}
                        </span>
                        <span className="text-sm text-gray-400">
                          {item.category}
                        </span>
                      </div>
                      
                      <h3 className="font-bold truncate">{item.name}</h3>
                      
                      {item.searchScore > 0 && (
                        <div className="flex items-center gap-2 mt-1">
                          <div className="w-24 bg-gray-700 rounded-full h-1.5 overflow-hidden">
                            <div 
                              className="bg-green-500 h-full"
                              style={{ width: `${Math.min(100, item.searchScore)}%` }}
                            ></div>
                          </div>
                          <span className="text-xs text-gray-400">
                            Relevanc√´: {Math.round(item.searchScore)}%
                          </span>
                        </div>
                      )}
                    </div>
                    
